INSERT INTO [t2300] VALUES (6290,'السؤال: يقول ما الحكم فيما يسمى بمواد التجميل الحديثة التي تتجمل بها المرأة مما يوضع على الشفاه أو على الوجه أو في العين أو على الأظافر وذلك إن كان لزوجها فقط ولا يراها رجل أجنبي حيث إنني سمعت أن بعض من لهم نصيب من العلم لم يجوز استعمال هذه المواد حتى وإن كان للزوج فقط لأنه جعلها من قبيل التشبه بالكافرات فما هو القول الفصل في هذا؟','6271','6284','0');
INSERT INTO [t2300] VALUES (6291,'السؤال: ما حكم استعمال المرأة للمناكير؟','6272','6284','0');
INSERT INTO [t2300] VALUES (6292,'يافضيلة الشيخ: ما حكم صلاة المرأة التي تضع المناكير على يديها؟','6273','6284','0');
