INSERT INTO [t2300] VALUES (6295,'العدسات اللاصقة','6276','6182','0');
INSERT INTO [t2300] VALUES (6296,'السؤال: السائلة ق س م تقول لقد انتشر بين أوساط النساء وخاصة طالبات الجامعة وضع العدسات اللاصقة في العين وهي عدسات طبية لضعف في النظر ولكن هذه العدسات تستخدم أحيانا بألوان فتكون لون أزرق أو أخضر أو عسلي بحيث يتغير لون العين الطبيعي علما بأن هناك من يستخدم هذه العدسات ملونة فقط للزينة والسؤال هل في العدسات الملونة تغيير لخلق الله أو تشبه بالكافرات نرجو منكم التوجيه.','6277','6295','0');
INSERT INTO [t2300] VALUES (6297,'السؤال: ما حكم استعمال العدسات الملونة بغرض الزينة؟','6278','6295','0');
INSERT INTO [t2300] VALUES (6298,'السؤال: يقول يا فضيلة الشيخ ظهر في هذا الزمن عدسات تلبس على العين عوضا عن النظارات وقد ظهر نوع منها يكون ملون بحيث إذا لبسه الإنسان تغير لون العين من لونها الطبيعي إلى لون العدسة فهل لبسها يجوز أم لا حيث إنها قد تكون من تغيير خلق الله أفيدوني بهذا مأجورين؟','6279','6295','0');
INSERT INTO [t2300] VALUES (6299,'السؤال: ما حكم لبس العدسات الملونة للضرورة؟','6280','6295','0');
