INSERT INTO [t2300] VALUES (6303,'أسئلة متفرقة في الزينة','6284','6182','0');
INSERT INTO [t2300] VALUES (6304,'السؤال: إنها فتاة في وجهها بقع سوداء صغيرة مثل حبة الخال وهذه كثيرة ما بين ست إلى ثمان نقاط متفرقة تقول ما حكم إزالة مثل هذه النقاط في مستشفى بواسطة الليزر أو أي طريقة أخرى؟','6285','6303','0');
