INSERT INTO [t2300] VALUES (6320,'السؤال: يقال إن السحاب أو السستة التي يضعها النساء في ملابسهن في الخلف حرام وكذلك الملابس الضيقة وحمالة الصدر والحمرة التي توضع على الشفاه أفيدونا أفادكم الله وجزاكم الله كل خير؟','6301','6305','0');
INSERT INTO [t2300] VALUES (6321,'السؤال: تقول هل يجوز لبس الثوب للنساء وموضوع الجيب فيه من الخلف على سحاب؟','6302','6305','0');
INSERT INTO [t2300] VALUES (6322,'السؤال: هل يجوز وضع السحاب في ثوب المرأة أم هو حرام؟','6303','6305','0');
INSERT INTO [t2300] VALUES (6323,'السائل: تقول ما حكم لبس الملابس الضيقة عند النساء مع أني لا أخرج بها إلى غير المحارم وكذلك لبس الملابس التي بها فتحات على الصدر أو من الخلف وبها سحاب؟','6304','6305','0');
INSERT INTO [t2300] VALUES (6324,'السؤال: يقول يلبس البعض من النساء ملابس مشقوقة من الأسفل أو مفتوحة على الصدر أو تبين شيئا من الأذرع فما حكم ذلك وما حكم لباس الملابس المشقوقة من الأسفل إلى الركبة بالنسبة للنساء ويكون الثوب شفافا أفيدونا بذلك؟','6305','6305','0');
