INSERT INTO [t23101] VALUES (1,'الأدب والبر والصلة','2','0','0');
INSERT INTO [t23101] VALUES (2,'يطلع الآن عليكم رجل من أهل الجنة , فطلع رجل من الأنصار تنطف لحيته من ماء وضوئه , قد علق','2','1','0');
INSERT INTO [t23101] VALUES (3,'اغتبتموه , فقالوا: إنما حدثنا ما فيه , قال: فحسبك إذا ذكرت أخاك بما','3','1','0');
INSERT INTO [t23101] VALUES (4,'طبت وطاب ممشاك وبوأك منزلا في الجنة','4','1','0');
INSERT INTO [t23101] VALUES (5,'أحببته في الله. قال: فإني رسول الله إليك , إن الله قد أحبك كما أحببته','5','1','0');
INSERT INTO [t23101] VALUES (6,'أين المتحابون بجلالي , اليوم أظلهم في ظلي يوم لا ظل إلا ظلي','6','1','0');
INSERT INTO [t23101] VALUES (7,'إذا أحب أحدكم صاحبه فليأته في منزله فيخبره أنه يحبه لله، فقد جئتك في','7','1','0');
INSERT INTO [t23101] VALUES (8,'من الناس ليسوا بأنبياء ولا شهداء يغبطهم النبيون والشهداء على مجالسهم , وقربهم من الله؟','8','1','0');
INSERT INTO [t23101] VALUES (9,'الذين يتحابون من جلال الله في ظل عرش الله , يوم لا ظل إلا ظله','9','1','0');
INSERT INTO [t23101] VALUES (10,'قد حققت محبتي للذين يتحابون من أجلي , وقد حققت محبتي للذين يتصافون من أجلي , وقد حققت','10','1','0');
INSERT INTO [t23101] VALUES (11,'لي جارين فإلى أيهما أهدي؟ قال: إلى أقربهما منك بابا','11','1','0');
INSERT INTO [t23101] VALUES (12,'الرجل يعمل لله ويحبه الناس؟ قال: تلك عاجل بشرى المؤمن','12','1','0');
INSERT INTO [t23101] VALUES (13,'المرء مع من أحب , قال: فما رأيت المسلمين فرحوا بشيء بعد الإسلام فرحهم','13','1','0');
INSERT INTO [t23101] VALUES (14,'ما تواد اثنان في الإسلام , فيفرق بينهما إلا بذنب يحدثه أحدهما','14','1','0');
INSERT INTO [t23101] VALUES (15,'المسلمون كالرجل الواحد إذا اشتكى عضو من أعضائه تداعى له سائر جسده','15','1','0');
INSERT INTO [t23101] VALUES (16,'ما من ذنب أجدر أن يعجل الله لصاحبه العقوبة في الدنيا , مع ما يدخر له في الآخرة من البغي ,','16','1','0');
INSERT INTO [t23101] VALUES (17,'دخل عبد الجنة بغصن من شوك كان على ظهر المسلمين , فأماطه عنه','17','1','0');
INSERT INTO [t23101] VALUES (18,'ويل لمن يحدث فيكذب ليضحك به القوم , ويل له , ويل له , ويل له','18','1','0');
INSERT INTO [t23101] VALUES (19,'العبد ليقول الكلمة لا يقولها إلا ليضحك الناس , يهوي بها أبعد ما بين السماء والأرض , وإنه','19','1','0');
INSERT INTO [t23101] VALUES (20,'كفى بالمرء جرما أن يحدث بكل ما سمع','20','1','0');
INSERT INTO [t23101] VALUES (21,'إذا اجتمع الأولون والآخرون , فيقال: هذه غدرة فلان بن فلان','21','1','0');
INSERT INTO [t23101] VALUES (22,'ما أحب أني حكيت أحدا , وإن لي كذا وكذا أعظم ذلك','22','1','0');
INSERT INTO [t23101] VALUES (23,'الحمد لله الذي كساني ما أواري به عورتي , وأتجمل به في حياتي , ثم قال: أتدرون لم قلت هذا؟','23','1','0');
INSERT INTO [t23101] VALUES (24,'سيأتي ناس من أمتي يوم القيامة نورهم كضوء الشمس. قلنا: ومن أولئك يا رسول الله؟ قال:','24','1','0');
INSERT INTO [t23101] VALUES (25,'لا يحل لمسلم أن يهجر مسلما فوق ثلاث ليال , فإنهما ناكبان عن الحق ما داما على صرامهما ,','25','1','0');
INSERT INTO [t23101] VALUES (26,'الحمد لله كتاب واحد وفيكم الأخيار , وفيكم الأحمر والأسود , اقرءوا قبل أن يأتي أقوام','26','1','0');
INSERT INTO [t23101] VALUES (27,'العلم','26','0','0');
INSERT INTO [t23101] VALUES (28,'الله لا يقبض العلم انتزاعا ينتزعه من الناس , ولكن يقبض العلم بقبض العلماء , حتى إذا لم','27','27','0');
INSERT INTO [t23101] VALUES (29,'رأيت ليلة أسري بي رجالا تقرض شفاههم بمقاريض من نار , فقلت: من هؤلاء يا جبريل؟ فقال:','28','27','0');
INSERT INTO [t23101] VALUES (30,'ما الإثم؟ قال: ما حاك في صدرك فدعه. قال: فما الإيمان؟ قال: إذا ساءتك سيئتك , وسرتك','29','27','0');
INSERT INTO [t23101] VALUES (31,'الإيمان','29','0','0');
INSERT INTO [t23101] VALUES (32,'ألا أخبركم بالمؤمن , من أمنه الناس على أموالهم وأنفسهم , والمسلم من سلم الناس من لسانه','30','31','0');
INSERT INTO [t23101] VALUES (33,'ثلاث من كن فيه وجد حلاوة الإيمان: من أحب المرء لا يحبه إلا لله , ومن كان الله ورسوله أحب','31','31','0');
INSERT INTO [t23101] VALUES (34,'ما رأيت شيئا أحسن من النبي صلى الله عليه وسلم كأن الأرض تطوى له , إنا لنجهد , وإنه لغير','32','31','0');
INSERT INTO [t23101] VALUES (35,'أعطه من هو أفقر إليه مني , فقال: خذه فإما تموله وإما تصدق به , وما جاءك من هذا المال وأنت','33','31','0');
INSERT INTO [t23101] VALUES (36,'الله لا يحب الفحش والتفحش','34','31','0');
INSERT INTO [t23101] VALUES (37,'يقدم عليكم رجل من أهل اليمن يقال له أويس القرني , لا يدع باليمن غير أم له , قد كان به','35','31','0');
INSERT INTO [t23101] VALUES (38,'لله مائة رحمة , أنزل منها رحمة واحدة بين الجن , والإنس , والبهائم , والهوام , فبها','36','31','0');
INSERT INTO [t23101] VALUES (39,'إن رجلين كانا في بني إسرائيل متحابين , أحدهما مجتهد في العبادة , والآخر كأنه يقول: مذنب.','37','31','0');
INSERT INTO [t23101] VALUES (40,'ما توضأ عبد فأسبغ وضوءه , ثم قام إلى الصلاة إلا غفر الله له ما بينه وبين الصلاة الأخرى ,','38','31','0');
INSERT INTO [t23101] VALUES (41,'الوضوء','38','0','0');
INSERT INTO [t23101] VALUES (42,'الصلوات كفارات للخطايا , الصلوات كفارات للخطايا , فاقرءوا إن شئتم: {إن الحسنات يذهبن','39','41','0');
INSERT INTO [t23101] VALUES (43,'الصلاة','39','0','0');
INSERT INTO [t23101] VALUES (44,'أنا عند ظن عبدي فليظن بي ما شاء','40','43','0');
INSERT INTO [t23101] VALUES (45,'انظروا إلى صلاة عبدي , فإن كانت تامة كتبت تامة , وأن كانت ناقصة , قال الله عز وجل بحلمه','41','43','0');
INSERT INTO [t23101] VALUES (46,'أشهد أن لا إله إلا الله , وأشهد أني رسول الله , لا يلقى الله عبد يؤمن بهما إلا حجبت عنه','42','43','0');
INSERT INTO [t23101] VALUES (47,'من ذا الذي يستغفرني أغفر له , من ذا الذي يدعوني أستجيب له , من ذا الذي يسألني أعطيه , حتى','43','43','0');
INSERT INTO [t23101] VALUES (48,'لا تقوله يقول: لا إله إلا الله يبتغي بذلك وجه الله عز وجل , قال: أما نحن فنرى وجهه وحديثه','44','43','0');
INSERT INTO [t23101] VALUES (49,'أي العمل أفضل؟ قال: لا يزال لسانك رطبا من ذكر الله عز وجل','45','43','0');
INSERT INTO [t23101] VALUES (50,'ما اجتمع قوم يذكرون الله إلا حفتهم الملائكة , وتنزلت عليهم السكينة وتغشتهم الرحمة ,','46','43','0');
INSERT INTO [t23101] VALUES (51,'الرجل ليتكلم بالكلمة يضحك بها جلساءه , يهوي بها من أبعد من الثريا','47','43','0');
INSERT INTO [t23101] VALUES (52,'ما جلس قوم مجلسا لم يذكروا فيه الله إلا كان عليهم ترة , وما مشى أحد ممشى لم يذكر الله فيه','48','43','0');
INSERT INTO [t23101] VALUES (53,'ما جلس قوم مجلسا لم يذكروا الله فيه , ولم يصلوا على النبي صلى الله عليه وسلم إلا كان','49','43','0');
INSERT INTO [t23101] VALUES (54,'الخشب يحن إلى رسول الله صلى الله عليه وسلم , أفليس الذين يرجون لقاءه أحق أن يشتاقوا إليه','50','43','0');
INSERT INTO [t23101] VALUES (55,'من صلى علي صلاة صلت عليه الملائكة ما صلى علي , فليقل عبد من ذلك أو','51','43','0');
INSERT INTO [t23101] VALUES (56,'لا يصلي عليك أحد من أمتك إلا صليت عليه عشرا , أو لا يسلم عليك أحد إلا سلمت عليه','52','43','0');
INSERT INTO [t23101] VALUES (57,'لله ملائكة سياحين في الأرض يبلغوني من أمتي السلام','53','43','0');
INSERT INTO [t23101] VALUES (58,'من توضأ فأحسن وضوءه ثم صلى صلاة غير ساه ولا لاه , كفر عنه ما كان قبلها من','54','43','0');
INSERT INTO [t23101] VALUES (59,'الصلاة مثنى مثنى تشهد في كل ركعتين , وتخشع وتضرع وتمسكن , ثم تقنع يديك , يقول: ترفعهما','55','43','0');
INSERT INTO [t23101] VALUES (60,'إذا قام أحدكم إلى الصلاة فإن الرحمة تواجهه، فلا يحركن الحصى','56','43','0');
INSERT INTO [t23101] VALUES (61,'لا يزال الله مقبلا على العبد ما لم يلتفت , فإذا صرف وجهه انصرف عنه','57','43','0');
INSERT INTO [t23101] VALUES (62,'يصلي , ثم ينام قدر ما يصلي , ثم يصلي قدر ما نام , ثم ينام قدر ما يصلي , حتى يصبح , ونعتت','58','43','0');
INSERT INTO [t23101] VALUES (63,'يقوم الليلة التمام فيقرأ سورة البقرة , وسورة آل عمران , وسورة النساء , لا يمر بآية فيها','59','43','0');
INSERT INTO [t23101] VALUES (64,'لا حسد إلا على اثنتين: رجل آتاه الله مالا فهو ينفق منه آناء الليل وآناء النهار , ورجل','60','43','0');
INSERT INTO [t23101] VALUES (65,'لا حسد إلا في اثنتين: رجل آتاه الله مالا فسلطه على هلكته في حق , ورجل آتاه الله حكمة فهو','61','43','0');
INSERT INTO [t23101] VALUES (66,'ذاك رجل لا يتوسد القرآن','62','43','0');
INSERT INTO [t23101] VALUES (67,'من الليل ساعة لا يوافقها عبد مسلم يسأل الله خيرا إلا أعطيه , وهي كل','63','43','0');
INSERT INTO [t23101] VALUES (68,'أي قيام الليل أفضل؟ قال أبو ذر: سألت رسول الله صلى الله عليه وسلم كما سألتني , فقال: نصف','64','43','0');
INSERT INTO [t23101] VALUES (69,'لولا أن أشق على أمتي لأمرت بالسواك عند كل وضوء , ولأخرت العشاء إلى نصف الليل أو ثلث الليل','65','43','0');
INSERT INTO [t23101] VALUES (70,'من بات طاهرا بات في شعاره ملك , فلا يستيقظ ساعة من الليل إلا قال الملك: اللهم اغفر لعبدك','66','43','0');
INSERT INTO [t23101] VALUES (71,'هل كان رسول الله صلى الله عليه وسلم يأمر بصلاة غير المكتوبة؟ قال: بين المغرب','67','43','0');
INSERT INTO [t23101] VALUES (72,'لم يكن يلزمه للصلاة شيء أحرى أن يؤخرها إذا كان على حديث من صلاة العشاء , وما صلاها قط،','68','43','0');
INSERT INTO [t23101] VALUES (73,'أقرأ القرآن في ثلاث؟ قال: إن استطعت. قال: فكان يقرؤه كذلك حتى توفي','69','43','0');
INSERT INTO [t23101] VALUES (74,'إذا دخل أحدكم المسجد فليركع ركعتين قبل أن يجلس','71','43','0');
INSERT INTO [t23101] VALUES (75,'أكثر من السجود، فإنه ليس من عبد مسلم يسجد لله سجدة إلا رفعه الله بها','72','43','0');
INSERT INTO [t23101] VALUES (76,'أبواب السماء وأبواب الجنة يفتحن في تلك الساعة , فلا يوافي أحد بهذه الصلاة , فأحببت أن','73','43','0');
INSERT INTO [t23101] VALUES (77,'الدعاء هو العبادة , ثم قرأ: {وقال ربكم ادعوني أستجب لكم} [غافر: ٦٠','74','43','0');
INSERT INTO [t23101] VALUES (78,'الرجل ليصلي الصلاة ولعله لا يكون له من صلاته إلا عشرها أو تسعها أو ثمنها أو سبعها أو','75','43','0');
INSERT INTO [t23101] VALUES (79,'ما من ثلاثة في قرية ولا بدو لا تقام فيهم الصلاة إلا استحوذ عليهم الشيطان , فعليك بالجماعة','76','43','0');
INSERT INTO [t23101] VALUES (80,'من لم يدع الزور والعمل به والجهل , فليس لله حاجة بأن يدع طعامه وشرابه','77','43','0');
INSERT INTO [t23101] VALUES (81,'رب صائم ليس له من صيامه إلا الجوع , ورب قائم ليس له من قيامه إلا السهر','78','43','0');
INSERT INTO [t23101] VALUES (82,'من أنفق زوجين في سبيل الله نودي في الجنة يا عبد الله , هذا خير , فإن كان من أهل الصلاة','79','43','0');
INSERT INTO [t23101] VALUES (83,'أحب الأعمال عند الله أدومها وإن قل , وكانت عائشة إذا عملت عملا داومت','80','43','0');
INSERT INTO [t23101] VALUES (84,'طوبى لمن طال عمره , وحسن عمله','81','43','0');
INSERT INTO [t23101] VALUES (85,'فأين صلاته بعد صلاته , وأين عمله بعد عمله؟، وأراه قال: صومه بعد صومه، فإن بينهما كما بين','82','43','0');
INSERT INTO [t23101] VALUES (86,'سبعة يظلهم الله يوم القيامة في ظله يوم لا ظل إلا ظله: إمام عادل , وشاب نشأ في طاعة الله','83','43','0');
INSERT INTO [t23101] VALUES (87,'قوما ركبوا البحر في سفينة فاقتسموها , فأصاب كل رجل مكانا فأخذ رجل منهم الفأس فبقر مكانه ,','84','43','0');
INSERT INTO [t23101] VALUES (88,'إذا أحب أحدكم أن يعلم قدر نعمة الله عليه فلينظر إلى من هو تحته , ولا ينظر إلى من هو','85','43','0');
INSERT INTO [t23101] VALUES (89,'لن يلج أحد بعمله الجنة , قالوا: ولا أنت يا رسول الله؟ قال: ولا أنا , إلا أن يتغمدني الله','86','43','0');
INSERT INTO [t23101] VALUES (90,'من استن خيرا فاستن به فله أجره , ومثل أجور من تبعه من غير منتقص من أجورهم شيئا , ومن استن','87','43','0');
INSERT INTO [t23101] VALUES (91,'ما أعرف منكم شيئا كنت أعهده على عهد رسول الله صلى الله عليه وسلم ليس قولكم لا إله إلا','88','43','0');
INSERT INTO [t23101] VALUES (92,'ما أنا بصانعة شيئا حتى أوامر ربي , فقامت إلى مسجدها ونزل القرآن , وجاء رسول الله صلى الله','89','43','0');
INSERT INTO [t23101] VALUES (93,'بعثت أنا والساعة كهاتين , وكان إذا ذكر الساعة احمرت وجنتاه , وعلا صوته , واشتد غضبه كأنه','90','43','0');
INSERT INTO [t23101] VALUES (94,'يوم القيامة','90','0','0');
INSERT INTO [t23101] VALUES (95,'مضى من دنياكم فما بقي منها كما مضى من يومكم هذا فما بقي منه','91','94','0');
INSERT INTO [t23101] VALUES (96,'لتقومن الساعة على رجلين وثوبهما في أيديهما','92','94','0');
INSERT INTO [t23101] VALUES (97,'كيف أنعم وصاحب القرن قد التقم القرن , واستمع الإذن متى يؤمر بالنفخ فينفخ؟ فكأن ذلك ثقل','93','94','0');
INSERT INTO [t23101] VALUES (98,'ما الصور؟ فقال: قرن ينفخ به','94','94','0');
INSERT INTO [t23101] VALUES (99,'أنا الملك , أين ملوك الأرض؟','95','94','0');
INSERT INTO [t23101] VALUES (100,'أتدرون ما أخبارها؟ قالوا: الله ورسوله أعلم , قال: فإن أخبارها أن تشهد على كل عبد وأمة','96','94','0');
INSERT INTO [t23101] VALUES (101,'يوم يقوم الناس لرب العالمين} [المطففين: ٦] حتى يغيب أحدهم في رشحه إلى أنصاف أذنيه ,','97','94','0');
INSERT INTO [t23101] VALUES (102,'إذا كان يوم القيامة أدنيت الشمس من العباد حتى تكون قيد ميل أو اثنين , قال سليم: لا أدري','98','94','0');
INSERT INTO [t23101] VALUES (103,'الصيام والقرآن يشفعان للعبد , يقول الصيام: أي رب , إني منعته الطعام والشهوات بالنهار','99','94','0');
INSERT INTO [t23101] VALUES (104,'أول ما يقضى بين الناس يوم القيامة في الدماء','100','94','0');
INSERT INTO [t23101] VALUES (105,'أعطيتك وخولتك وأنعمت عليك فما صنعت؟ فيقول: يا رب , جمعته وثمرته وتركته أكثر ما كان ,','101','94','0');
INSERT INTO [t23101] VALUES (106,'من نوقش الحساب هلك , قلت: يا رسول الله , فإن الله يقول: {فأما من أوتي كتابه بيمينه فسوف','102','94','0');
INSERT INTO [t23101] VALUES (107,'أتنكر من هذا شيئا؟ أظلمك كتبتي الحافظون؟ فيقول: لا يا رب. فيقول الله: ألك عذر أو حسنة','103','94','0');
INSERT INTO [t23101] VALUES (108,'ارفع رأسك , سل تعطه , اشفع تشفع , فأرفع رأسي , فأقول: أمتي يا رب , أمتي. فيقال لمحمد صلى','104','94','0');
INSERT INTO [t23101] VALUES (109,'فيثور من مجلسي أطيب ريح شمها أحد , حتى آتي ربي فيشفعني , ويجعل لي نورا من شعر رأسي إلى','105','94','0');
INSERT INTO [t23101] VALUES (110,'غر محجلون من آثار الوضوء , ولا يكون أحد من الأمم غيرهم , وأعرفهم أنهم يؤتون كتبهم','106','94','0');
INSERT INTO [t23101] VALUES (111,'يأتي يوم القيامة مع أمتي مثل الليل أو السيل , فيخطف الناس خطفة واحدة , فيقول الملائكة:','107','94','0');
INSERT INTO [t23101] VALUES (112,'لكل نبي دعوة قد دعا بها , وإني استخبأت دعوتي شفاعة لأمتي يوم القيامة','108','94','0');
INSERT INTO [t23101] VALUES (113,'إنكم توفون سبعين أمة، أنتم خيرها وأكرمها على الله عز وجل','109','94','0');
INSERT INTO [t23101] VALUES (114,'نحن الآخرون السابقون يوم القيامة , وذلك أن أهل الكتاب أوتوه من قبلنا , وأوتيناه من بعدهم','110','94','0');
INSERT INTO [t23101] VALUES (115,'من أمتي لمن يعظم للنار حتى يكون ركنا من أركانها , وإن من أمتي لمن يشفع لأكثر من ربيعة ,','111','94','0');
INSERT INTO [t23101] VALUES (116,'يدخل الجنة من أمتي زمرة هم سبعون ألفا , تضيء وجوههم إضاءة القمر ليلة البدر. فقال أبو','112','94','0');
INSERT INTO [t23101] VALUES (117,'ردوه , فيرد , فيقال له: لم التفت؟ قال: كنت أرجو أن تدخلني الجنة , قال: فيؤمر به إلى','113','94','0');
INSERT INTO [t23101] VALUES (118,'أخرجوهما , فلما أخرجا , قال لهما: لأي شيء اشتد صياحكما؟ قالا: فعلنا ذلك لترحمنا. قال:','114','94','0');
INSERT INTO [t23101] VALUES (119,'يا أهل الجنة، فيقولون: لبيك ربنا وسعديك , فيقول: هل رضيتم؟ فيقولون: وما لنا لا نرضى ,','115','94','0');
INSERT INTO [t23101] VALUES (120,'أول زمرة تلج الجنة , صورتهم على صورة القمر ليلة البدر , لا يبصقون فيها , ولا يمتخطون ولا','116','94','0');
INSERT INTO [t23101] VALUES (121,'نحن الآخرون السابقون يوم القيامة , أول زمرة يدخلون الجنة من أمتي سبعون ألفا لا حساب عليهم','117','94','0');
INSERT INTO [t23101] VALUES (122,'لو أن ما يقل ظفر مما في الجنة لتزخرفت له ما بين خوافق السموات والأرض , ولو أن رجلا من أهل','118','94','0');
INSERT INTO [t23101] VALUES (123,'أهل الجنة ليتراءون في الغرف كما تتراءون الكوكب الشرقي أو الكوكب الغربي الغارب في الأفق أو','119','94','0');
INSERT INTO [t23101] VALUES (124,'أدنى أهل الجنة الذي له ثمانون ألف خادم , واثنتان وسبعون زوجة , وينصب له قبة من لؤلؤ','120','94','0');
INSERT INTO [t23101] VALUES (125,'من مات من أهل الجنة من صغير أو كبير يردون بني ثلاثين سنة في الجنة لا يزيدون عليها أبدا ,','121','94','0');
INSERT INTO [t23101] VALUES (126,'فينظر إلى وجهه في خدها أصفى من المرآة , وإن أدنى لؤلؤة عليها لتضيء ما بين المشرق والمغرب','122','94','0');
INSERT INTO [t23101] VALUES (127,'في الجنة شجرة يسير الراكب في ظلها سبعين , أو قال: مائة سنة هي شجرة','123','94','0');
INSERT INTO [t23101] VALUES (128,'أعددت لعبادي الصالحين ما لا عين رأت , ولا أذن سمعت , ولا خطر على قلب','124','94','0');
INSERT INTO [t23101] VALUES (129,'إذا صار أهل الجنة إلى الجنة وأهل النار إلى النار , جيء بالموت حتى يجعل بين الجنة والنار ,','125','94','0');
INSERT INTO [t23101] VALUES (130,'يؤتى بالموت يوم القيامة كالكبش الأملح فيوقف بين الجنة والنار , فيقال: يا أهل الجنة , هذا','126','94','0');
INSERT INTO [t23101] VALUES (131,'لو أن رصاصة مثل هذه، وأشار إلى مثل الجمجمة، أرسلت من السماء إلى الأرض مسير ة خمسمائة سنة','127','94','0');
INSERT INTO [t23101] VALUES (132,'ابكوا , فإن لم تبكوا فتباكوا , فإن أهل النار يبكون في النار , حتى تسيل دموعهم في وجوههم','128','94','0');
INSERT INTO [t23101] VALUES (133,'وهم فيها كالحون} [المؤمنون: ١٠٤] , قال: تشويه النار , فتقلص شفته العليا حتى تبلغ وسط','129','94','0');
INSERT INTO [t23101] VALUES (134,'ناركم هذه التي يوقد بنو آدم جزء واحد من سبعين جزءا من حر جهنم , قالوا: والله إن كانت','130','94','0');
INSERT INTO [t23101] VALUES (135,'الحميم ليصب على رءوسهم , فينفذ الجمجمة حتى يخلص إلى جوفه , فيسلت ما في جوفه حتى يمرق من','131','94','0');
INSERT INTO [t23101] VALUES (136,'ويسقى من ماء صديد يتجرعه} [إبراهيم: ١٧] , قال: يقرب إلى فيه فيكرهه , فإذا أدني منه شوى','132','94','0');
INSERT INTO [t23101] VALUES (137,'ماء كالمهل , قال: كعكر الزيت , فإذا قرب إليه سقطت فروة وجهه','133','94','0');
INSERT INTO [t23101] VALUES (138,'لسرادق النار أربعة جدر , كثف كل جدار مسيرة أربعين سنة','134','94','0');
INSERT INTO [t23101] VALUES (139,'رأيت ليلة أسري بي رجالا تقرض شفاههم بمقاريض من نار , فقلت: من هؤلاء يا جبريل؟ فقال:','135','94','0');
INSERT INTO [t23101] VALUES (140,'في جهنم لواديا يقال له: لملم , إن أودية جهنم لتستعيذ بالله منه','136','94','0');
INSERT INTO [t23101] VALUES (141,'ويل , واد في جهنم يهوي فيه الكافر أربعين خريفا قبل أن يبلغ قعره , والصعود جبل من نار','137','94','0');
INSERT INTO [t23101] VALUES (142,'يهوديا رض رأس جارية بين حجرين , فسئلت: من فعل هذا بك؟ فقيل: فلان , أو فلان حتى ذكر اسم','138','94','0');
INSERT INTO [t23101] VALUES (143,'الحدود','138','0','0');
