INSERT INTO [t23101] VALUES (148,'كيف ندي من لا صاح ولا استهل ولا شرب ولا أكل؟ فقال النبي صلى الله عليه وسلم: أسجع كسجع','143','143','0');
INSERT INTO [t23101] VALUES (149,'في الذي يعض فأندر ثنيته فلا دية له , فذكروا ذلك للنبي صلى الله عليه وسلم , فقال: لا دية','144','143','0');
INSERT INTO [t23101] VALUES (150,'أتي برجل قد شرب الخمر فجلده بجريدتين نحوا من أربعين , قال: وفعله أبو بكر , فلما كان عمر ,','145','143','0');
INSERT INTO [t23101] VALUES (151,'ونهي عن الدباء , وعن الزبيب والتمر أن يخلطا','146','143','0');
INSERT INTO [t23101] VALUES (152,'ما أخالك سرقت. قال: بلى. قال: ثم قال: ما أخالك سرقت. قال: بلى. قال: فاذهبوا به','147','143','0');
INSERT INTO [t23101] VALUES (153,'قطع رسول الله صلى الله عليه وسلم يد سارق وعلق يده في عنقه','148','143','0');
