INSERT INTO [t23101] VALUES (162,'ما تجدون في التوراة من الحد؟ , قالا: نجد في التوراة أن الرجل إذا خلى بالمرأة في البيت ما','157','143','0');
INSERT INTO [t23101] VALUES (163,'رجم ماعزا , ولم يذكر جلدا','158','143','0');
