INSERT INTO [t23101] VALUES (209,'ألك ولد سوى هذا؟ , قال: نعم , فأراه قال: لا , لا تشهدني على هذا','204','193','0');
INSERT INTO [t23101] VALUES (210,'الهبة والعتق','204','0','0');
