INSERT INTO [t23101] VALUES (212,'الراجع في هبته كالكلب في قيئه','206','210','0');
INSERT INTO [t23101] VALUES (213,'هذا مثل الكلب الذي يأكل حتى إذا شبع قاء ما في بطنه , ثم رجع إليه فأكله , وقال عمرو بن','207','210','0');
INSERT INTO [t23101] VALUES (214,'لا يحل لرجل أن يعطي عطية فيرجع فيها إلا الوالد فيما يعطي ولده , ومثل من يعطي العطية ثم','208','210','0');
INSERT INTO [t23101] VALUES (215,'لا ترقبوا شيئا , فمن أرقب شيئا فهو له , ولا تعمروا , فمن عمر شيئا فهو','209','210','0');
INSERT INTO [t23101] VALUES (216,'ما تقول في العمرى؟ قلت: قبلها رسول الله صلى الله عليه وسلم , قال معاوية: أشهد لسمعها من','210','210','0');
INSERT INTO [t23101] VALUES (217,'العمرى للوارث','211','210','0');
INSERT INTO [t23101] VALUES (218,'العمرى جائزة','212','210','0');
INSERT INTO [t23101] VALUES (219,'العمرى جائزة لأهلها , أو ميراث لأهلها. ٢٠٨ - حدثنا جدي، نا حبان، أنا عبد الله، أنا عن','213','210','0');
INSERT INTO [t23101] VALUES (220,'لا يجوز لأمرأة عطية إلا بإذن زوجها','214','210','0');
INSERT INTO [t23101] VALUES (221,'مثل الذي يرجع في صدقته كمثل الكلب يقيء فيرجع في قيئه فيأكله','215','210','0');
INSERT INTO [t23101] VALUES (222,'إن شئت تصدقت بها وحبست أصلها، فجعلها عمر صدقة لا يباع أصلها ولا يورث ولا يوهب , وتصدق','216','210','0');
