INSERT INTO [t23101] VALUES (224,'فليعتقون فيه مثله يفك الله بكل عضو منه عضوا من النار','218','210','0');
INSERT INTO [t23101] VALUES (225,'العبد والأمة أحدهما بين شركاء , فيعتق أحدهم نصيبه منه: فقد يوجب عتقه كله عليه , إذا كان','219','210','0');
INSERT INTO [t23101] VALUES (226,'أيما رجل كان بينه وبين آخر شركة في عبد أو وليدة فأعتق أحدهما نصيبه , فعلى المعتق أن يقام','220','210','0');
INSERT INTO [t23101] VALUES (227,'من أعتق شقيصا من مملوكه فعليه خلاصه في ماله , فإن لم يكن له مال قوم المملوك قيمة عدل ثم','221','210','0');
INSERT INTO [t23101] VALUES (228,'الولد للفراش , واحتجبي منه يا سودة , فلم يرها حتى ماتت','222','210','0');
INSERT INTO [t23101] VALUES (229,'لي جارية، وأنا أريد أن آتيها وأحتبسها. فقال رسول الله صلى الله عليه وسلم: أما إن ذلك ليس','223','210','0');
INSERT INTO [t23101] VALUES (230,'هل لك إبل؟ , قال: نعم. قال: فما ألوانها؟ , قال: هي حمر. قال له النبي صلى الله عليه','224','210','0');
INSERT INTO [t23101] VALUES (231,'رجلا أعتق غلاما له عن دبر , فاحتاج فأخذه رسول الله صلى الله عليه وسلم فقال: من يشتريه مني','225','210','0');
INSERT INTO [t23101] VALUES (232,'نهى عن بيع الولاء وعن هبته , إلا أن عبيد الله نسي وفي الهبة أو لم','226','210','0');
INSERT INTO [t23101] VALUES (233,'فما بال رجال منكم يشترطون شروطا ليست في كتاب الله , ما كان من شرط ليس في كتاب الله فهو','227','210','0');
INSERT INTO [t23101] VALUES (234,'من ملك ذا رحم فهو حر','228','210','0');
INSERT INTO [t23101] VALUES (235,'ثلاثة حق على الله عونهم: المجاهد في سبيل الله , والناكح الذي يريد العفاف , والمكاتب الذي','229','210','0');
INSERT INTO [t23101] VALUES (236,'أقلوا الحديث عن النبي صلى الله عليه وسلم وأنا شريككم فيه , قال: فما كنت أحدث عن النبي صلى','230','210','0');
INSERT INTO [t23101] VALUES (237,'تعظيم الحديث عن رسول الله صلى الله عليه وسلم','230','0','0');
INSERT INTO [t23101] VALUES (238,'أو فوق ذلك أو دون ذلك أو نحو ذلك','231','237','0');
INSERT INTO [t23101] VALUES (239,'إن شاء الله , إما فوق ذلك وإما نحو ذلك , وإما قريب من ذلك','232','237','0');
INSERT INTO [t23101] VALUES (240,'كنا نحفظ الحديث , والحديث يحفظ عن رسول الله صلى الله عليه وسلم حتى ركبتم فيه الصعبة','233','237','0');
INSERT INTO [t23101] VALUES (241,'فما سمعته يحدث عن النبي صلى الله عليه وسلم بحديث حتى رجعنا , وقال حماد: يعظم الحديث عن','234','237','0');
INSERT INTO [t23101] VALUES (242,'لن أكتبكموه , ولن أجعله قرآنا','235','237','0');
INSERT INTO [t23101] VALUES (243,'فأعجبني هذا الحديث. فقلت لابني: اكتبه فكتبه','236','237','0');
INSERT INTO [t23101] VALUES (244,'الله قد أحكم ذلك والسنة تفسر ذلك','237','237','0');
INSERT INTO [t23101] VALUES (245,'أحدثهم عن رسول الله صلى الله عليه وسلم بالحديث , فيقولون: أنت سمعته','238','237','0');
INSERT INTO [t23101] VALUES (246,'ما من رجل ينعش بلسانه حقا يعمل به بعده , إلا أجري عليه أجره إلى يوم القيامة , ثم وفاه','239','237','0');
INSERT INTO [t23101] VALUES (247,'الفتن','240','0','0');
INSERT INTO [t23101] VALUES (248,'لا يحل دم رجل يشهد أن لا إله إلا الله , وأني رسول الله إلا أحد ثلاثة نفر: النفس بالنفس ,','241','247','0');
INSERT INTO [t23101] VALUES (249,'أنا فرطكم على الحوض , وإني مكاثر بكم الأمم , فلا تقتتلوا بعدي','242','247','0');
INSERT INTO [t23101] VALUES (250,'من مات ولم يشرك بالله شيئا , ولم يتند من الدماء الحرام بشيء دخل من أي أبواب الجنة','243','247','0');
INSERT INTO [t23101] VALUES (251,'ربكم واحد , وإن أباكم واحد , ألا لا فضل لعربي على أعجمي ولا لعجمي على عربي , ولا أسود على','244','247','0');
INSERT INTO [t23101] VALUES (252,'أمرت أن أقاتل الناس حتى يشهدوا أن لا إله إلا الله , وأن محمدا رسول الله , واستقبلوا','245','247','0');
INSERT INTO [t23101] VALUES (253,'استوصوا بأصحابي خيرا , ثم الذين يلونهم , ثم الذين يلونهم , ثم يفشو الكذب حتى إن الرجل','246','247','0');
INSERT INTO [t23101] VALUES (254,'يكون عليكم أئمة فتعرفون حقهم وتنكرون , فمن أنكر فقد نجا , ومن كره فقد سلم , ولكن من رضي','247','247','0');
INSERT INTO [t23101] VALUES (255,'خيار أئمتكم من تحبونهم ويحبونكم , وتصلون عليهم ويصلون عليكم , وشرار أئمتكم الذين تبغضونهم','248','247','0');
INSERT INTO [t23101] VALUES (256,'تؤدون الحق الذي عليكم وتسألون الله الذي لكم','249','247','0');
INSERT INTO [t23101] VALUES (257,'كيف تعمل إذا اقتتل الناس حتى يغرق حجر الزيت؟ , قلت: الله ورسوله أعلم. قال: تأتي من أنت','250','247','0');
INSERT INTO [t23101] VALUES (258,'يلحق رجل من قريش بمكة عليه نصف عذاب العالم فلن أكون إياه , وأما قولك: أن أقعد على راحلتي','251','247','0');
INSERT INTO [t23101] VALUES (259,'ستكون فتنة وفرقة , فاضرب بسيفك عرض أو عرض أحد , واكسر نبلك , واقطع واترك واقعد في بيتك ,','252','247','0');
INSERT INTO [t23101] VALUES (260,'بين يدي الساعة فتن كأنها قطع الليل المظلم , يصبح الرجل فيها مؤمنا ويمسي كافرا , ويمسي','253','247','0');
INSERT INTO [t23101] VALUES (261,'بينما أنا على الحوض إذ مر بكم زمرا , فتذهب بكم الطرق , فناديتكم ألا هلموا إلى الطريق ,','254','247','0');
INSERT INTO [t23101] VALUES (262,'خير الرزق ما يكفي , وخير الذكر الخفي','255','247','0');
INSERT INTO [t23101] VALUES (263,'ما عهد إلينا رسول الله صلى الله عليه وسلم شيئا لم يعهده إلى الناس كافة','256','247','0');
INSERT INTO [t23101] VALUES (264,'والله ما عهد إلي رسول الله صلى الله عليه وسلم إلا شيئا عهده إلى الناس , ولكن الناس وثبوا','257','247','0');
INSERT INTO [t23101] VALUES (265,'ينهى عن شتم الهلكى , فلم تسب عليا وقد مات؟','258','247','0');
INSERT INTO [t23101] VALUES (266,'ليجعلك الله مع صاحبيك , وذاك أني كنت أكثر أن أسمع رسول الله صلى الله عليه وسلم يقول: ذهبت','259','247','0');
INSERT INTO [t23101] VALUES (267,'فيم قتلته؟ فيقول: في ملك فلان , فاتق ألا تكون ذلك الرجل','260','247','0');
INSERT INTO [t23101] VALUES (268,'لا حرج إلا في قتل المسلم , ثلاثا','261','247','0');
INSERT INTO [t23101] VALUES (269,'لو أن الناس تابعوني إلا رجل لم يسدد سلطاني إلا به ما قتلته','262','247','0');
INSERT INTO [t23101] VALUES (270,'الزم بيتك , وأملك عليك لسانك , وخذ ما تعرف , وذر ما تنكر , وعليك بأمر خاصة نفسك , وذر عنك','263','247','0');
INSERT INTO [t23101] VALUES (271,'ما من رجل استرعاه الله رعية إلا سأله الله عنها يوم القيامة أقام الله فيهم أم أضاعه , حتى','264','247','0');
INSERT INTO [t23101] VALUES (272,'ولئن اقتتلتم لأدخلن بيتي , فلئن دخل علي لأقولن: أبوء بإثمي وإثمك','265','247','0');
INSERT INTO [t23101] VALUES (273,'بين يدي الساعة الهرج , فقلنا: وما الهرج؟ قال: القتل , قلنا: أكثر مما نقتل كل عام من','266','247','0');
INSERT INTO [t23101] VALUES (274,'يوشك أن يكون خير مال المسلم غنم يتبع بها شعف الجبال ومواقع القطر , يفر بدينه من','267','247','0');
INSERT INTO [t23101] VALUES (275,'تكون فتنة , النائم فيها خير من المضطجع , والمضطجع فيها خير من القاعد , والقاعد فيها خير','268','247','0');
INSERT INTO [t23101] VALUES (276,'في الفتنة لا ترون القتل شيئا','269','247','0');
INSERT INTO [t23101] VALUES (277,'لا يتناجى اثنان دون واحد','270','247','0');
INSERT INTO [t23101] VALUES (278,'لا طاعة لأحد في معصية الله , إنما الطاعة في المعروف','271','247','0');
INSERT INTO [t23101] VALUES (279,'ستكون أمراء يغشاهم غواش، أو حواش، من الناس يظلمون ويكذبون , فمن أعانهم على ظلمهم وصدقهم','272','247','0');
INSERT INTO [t23101] VALUES (280,'من استرعي رعية فلم يحطها بنصيحة فقد حرم الله عليه ريح الجنة , وريحها يوجد من مسيرة مائة','273','247','0');
INSERT INTO [t23101] VALUES (281,'أحب الناس إلى الله يوم القيامة وأقربهم مني مجلسا , إمام عادل , وأبغض الناس إلى الله يوم','274','247','0');
INSERT INTO [t23101] VALUES (282,'إنكم ستحرصون على الإمارة , وإنها ستكون ندامة وحسرة يوم القيامة , فنعمت المرضعة , وبئست','275','247','0');
INSERT INTO [t23101] VALUES (283,'لا تغبطن فاجرا بنعمة , فإنك لا تدري ما هو لاق بعد موته , إن له عند الله قاتلا لا يموت ,','276','247','0');
INSERT INTO [t23101] VALUES (284,'الراحمون يرحمهم الرحمن , ارحموا من في الأرض يرحمكم أهل السماء','277','247','0');
INSERT INTO [t23101] VALUES (285,'من رفق بأمتي رفق الله به , ومن شق على أمتي شق الله عليه','278','247','0');
INSERT INTO [t23101] VALUES (286,'ما من وال ولا أمير إلا له بطانتان , بطانة تأمره بالمعروف وتنهى عن المنكر , وبطانة لا','279','247','0');
