INSERT INTO [t23102] VALUES (1,'اللهم إني أسألك من فضلك، ورحمتك , فإنه لا يملكها إلا أنت، فإن الله','2','0','0');
INSERT INTO [t23102] VALUES (2,'الله لا يرى الدنيا كالذي تراها، فإذا كنت سائلا فقل: اللهم أرني الدنيا كالذي يراها صالحو','3','0','0');
INSERT INTO [t23102] VALUES (3,'اللهم إنما أنا عبد، فأيما عبد مسلم , أو امرأة مسلمة , دعوت عليه بدعوة، فاجعلها له صلاة','4','0','0');
INSERT INTO [t23102] VALUES (4,'شعار المسلمين يوم القيامة على الصراط: اللهم سلم سلم»','5','0','0');
INSERT INTO [t23102] VALUES (5,'أدعوك اللهم، وأدعوك الرحمن، وأدعوك البر الرحيم، وأسألك بأسمائك الحسنى كلها , ما علمت منها','6','0','0');
INSERT INTO [t23102] VALUES (6,'إذا أصاب أحدكم هم , أو حزن فليقل: اللهم , إني عبدك , وابن عبدك , وابن أمتك , وفي قبضتك ,','7','0','0');
INSERT INTO [t23102] VALUES (7,'فأيما عبد غضبت عليه , أو سببته في غير كنهه فاجعلها له صلاة ورحمة»','8','0','0');
INSERT INTO [t23102] VALUES (8,'اللهم إني ضعيف , فقو في رضاك ضعفي، وخذ إلى الخير بناصيتي، واجعل الإسلام منتهى رضائي،','9','0','0');
INSERT INTO [t23102] VALUES (9,'إذا أنت نمت فاجعل يدك اليمنى تحت خدك الأيمن، ثم قل: اللهم أسلمت نفسي إليك، ووجهت وجهي','10','0','0');
INSERT INTO [t23102] VALUES (10,'الموجبتان: من لقي الله لا يشرك به شيئا دخل الجنة، ومن لقيه يشرك به دخل النار» . قيل: وما','11','0','0');
INSERT INTO [t23102] VALUES (11,'لا يشكر الله من لا يشكر الناس»','12','0','0');
INSERT INTO [t23102] VALUES (12,'أمتي أمة مرحومة , ليس عليها في الآخرة حساب , ولا عذاب، إنما عذابها في القتل والزلازل','13','0','0');
INSERT INTO [t23102] VALUES (13,'من شهد أن لا إله إلا الله , وحده لا شريك له، وأن محمدا عبده ورسوله , مخلصا بهما من قبل','14','0','0');
INSERT INTO [t23102] VALUES (14,'حق الله على العباد أن يعبدوه ولا يشركوا به شيئا» . ثم قال: «يا أبا هريرة، تدري ما حق','15','0','0');
INSERT INTO [t23102] VALUES (15,'يدرس الإسلام كما يدرس وشي الثوب، فيصبح الناس لا يدرون ما صلاة ولا صيام , ولا نسك غير أن','16','0','0');
INSERT INTO [t23102] VALUES (16,'الابتهال هكذا: وقلب كفيه إلى الأرض، والمسألة هكذا: وبسط كفيه، والإخلاص أن تشير بأصبع','17','0','0');
INSERT INTO [t23102] VALUES (17,'وتبتل إليه تبتيلا} [المزمل: ٨] قال: «أخلص إليه إخلاصا»','18','0','0');
INSERT INTO [t23102] VALUES (18,'يدعو ويشير بأصبعيه , فقال: رسول الله صلى الله عليه وسلم: «يا سعد أحد','19','0','0');
INSERT INTO [t23102] VALUES (19,'وتبتل إليه تبتيلا} [المزمل: ٨] قال: «إخلاصا»','20','0','0');
INSERT INTO [t23102] VALUES (20,'إذا دخل المسجد قال: «اللهم اغفر لي ذنوبي، وافتح لي أبواب فضلك , أو رحمتك» . وإذا خرج قال:','21','0','0');
INSERT INTO [t23102] VALUES (21,'يؤمر بالدعاء عند أذان المؤذنين»','22','0','0');
INSERT INTO [t23102] VALUES (22,'عند أذان المغرب: اللهم عند استقبال ليلك، وإدبار نهارك، وأصوات دعاتك، وحضور صلواتك , أسألك','23','0','0');
INSERT INTO [t23102] VALUES (23,'تفتح أبواب السماء لخمس: للقاء الزحف، والغيث إذا نزل، والنداء بالصلاة، ولقراءة القرآن','24','0','0');
INSERT INTO [t23102] VALUES (24,'أنا عند ظن عبدي، وأنا معه حيث يدعوني، فإن ذكرني في نفسه ذكرته في نفسي، وإن ذكرني في ملإ','25','0','0');
INSERT INTO [t23102] VALUES (25,'لا يزال العبد بخير ما لم يستعجل» قيل: يا رسول الله، وكيف يستعجل؟ قال: «يقول: قد دعوت','26','0','0');
INSERT INTO [t23102] VALUES (26,'أنا عند ظن عبدي، وأنا معه إذا دعاني»','27','0','0');
INSERT INTO [t23102] VALUES (27,'يتعوذ من وعثاء السفر، وكآبة المنقلب، والحور بعد الكور، ودعوة المظلوم، وسوء المنظر في','28','0','0');
INSERT INTO [t23102] VALUES (28,'إذا سافر الرجل: اللهم بلاغ يبلغ خيرا , مغفرة منك ورضوانا، بيدك الخير، إنك على كل شيء','29','0','0');
INSERT INTO [t23102] VALUES (29,'في الاستخارة: «اللهم إني أستخيرك بعلمك، وأستقدرك بقدرتك، وأسألك من فضلك، فإنك تعلم , ولا','30','0','0');
INSERT INTO [t23102] VALUES (30,'الناس لم يعطوا شيئا من الدنيا خيرا من اليقين والعافية، ألا فسلوهما','31','0','0');
INSERT INTO [t23102] VALUES (31,'سل الله العافية في الدنيا والآخرة»','32','0','0');
INSERT INTO [t23102] VALUES (32,'الناس لم يعطوا بعد يقين خيرا من المعافاة , ألا فسلوهما الله»','33','0','0');
INSERT INTO [t23102] VALUES (33,'لا أصل إلى امرأتي. قال له: «توضأ، ثم صل ركعتين، ومرها أن تصلي خلفك، فإذا فرغت من صلاتك','34','0','0');
INSERT INTO [t23102] VALUES (34,'تصلي المرأة خلف زوجها، فإن أبت أن تصلي خلفه، فصل أنت ركعتين، ثم قل: «اللهم بارك لي في','35','0','0');
INSERT INTO [t23102] VALUES (35,'الرجل يعمل الخير , ويحمده الناس عليه؟ قال: «يا بني , تلك عاجل بشرى المؤمن في','36','0','0');
INSERT INTO [t23102] VALUES (36,'إذا رأى الهلال قال: «الله أكبر، ربي وربك الله، هلال رشد وبركة»','37','0','0');
INSERT INTO [t23102] VALUES (37,'إذا دخل الخلاء قال: «اللهم إني أعوذ بك من الرجس النجس الخبيث المخبث الشيطان','38','0','0');
INSERT INTO [t23102] VALUES (38,'إذا خرج من الخلاء: «الحمد لله الذي أذهب عني الأذى , وعافاني»','39','0','0');
INSERT INTO [t23102] VALUES (39,'خياركم كل مفتن تواب»','40','0','0');
INSERT INTO [t23102] VALUES (40,'ما من عبد مؤمن إلا له ذنب يعتريه الفينة بعد الفينة حتى لينجو بعد ذلك مغفور الذنب , أو','41','0','0');
INSERT INTO [t23102] VALUES (41,'وإياك والسجع في الدعاء، فإن رسول الله صلى الله عليه وسلم وأصحابه كانوا','42','0','0');
INSERT INTO [t23102] VALUES (42,'إمام يخاف تغطرسه وظلمه , فليتوضأ , وليصل ركعتين، ثم ليقل في دبر صلاته: «اللهم -[٢٠٨]- رب','43','0','0');
INSERT INTO [t23102] VALUES (43,'إذا كان على أحدكم إمام يخاف تغطرسه وظلمه , فليقل: اللهم رب السموات السبع، ورب العرش','44','0','0');
INSERT INTO [t23102] VALUES (44,'فإذا كان من السحر نادى: «سمع سامع بحمد الله ونعمه، وحسن بلائه عندنا - ثلاث مرات -[٢١١]-','45','0','0');
INSERT INTO [t23102] VALUES (45,'حين ينفجر الصبح: «سمع سامع بحمد الله ونعمته، وحسن بلائه علينا - ثلاث مرات - صاحبنا فأقلنا','46','0','0');
INSERT INTO [t23102] VALUES (46,'اللهم اشفني من النوم باليسير، واجعل سهري في طاعتك» . قال: فكان لا ينام إلا هنيهة وهو','47','0','0');
INSERT INTO [t23102] VALUES (47,'ساجد , وهو يقول: «اللهم إني خائف مستجير فأجرني من عذابك، وسائل فقير , فارزقني من فضلك ,','48','0','0');
INSERT INTO [t23102] VALUES (48,'اللهم اجعلني من عبادك الأقلين، فلما انصرف عمر قال: «من صاحب الدعوة؟» قال الرجل: أنا ,','49','0','0');
INSERT INTO [t23102] VALUES (49,'أعوذ بوجه الله الكريم، وكلمات الله التامات , من شر كل شيء هو آخذ بناصيته اللهم أنت تكشف','50','0','0');
INSERT INTO [t23102] VALUES (50,'اللهم دعوتني , فأجبتك , وأمرتني , فأطعتك، وهذا سحر فاغفر لي» قال: فرصده عمي، فإذا عبد','51','0','0');
INSERT INTO [t23102] VALUES (51,'اللهم فاطر السموات والأرض , عالم الغيب -[٢١٧]- والشهادة، إني أعهد إليك في هذه الحياة','52','0','0');
INSERT INTO [t23102] VALUES (52,'اللهم يا ذا المن، ولا يمن عليك , يا ذا الجلال والإكرام، يا ذا الطول لا إله إلا أنت , ظهر','53','0','0');
INSERT INTO [t23102] VALUES (53,'اللهم بارك لي في ديني الذي هو عصمتي، وبارك لي في دنياي التي فيها بلاغي، وبارك لي في آخرتي','54','0','0');
INSERT INTO [t23102] VALUES (54,'أبخل الناس من بخل بالسلام، وأعجز الناس من عجز عن الدعاء»','55','0','0');
INSERT INTO [t23102] VALUES (55,'ألا أدلك على كنز من كنوز الجنة؟» قلت: بلى , يا رسول الله , قال: «لا حول ولا قوة إلا','56','0','0');
INSERT INTO [t23102] VALUES (56,'فلما استوى على الدابة قال: «الحمد لله الذي كرمنا , وحملنا في البر والبحر , ورزقنا من','57','0','0');
INSERT INTO [t23102] VALUES (57,'إذا فرغ من طعام قال: «الحمد -[٢٢٧]- لله الذي من علينا فهدانا، والحمد لله الذي أشبعنا','58','0','0');
INSERT INTO [t23102] VALUES (58,'كيف أنعم؟ وصاحب القرن قد التقم القرن وحنى جبهته , يسمع متى يؤمر , فينفخ فيه» فقال: أصحاب','59','0','0');
INSERT INTO [t23102] VALUES (59,'التوكل على الله جميع الإيمان»','60','0','0');
INSERT INTO [t23102] VALUES (60,'حسبي الله ونعم الوكيل»','61','0','0');
INSERT INTO [t23102] VALUES (61,'إذا أصبح يقول: «اللهم اجعلني من أعظم عبادك نصيبا في كل خير تقسمه لأحد -[٢٣٢]- من خلقك، من','62','0','0');
INSERT INTO [t23102] VALUES (62,'إذا توضأ أحدكم فليقل: أشهد أن لا إله إلا الله، أشهد أن محمدا رسول الله، اللهم اجعلني من','63','0','0');
INSERT INTO [t23102] VALUES (63,'-[٢٣٥]- ما من عبد مسلم يدعو لأخيه بظهر الغيب إلا قال الملك: ولك مثل','64','0','0');
INSERT INTO [t23102] VALUES (64,'اللهم رب إبراهيم، ورب إسحاق ويعقوب، ورب جبريل وميكائيل وإسرافيل، ومنزل التوراة والإنجيل','65','0','0');
INSERT INTO [t23102] VALUES (65,'شيء أقوله ينفعني من أجل الأسد؟ قال: نعم , قل: «اللهم رب دانيال، ورب الجب عافني من الأسد»','66','0','0');
INSERT INTO [t23102] VALUES (66,'إذا صام، ثم أفطر يقول: «اللهم لك صمت، وعلى رزقك أفطرت»','67','0','0');
INSERT INTO [t23102] VALUES (67,'الحمد لله الذي أعانني فصمت، ورزقني فأفطرت»','68','0','0');
INSERT INTO [t23102] VALUES (68,'لما خلق الله عز وجل آدم قال: ثلاث: واحدة لي , وواحدة لك، وواحدة بيني وبينك، فأما التي لي:','69','0','0');
INSERT INTO [t23102] VALUES (69,'تم نورك فهديت , فلك الحمد، وعظم حلمك فعفوت فلك الحمد، وبسطت يدك فأعطيت ربنا , وجهك أكرم','70','0','0');
INSERT INTO [t23102] VALUES (70,'يدعو: «اللهم هذا مكان المستغيث المستجير , مكان البائس الفقير، مكان الهالك الغريق، مكان','71','0','0');
INSERT INTO [t23102] VALUES (71,'لا تفزع ولا تفرق، قل: اللهم إنك مليك مقتدر، وإن ما تشاء من أمر يكون» . ثم سل ما بدا لك.','72','0','0');
INSERT INTO [t23102] VALUES (72,'انطلق ثلاثة نفر يمشون، فدخلوا في غار، فأرسل الله عليهم صخرة فأطبقت الغار عليهم، فقال بعض','73','0','0');
INSERT INTO [t23102] VALUES (73,'اللهم إني أعوذ بك أن تأخذني على غرة، أو أن تذرني في غفلة، أو تجعلني من','74','0','0');
INSERT INTO [t23102] VALUES (74,'من حلف بالأمانة فليس منا، ومن غش امرأ مسلما في أهله , أو خادمه , فليس','75','0','0');
INSERT INTO [t23102] VALUES (75,'اللهم احفظني بالإسلام قائما، واحفظني بالإسلام قاعدا، واحفظني بالإسلام راقدا، ولا تطع في','77','0','0');
INSERT INTO [t23102] VALUES (76,'باسمك اللهم , أنت الرحمن الرحيم , الذي ليس إله غيرك، البديع الذي ليس قبلك شيء , الدائم','78','0','0');
INSERT INTO [t23102] VALUES (77,'ما من رجل يقول سبع مرات: اللهم منزل الكتاب، مفرج السحاب، واضع الميزان، يا ذا الجلال','79','0','0');
INSERT INTO [t23102] VALUES (78,'يا عليم، يا حليم، يا علي، يا عظيم، إنا عبيدك , وفي سبيلك نقاتل عدوك، اسقنا غيثا نشرب منها','80','0','0');
INSERT INTO [t23102] VALUES (79,'كان نبي من بني إسرائيل قد ظهر , وتبعه من شاء الله، وإنه تزوج بنت رجل ممن تابعه من','81','0','0');
INSERT INTO [t23102] VALUES (80,'تعم في الدعاء والنصح للعامة، فإنما أنت شافع، فإن يعطك الله الذي تريد، وإلا فقد قضيت الذي','82','0','0');
INSERT INTO [t23102] VALUES (81,'دعوات الفرج: اللهم لا إله إلا أنت , أسألك الفضل والرحمة، أنت وليهما , لا يليهما غيرك، رب','83','0','0');
INSERT INTO [t23102] VALUES (82,'اللهم بعلمك الغيب، وقدرتك على الخلق، أحيني ما علمت الحياة خيرا لي، واقبضني إذا علمت أن','84','0','0');
INSERT INTO [t23102] VALUES (83,'كلمتان خفيفتان على اللسان , ثقيلتان في الميزان , حبيبتان إلى الرحمن: سبحان الله وبحمده،','85','0','0');
INSERT INTO [t23102] VALUES (84,'ما اجتمع ملأ قط يذكرون الله , إلا ذكرهم الله في ملإ أعز وأكرم، وما تفرق قوم لم يذكروا','86','0','0');
INSERT INTO [t23102] VALUES (85,'إذا كان الرجل يذكر الله في السراء ويحمده في الرخاء , فأصابه ضر فدعا الله، قالت الملائكة:','87','0','0');
INSERT INTO [t23102] VALUES (86,'إذا نزل بك الموت , أو أمر تفظعين به , فقولي: لا إله إلا الله الحليم الكريم، سبحان الله رب','88','0','0');
INSERT INTO [t23102] VALUES (87,'إذا خرجت حتى أبلغ حاجتي: سبحان الله، والحمد لله، ولا إله إلا الله، والله أكبر , أحب إلي','89','0','0');
INSERT INTO [t23102] VALUES (88,'أي الكلام أحب إليك عشية عرفة؟ قال: «لا إله إلا الله، والله أكبر»','90','0','0');
INSERT INTO [t23102] VALUES (89,'أحب الكلام إلى الله أربع , لا يضرك بأيهن بدأت: سبحان الله، والحمد لله، ولا إله إلا الله،','91','0','0');
INSERT INTO [t23102] VALUES (90,'إذا صعدنا -[٢٦٨]- كبرنا، وإذا هبطنا سبحنا»','92','0','0');
INSERT INTO [t23102] VALUES (91,'مائة نسمة من مال رجل لكثير، وأفضل من ذلك وأفضل: إيمان ملزوم بالليل والنهار، ولا يزال لسان','93','0','0');
INSERT INTO [t23102] VALUES (92,'ما يصنع أحدكم بالكلام بعد سبع: سبحان الله، والحمد لله، ولا إله إلا الله، والله أكبر، وأمر','94','0','0');
INSERT INTO [t23102] VALUES (93,'من قال: سبحان الله، والحمد لله، ولا إله إلا الله، والله أكبر , كتب الله له بكل واحدة منهن','95','0','0');
INSERT INTO [t23102] VALUES (94,'إذا قال العبد: سبحان الله , قال الملك: وبحمده , فإن قال: سبحان الله وبحمده صلت عليه','96','0','0');
INSERT INTO [t23102] VALUES (95,'إذا قال العبد: الحمد لله كثيرا , قال الملك: كيف أكتب؟ قال: اكتب له رحمتي كثيرا، وإذا','97','0','0');
INSERT INTO [t23102] VALUES (96,'الله اختار لكم من الكلام أربعا من القرآن , وليس من القرآن: سبحان الله، والحمد لله، ولا','98','0','0');
INSERT INTO [t23102] VALUES (97,'الله قسم بينكم أخلاقكم , كما قسم بينكم أرزاقكم، وإن الله يعطي الدنيا من يحب , ومن لا يحب،','99','0','0');
INSERT INTO [t23102] VALUES (98,'ولذكر الله أكبر} [العنكبوت: ٤٥] قال: قوله: {فاذكروني أذكركم} [البقرة: ١٥٢] «فذكر الله','100','0','0');
INSERT INTO [t23102] VALUES (99,'تصيب أحدنا الجنابة، والغائط» . قال: «يا موسى اذكرني على كل حال»','101','0','0');
INSERT INTO [t23102] VALUES (100,'سبحان الله ملء السموات وملء الأرض، وملء ما شاء من شيء بعد، والحمد لله ملء السموات , وملء','102','0','0');
INSERT INTO [t23102] VALUES (101,'أي العمل أفضل؟ قال: «ذكر الله أكبر» - حتى أعادها عليه ثلاث مرات - ثم قال: «ما جلس قوم في','103','0','0');
INSERT INTO [t23102] VALUES (102,'موسى لما قربه الله -[٢٨١]- نجيا بطور سيناء، أبصر عبدا جالسا في ظل العرش , سأله: أي رب ,','104','0','0');
INSERT INTO [t23102] VALUES (103,'أي عبادك أحب إليك؟ قال: أكثرهم ذكرا. قال: أي عبادك أعظم؟ قال: عالم يلتمس العلم. قال:','105','0','0');
INSERT INTO [t23102] VALUES (104,'أين؟ ومن أين؟ قالوا: ربنا عبيد لك من أهل الأرض , ذكروك فذكرناك، قال: ويقولون ماذا؟','106','0','0');
INSERT INTO [t23102] VALUES (105,'من قال: لا إله إلا الله، والحمد لله، والله أكبر، وسبحان الله، عرج بها ملك إلى السماء ,','107','0','0');
INSERT INTO [t23102] VALUES (106,'من أحب الكلام إلى الله أن يقول الرجل: سبحانك اللهم وبحمدك، وتبارك اسمك، وتعالى جدك، ولا','108','0','0');
INSERT INTO [t23102] VALUES (107,'من قال حين يقوم من مجلسه: سبحانك اللهم وبحمدك، أشهد أن لا إله إلا الله، أستغفرك وأتوب','109','0','0');
INSERT INTO [t23102] VALUES (108,'لله مائة اسم غير واحد , من أحصاها دخل الجنة»','110','0','0');
INSERT INTO [t23102] VALUES (109,'خذوا جنتكم، خذوا جنتكم - يعني السلاح - من النار» . قالوا: يا رسول الله، أمن عدو حضر؟','111','0','0');
INSERT INTO [t23102] VALUES (110,'من ستر ما بين عورات بني آدم وبين أعين الجن والشياطين أن يقول أحدكم إذا وضع ثيابه: بسم','112','0','0');
INSERT INTO [t23102] VALUES (111,'إذا فرغ من طعام قال: «الحمد لله الذي أطعمنا وسقانا، وجعلنا مسلمين»','113','0','0');
INSERT INTO [t23102] VALUES (112,'الله ليحب أن يذكر في الأسواق، وذلك للغط الناس , وغفلتهم، وإني لآتي السوق وما لي فيه حاجة','114','0','0');
INSERT INTO [t23102] VALUES (113,'أنيروا بيوتكم بذكر الله، واجعلوا لبيوتكم من صلاتكم جزءا، ولا تتخذوها قبرا كما اتخذت','115','0','0');
INSERT INTO [t23102] VALUES (114,'على ابن آدم سبعة أغلاق فإن حدث نفسه بحسنة فأخرجها من الأغلاق كلها كتبت له عشرا، وإن لم','116','0','0');
INSERT INTO [t23102] VALUES (115,'أربع لا يهلك بعدهن إلا هالك , قال: إذا عمل الرجل الحسنة كتبت عشرا، وإن هم بها ولم يعملها','117','0','0');
INSERT INTO [t23102] VALUES (116,'أعيذكما بكلمة الله التامة , من كل شيطان وهامة، ومن كل عين لامة»','118','0','0');
INSERT INTO [t23102] VALUES (117,'بسم الله , لا بأس، أذهب البأس , رب الناس، واشف , أنت الشافي , لا يكشف الضر إلا أنت» .','119','0','0');
INSERT INTO [t23102] VALUES (118,'اللهم -[٣٠٠]- إني أعوذ بك من الشيطان: من همزه، ونفخه، ونفثه» . قال: «فهمزه: الموت،','120','0','0');
INSERT INTO [t23102] VALUES (119,'ليس من شيء يستطيع يتغير عن خلق الله الذي خلقه، ولكن لهم سحرة كسحرتكم، فإذا رأيتم من ذلك','121','0','0');
INSERT INTO [t23102] VALUES (120,'فيسرك أن يبرأ» ؟ قال: «قل: اللهم يا حليم , يا كريم , اشف فلانا»','122','0','0');
INSERT INTO [t23102] VALUES (121,'أعوذ بوجهك الكريم , وباسمك العظيم , وبكلماتك التامات , التي لا يجاوزهن بر ولا فاجر , الذي','123','0','0');
INSERT INTO [t23102] VALUES (122,'ما تعار -[٣٠٧]- عبد من الليل , فقال: لا إله إلا أنت , رب , ظلمت نفسي , فاغفر لي، إلا خرج','124','0','0');
INSERT INTO [t23102] VALUES (123,'إذا استيقظ الرجل فقال: سبحان الله الذي يحيي الموتى، وهو على كل شيء قدير. قال الله: صدق','125','0','0');
INSERT INTO [t23102] VALUES (124,'إذا كان جوف الليل - أو من آخر الليل - اطلع ملك من السماء فقال: سبحان الملك القدوس، ثم','126','0','0');
INSERT INTO [t23102] VALUES (125,'مما خلق الله لديكا , رجلاه في الأرض -[٣١٢]- السفلى، وجناحاه في الهواء، وعنقه مثنية دون','127','0','0');
INSERT INTO [t23102] VALUES (126,'ألا أعلمك كلمات إذا قلتهن نمت؟» -[٣١٥]- قال: بلى يا رسول الله. قال: «قل: اللهم رب','128','0','0');
INSERT INTO [t23102] VALUES (127,'الله ليفتح أبواب السماء للثلث الباقي، ثم يهبط إلى السماء الدنيا، ثم يبسط يده: ألا عبد','129','0','0');
INSERT INTO [t23102] VALUES (128,'لو أنكم تكونون إذا خرجتم من عندي على الحال الذي أنتم عليها عندي , لزارتكم الملائكة في','130','0','0');
INSERT INTO [t23102] VALUES (129,'اللهم إني أعوذ بك من الهدمتين والعميين، ومن قترة وما ولد» قال محمد: «الهدمتين: الخبط','134','0','0');
INSERT INTO [t23102] VALUES (130,'يا عبادي , كلكم مذنب إلا من عافيت , فاستغفروني أغفر لكم، من علم منكم أني -[٣٢٢]- ذو قدرة','135','0','0');
INSERT INTO [t23102] VALUES (131,'إني لأستغفر الله في اليوم كذا وكذا»','136','0','0');
INSERT INTO [t23102] VALUES (132,'المؤمن يرى ذنوبه كأنه في أصل جبل , يخاف أن ينقلب عليه، والفاجر يرى ذنوبه كأنه مر على أنفه','137','0','0');
INSERT INTO [t23102] VALUES (133,'الله أفرح بتوبة عبده من رجل بأرض دوية مهلكة , معه راحلته , عليها طعامه وشرابه، فوضع رأسه','138','0','0');
INSERT INTO [t23102] VALUES (134,'بحسب امرئ من العلم أن يخاف الله، وبحسبه من الكذب أن يستغفر الله، ثم','139','0','0');
INSERT INTO [t23102] VALUES (135,'أبصر عبدا على فاحشة , فدعا عليه، فأوحى الله إليه: مهلا يا إبراهيم، لا تدع على عبادي، فإنك','140','0','0');
INSERT INTO [t23102] VALUES (136,'التوبة من الذنب: الندم عليه»','141','0','0');
INSERT INTO [t23102] VALUES (137,'التوبة من الذنب أن لا يعود إليه»','142','0','0');
INSERT INTO [t23102] VALUES (138,'الذنوب ثلاثة: ذنب يغفر، وذنب -[٣٣٣]- لا يغفر، وذنب يجازى به صاحبه، فأما الذنب الذي يغفر:','143','0','0');
INSERT INTO [t23102] VALUES (139,'الله يدعو العبد يوم القيامة , فيستره بيده فيقول: «تعرف ما هاهنا؟» فيقول: نعم يا رب.','144','0','0');
INSERT INTO [t23102] VALUES (140,'للجنة سبعة أبواب , كلها -[٣٣٥]- تفتح وتغلق، غير باب التوبة موكل به ملك , اعمل ولا','145','0','0');
INSERT INTO [t23102] VALUES (141,'من قال: سبحان الله وبحمده، أستغفر الله , وأتوب إليه , إلا كتب في رق، ثم طبع عليها طابع من','146','0','0');
INSERT INTO [t23102] VALUES (142,'دعوتني يوم كذا وكذا، وسألتني يوم كذا وكذا , حتى يعد عليه فيما يعد عليه، فقلت: يا رب ,','147','0','0');
INSERT INTO [t23102] VALUES (143,'الله لا ينام، ولا ينبغي له أن ينام، يخفض القسط ويرفعه، ويرفع إليه عمل النهار قبل الليل،','148','0','0');
INSERT INTO [t23102] VALUES (144,'ليس من نفس تتوب قبل مرضها الذي تموت فيه إلا تاب الله عليها حتى تطلع الشمس من','149','0','0');
INSERT INTO [t23102] VALUES (145,'تعرض أعمال بني آدم كل اثنين وخميس، فيغفر لكل مسلم , إلا رجلا في قلبه حنة فيقول: اتركوه','150','0','0');
INSERT INTO [t23102] VALUES (146,'ثلاث من لم يكن فيه , فإن الله يغفر ما دون ذلك لمن يشاء: من مات لا يشرك بالله شيئا، ومن لم','151','0','0');
INSERT INTO [t23102] VALUES (147,'في بني إسرائيل رجل يقال له الكفل لا يتورع من ذنب عمله، فاتبع امرأة , فأعطاها ستين دينارا','152','0','0');
INSERT INTO [t23102] VALUES (148,'الله لا يرحم من لا يرحم، ولا يغفر لمن لا يغفر، ولا يتوب على من لا','153','0','0');
INSERT INTO [t23102] VALUES (149,'ما عملت في الدنيا؟ قال: ما عملت لك يا رب في الدنيا مثقال ذرة , أو خردلة - قالها ثلاث','154','0','0');
INSERT INTO [t23102] VALUES (150,'جعلت الشفاعة لأهل الكبائر من أمتي»','156','0','0');
INSERT INTO [t23102] VALUES (151,'لا يصيب مؤمنا من شوكة فما فوقها إلا حط الله له بها خطيئة»','157','0','0');
INSERT INTO [t23102] VALUES (152,'قال موسى: «يا رب ارزقني عملا ينضب به جسدي , يكون شكرا لما أنعمت به علي» . قال: فقال: قل:','158','0','0');
INSERT INTO [t23102] VALUES (153,'من قال أول النهار: لا إله إلا الله , وحده لا شريك له، له الملك وله الحمد , وهو على كل شيء','159','0','0');
INSERT INTO [t23102] VALUES (154,'الخمس من قالهن استجيب له في خمس لنفسه: لا إله إلا الله، وحده لا شريك له، له الملك وله','160','0','0');
INSERT INTO [t23102] VALUES (155,'من قال: لا إله إلا الله , نفعته يوما من الدهر , أصابه قبل ذلك ما','161','0','0');
INSERT INTO [t23102] VALUES (156,'من قال لا إله إلا الله , وحده لا شريك له، له الملك , وله الحمد , وهو على كل شيء قدير، كن','162','0','0');
INSERT INTO [t23102] VALUES (157,'من قال: لا إله إلا الله , وحده لا شريك له، له الملك , وله الحمد، وهو على كل شيء قدير، كان','163','0','0');
INSERT INTO [t23102] VALUES (158,'من قال مائة مرة عند طلوع الشمس: لا إله إلا الله , وحده لا شريك له، له الملك , وله الحمد ,','164','0','0');
INSERT INTO [t23102] VALUES (159,'أكثروا مسألة الله الجنة، واستعيذوا بالله من النار , فإنهما شافعان مشفعان، فإن العبد إذا','165','0','0');
INSERT INTO [t23102] VALUES (160,'ما سأل الله العبد شيئا أفضل من المغفرة، ولا أعطي العباد شيئا أفضل من أن يغفر','166','0','0');
INSERT INTO [t23102] VALUES (161,'اربعوا على أنفسكم، إنكم ليس تدعون أصم ولا غائبا، إنكم تدعون سميعا قريبا , وهو معكم» .','167','0','0');
