INSERT INTO [t23118] VALUES (2,'ليس في الجنة من شيء في الدنيا إلا الأسماء "','4','1','0');
INSERT INTO [t23118] VALUES (3,'إنما الوضوء مما خرج، وليس مما دخل، وإنما الفطر مما دخل، وليس مما خرج "','5','1','0');
INSERT INTO [t23118] VALUES (4,'فاذكروا اسم الله عليها صواف} [الحج: ٣٦] فيقول: " معقولة على ثلاث، يقول: باسم الله، والله','6','1','0');
INSERT INTO [t23118] VALUES (5,'إن أول ما خلق الله من شيء القلم، فقال: اكتب، -[٥٧]- فقال: أي رب، وما أكتب؟ قال: اكتب','7','1','0');
INSERT INTO [t23118] VALUES (6,'ما أصاب من مصيبة إلا بإذن الله ومن يؤمن بالله يهد قلبه} [التغابن: ١١] قال: فسألناه عنها','8','1','0');
INSERT INTO [t23118] VALUES (7,'الرؤيا ثلاثة: حضور الشيطان، والرجل يحدث نفسه بالنهار، وما يعني فيراه بالليل، والرؤيا التي','9','1','0');
INSERT INTO [t23118] VALUES (8,'لا تدخلوا الجنة حتى تؤمنوا، ولا تؤمنوا حتى تحابوا، أولا أدلكم على شيء إذا فعلتموه تحاببتم','10','1','0');
INSERT INTO [t23118] VALUES (9,'انظروا إلى من هو أسفل منكم، ولا تنظروا إلى من هو فوقكم؛ فإنه أجدر أن لا -[٦١]- تزدروا','11','1','0');
INSERT INTO [t23118] VALUES (10,'لا يقولن أحدكم لعبده: عبدي، ولكن ليقل: فتاي، ولا يقولون لمولاه: مولاي، ولكن ليقل: سيدي','12','1','0');
INSERT INTO [t23118] VALUES (11,'من أطاعني فقد أطاع الله، ومن أطاع الإمام فقد أطاعني، ومن عصاني فقد -[٦٤]- عصى الله، ومن','13','1','0');
INSERT INTO [t23118] VALUES (12,'إن أثقل الصلاة على المنافقين صلاة العشاء والفجر، ولو يعلمون ما فيهما لأتوهما، ولو حبوا','14','1','0');
INSERT INTO [t23118] VALUES (13,'خير الصدقة ما كان عن ظهر غنى، واليد العليا خير من السفلى، وابدأ بمن تعول','15','1','0');
INSERT INTO [t23118] VALUES (14,'كل عمل ابن آدم يضاعف، الحسنة عشر أمثالها، إلى سبعمائة ضعف، قال الله تعالى: إلا الصوم،','16','1','0');
INSERT INTO [t23118] VALUES (15,'لأن يمتلئ الرجل قيحا يريه، خير له من أن يمتلئ شعرا "','17','1','0');
INSERT INTO [t23118] VALUES (16,'أيحب أحدكم إذا رجع إلى أهله أن يجد فيه ثلاث خلفات عظام سمان؟ " قلنا: نعم قال: " فثلاث','18','1','0');
