INSERT INTO [t23118] VALUES (19,'إذا استيقظ أحدكم من منامه، فلا يغمس يده في الإناء، حتى يغسلها ثلاثا؛ فإنه لا يدري أين','21','1','0');
INSERT INTO [t23118] VALUES (20,'أطيعوا الله، وأطيعوا الرسول، وأولي الأمر منكم} [النساء: ٥٩] قال: الأمراء. قال وكيع:','22','1','0');
INSERT INTO [t23118] VALUES (21,'تعرض أعمال العباد كل اثنين وخميس، فيغفر لكل عبد لا يشرك بالله شيئا إلا عبدا بينه وبين','23','1','0');
INSERT INTO [t23118] VALUES (22,'من قرأ في ليلة مائة آية، كتب من القانتين "','24','1','0');
