INSERT INTO [t23118] VALUES (24,'لا تسبوا أصحابي، فوالذي نفسي بيده، لو أن -[٨٢]- أحدكم أنفق مثل أحد ذهبا، ما أدرك مد','26','1','0');
INSERT INTO [t23118] VALUES (25,'لا تسافر امرأة سفرا ثلاثة أيام، فصاعدا، إلا مع أبيها، أو ابنها، أو أخيها، أو زوجها، أو ذي','27','1','0');
INSERT INTO [t23118] VALUES (26,'يدعى نوح يوم القيامة، فيقال له: هل بلغت؟ فيقول: نعم فيدعى قومه، فيقال لهم: هل بلغكم؟','28','1','0');
INSERT INTO [t23118] VALUES (27,'يقول الله تبارك وتعالى يوم القيامة لآدم: يا آدم قم، فابعث بعث النار، قال: فيقول: لبيك','29','1','0');
