INSERT INTO [t24831] VALUES (1,'لو تعلمون ما أعلم لضحكتم قليلا ولبكيتم كثيرا','11','0','0');
INSERT INTO [t24831] VALUES (2,'اللهم اجعل في قلبي نورا , وفي سمعي نورا , وفي بصري نورا','12','0','0');
INSERT INTO [t24831] VALUES (3,'ما كان يحرم من النسب فهو حرام من الرضاع','13','0','0');
INSERT INTO [t24831] VALUES (4,'أحلت ذبائح اليهود والنصارى، لأنهم آمنوا بالتوراة والإنجيل','14','0','0');
INSERT INTO [t24831] VALUES (5,'من شرب الخمر فاجلدوه , ثم إن شرب فاجلدوه , ثم إن شرب فاجلدوه , ثم إن شرب الرابعة','15','0','0');
INSERT INTO [t24831] VALUES (6,'شارب الخمر فاجلدوه , ثم إن شرب فاجلدوه , ثم إن شرب فاجلدوه , فإن شرب فاجلدوه , ثم إن شرب','16','0','0');
INSERT INTO [t24831] VALUES (7,'وقى أحدكم وجهه النار ولو بشق تمرة , فإن لم يجد فكلمة لينة , فإن أحدكم إذا لقي الله يوم','17','0','0');
INSERT INTO [t24831] VALUES (8,'يخرج إلى الصلاة ورأسه يقطر من الجنابة قال: فقال أبو هريرة: هي أعلم إذن','18','0','0');
INSERT INTO [t24831] VALUES (9,'هل تدرون ما هذا؟ قلنا: السحاب. قال: أو المزن. قلنا: أو المزن ‍‍ قال: أو العنان. قلنا: أو','19','0','0');
INSERT INTO [t24831] VALUES (10,'يعتكف العشر الأول من رمضان','20','0','0');
INSERT INTO [t24831] VALUES (11,'طيبت رسول الله صلى الله عليه وسلم لإحرامه، وحله','21','0','0');
INSERT INTO [t24831] VALUES (12,'أذن لي ربي أحدث عن ملك من الملائكة من حملة العرش ما بين شحمة أذنه إلى عاتقه مسيرة سبعمائة','22','0','0');
INSERT INTO [t24831] VALUES (13,'لولا بنو إسرائيل لم يخنز اللحم , ولولا حواء لم تخن امرأة زوجها الدهر قال: وكانت خيبر لمن','23','0','0');
INSERT INTO [t24831] VALUES (14,'ما نقم ابن جميل , إلا أنه كان فقيرا , فأغناه الله ورسوله , وأما خالد فإنكم تظلمون خالدا ,','24','0','0');
INSERT INTO [t24831] VALUES (15,'الرجل ليتكلم بالكلمة من الخير ما يعلم مبلغها , يكتب الله له بها رضوانه إلى يوم القيامة ,','25','0','0');
INSERT INTO [t24831] VALUES (16,'هاجر إبراهيم بسارة , فدخل بها قرية فيها ملك من الملوك أو جبار من الجبابرة , فقيل: دخل','26','0','0');
INSERT INTO [t24831] VALUES (17,'إذا كفنتموه فلا تغطوا وجهه حتى يبعث يوم القيامة ملبيا ٢٧ -. عن مطر، عن عمرو بن دينار، عن','27','0','0');
INSERT INTO [t24831] VALUES (18,'الله لغني عن مشي أختك , فلتركب ولتهد بدنة','28','0','0');
INSERT INTO [t24831] VALUES (19,'ليتصدق بدينار أو نصف دينار','29','0','0');
INSERT INTO [t24831] VALUES (20,'نهى رسول الله صلى الله عليه وسلم عن الجلب والجنب , ونهى عن النجش واللمس في البيع , ونهى','30','0','0');
INSERT INTO [t24831] VALUES (21,'يخرج ناس من المدينة , والمدينة خير لهم لو كانوا يعلمون','31','0','0');
INSERT INTO [t24831] VALUES (22,'مجامرهم اللؤلؤ , وأمشاطهم الذهب','32','0','0');
INSERT INTO [t24831] VALUES (23,'حائط الجنة لبنة من فضة , لبنة من ذهب','33','0','0');
INSERT INTO [t24831] VALUES (24,'ليرى مخ ساقها من وراء الحلل وإن عليها سبعين حلة','34','0','0');
INSERT INTO [t24831] VALUES (25,'رمي رجل بسهم في صدره، أو قال في حلقه، فمات , فأدرج في ثيابه كما هو , قال: ونحن مع رسول','35','0','0');
INSERT INTO [t24831] VALUES (26,'أفاء الله خيبر على رسوله , فأقرهم رسول الله صلى الله عليه وسلم كما كانوا , وجعلها بينه','36','0','0');
INSERT INTO [t24831] VALUES (27,'لا عدوى , ولا طيرة , ولا شؤم , فإن يكن في شيء ففي الربع والفرس والمرأة','37','0','0');
INSERT INTO [t24831] VALUES (28,'لا عدوى، ومن أعدى الأول ولا صفر، ولا غول','38','0','0');
INSERT INTO [t24831] VALUES (29,'مات رسول الله صلى الله عليه وسلم في بيتي , وفي يومي , وبين سحري، ونحري , وريقه مع ريقي ,','39','0','0');
INSERT INTO [t24831] VALUES (30,'سرب رسول الله صلى الله عليه وسلم نساءه ليلة جمع قبل الزحام','40','0','0');
INSERT INTO [t24831] VALUES (31,'ما كانت نبوة قط إلا تبعتها خلافة , ولا كانت خلافة قط إلا تبعها ملك , ولا كانت صدقة إلا','41','0','0');
INSERT INTO [t24831] VALUES (32,'من قال: يثرب مرة , فليقل: المدينة عشر مرات','42','0','0');
INSERT INTO [t24831] VALUES (33,'عجبت لهؤلاء , يسألنني , ويستكثرنني , عالية أصواتهن فوق صوتي , فلما سمعن صوتك تبادرن','43','0','0');
INSERT INTO [t24831] VALUES (34,'ثكلته أمه قاتل المؤمن متعمدا , يجيء يوم القيامة متعلقا , رأسه بيمينه أو بشماله , قد لزم','44','0','0');
INSERT INTO [t24831] VALUES (35,'لله ضنائن من عباده , يضن بهم من القتل والأمراض , يعيشهم في عافية , ويميتهم في','45','0','0');
INSERT INTO [t24831] VALUES (36,'من جر ثوبه من الخيلاء , لم ينظر الله إليه يوم القيامة قال نافع: فبلغني أن أم سلمة زوج','46','0','0');
INSERT INTO [t24831] VALUES (37,'اقتلوا الفويسق يعني الوزغ','47','0','0');
INSERT INTO [t24831] VALUES (38,'هي الليلة. ثم رجع , فقال: أو القابلة. يريد ليلة ثلاث وعشرين','48','0','0');
INSERT INTO [t24831] VALUES (39,'اجلسي , بارك الله فيك , أما نحن فلا حاجة لنا فيك , ولكن تملكيني أمرك؟ قالت: نعم. فنظر','49','0','0');
INSERT INTO [t24831] VALUES (40,'إن إبراهيم حرم مكة وأنا أحرم المدينة , وأنها حرام ما بين حرتيها , لا يقطع منها شجرة إلا','50','0','0');
INSERT INTO [t24831] VALUES (41,'لا تلاعنوا بلعنة الله , ولا تلاعنوا بغضب الله , ولا تدعوا بجهنم أو قال','51','0','0');
INSERT INTO [t24831] VALUES (42,'عق رسول الله صلى الله عليه وسلم عن الحسن والحسين بكبشين كبشين','52','0','0');
INSERT INTO [t24831] VALUES (43,'لا تدعوا بالموت , ولا تتمنوه , فمن كان داعيا لا بد فليقل: اللهم أحيني ما كانت الحياة خيرا','53','0','0');
INSERT INTO [t24831] VALUES (44,'نهى النبي عليه السلام عن الأكل والشرب، في آنية الذهب والفضة','54','0','0');
INSERT INTO [t24831] VALUES (45,'لا عليكم ألا تفعلوا فإنما هو القدر','55','0','0');
INSERT INTO [t24831] VALUES (46,'لا يتقدمن أحدكم قبل صوم رمضان بصوم يوم أو يومين إلا صوما كان يصومه','56','0','0');
INSERT INTO [t24831] VALUES (47,'للمؤمن في الجنة ثلاثون زوجة. فقلنا: يا رسول الله , أو له قوة ذلك؟ قال: إنه يعطى قوة','57','0','0');
INSERT INTO [t24831] VALUES (48,'إن الله أمرني أن أقرئك القرآن فقال أبي: يا رسول الله , سماني؟ قال: نعم. قال: فجعل أبي','58','0','0');
INSERT INTO [t24831] VALUES (49,'يا حي يا قيوم','59','0','0');
INSERT INTO [t24831] VALUES (50,'تبعث نار على أهل المشرق , فتحشرهم إلى المغرب , تبيت معهم حيث باتوا , وتقيل معهم حيث قالوا','60','0','0');
INSERT INTO [t24831] VALUES (51,'صلاة في مسجدي أفضل من أربع صلوات فيه ولنعم المصلى هو , وليوشكن لأن يكون للرجل مثل شطن','61','0','0');
INSERT INTO [t24831] VALUES (52,'مثل أمتي أربعة: رجل أعطاه الله مالا , فجعله في سبله التي فرض الله ورضي , فرآه رجل من','62','0','0');
INSERT INTO [t24831] VALUES (53,'أليس يشهد أن لا إله إلا الله , وأن محمدا عبده ورسوله؟ قالوا: بلى , ولا خير في شهادته.','63','0','0');
INSERT INTO [t24831] VALUES (54,'ألن الكلام , وأفش السلام , وأطعم الطعام , وصل الأرحام , وصل والناس نيام , تدخل الجنة','64','0','0');
INSERT INTO [t24831] VALUES (55,'تزوجها حلالا , وبنى بها حلالا , وتزوجها بسرف , وبنى بها تحت السقيفة','65','0','0');
INSERT INTO [t24831] VALUES (56,'اثبت حراء , فإنه ليس عليك إلا نبي، أو صديق، أو شهيد','66','0','0');
INSERT INTO [t24831] VALUES (57,'الدنيا حلوة خضرة , وإن الله مستخلفكم فيها , فناظر كيف تعملون , فاتقوا الدنيا واتقوا','68','0','0');
INSERT INTO [t24831] VALUES (58,'أقل ساكني الجنة النساء','69','0','0');
INSERT INTO [t24831] VALUES (59,'سيكون قوم يأكلون بألسنتهم كما تأكل البقر بألسنتها من الأرض','70','0','0');
INSERT INTO [t24831] VALUES (60,'خذوها وما حولها من السمن فاطرحوه','71','0','0');
INSERT INTO [t24831] VALUES (61,'ألم تري أن قومك حين بنوا الكعبة اقتصروا عن قواعد إبراهيم، فقلت: يا رسول الله , أفلا تردها','72','0','0');
INSERT INTO [t24831] VALUES (62,'عهد إلينا إذا كان عند مكاتب إحداكن وفاء لما بقي عليه من مكاتبته , فاضربن دونه','73','0','0');
INSERT INTO [t24831] VALUES (63,'ما مس رسول الله صلى الله عليه وسلم بيده امرأة قط إلا أن يأخذ عليها , فإذا أخذ عليها','74','0','0');
INSERT INTO [t24831] VALUES (64,'كل مسكر حرام','75','0','0');
INSERT INTO [t24831] VALUES (65,'كل شراب أسكر فهو حرام','76','0','0');
INSERT INTO [t24831] VALUES (66,'أي ساعة هذه؟ قال: إني شغلت اليوم فلم أنقلب إلى أهلي حتى سمعت التأذين , فلم ـ يعني: أزد ـ','77','0','0');
INSERT INTO [t24831] VALUES (67,'للمملوك طعامه وكسوته بالمعروف , ولا يكلف من العمل ما لا يطيق','78','0','0');
INSERT INTO [t24831] VALUES (68,'أيما دار أو أرض قسمت في الجاهلية , فهي على قسم الجاهلية , وأيما دار أو أرض قسمت في','79','0','0');
INSERT INTO [t24831] VALUES (69,'أين المتحابون بجلالي؟ اليوم أظلهم في ظلي يوم لا ظل إلا ظلي','80','0','0');
INSERT INTO [t24831] VALUES (70,'أما علمت أن الفخذ عورة؟','81','0','0');
INSERT INTO [t24831] VALUES (71,'خلق أحدكم يجمع في بطن أمه أربعين يوما , ثم يكون علقة مثل ذلك , ثم يكون مضغة مثل ذلك','82','0','0');
INSERT INTO [t24831] VALUES (72,'ما من نفس منفوسة إلا قد كتب مكانها من الجنة أو النار , وإلا قد كتبت شقية أو سعيدة الحديث','83','0','0');
INSERT INTO [t24831] VALUES (73,'ما الإيمان؟ قال: أن تؤمن بالله واليوم الآخر والملائكة والكتاب والنبيين. فذكره , وذكر فيه','84','0','0');
INSERT INTO [t24831] VALUES (74,'لا يعدي شيء شيئا. فقال أعرابي: يا رسول الله , فإن النقبة لتكون بمشفر البعير أو بذنبه','85','0','0');
INSERT INTO [t24831] VALUES (75,'ذلك القدر','86','0','0');
INSERT INTO [t24831] VALUES (76,'الرجل ليعمل الدهر الطويل بأعمال أهل الجنة , ثم يختم الله عمله بأعمال أهل النار , وإن','87','0','0');
INSERT INTO [t24831] VALUES (77,'لا تنذروا فإن النذر لا يرد شيئا من القدر , وإنما يستخرج به من البخيل','88','0','0');
INSERT INTO [t24831] VALUES (78,'احتج آدم وموسى','89','0','0');
INSERT INTO [t24831] VALUES (79,'النذر لا يرد من القدر شيئا , ولكنه يستخرج به من البخيل','90','0','0');
INSERT INTO [t24831] VALUES (80,'اعملوا فإن كلا لا ينال إلا بعمل. فقال عمر: ذاك حين نجتهد','91','0','0');
INSERT INTO [t24831] VALUES (81,'كذبت يهود، لو أراد الله أن يخلقه لم تستطع أن تصرفه','92','0','0');
INSERT INTO [t24831] VALUES (82,'تحاج آدم وموسى , فحج آدم موسى','93','0','0');
INSERT INTO [t24831] VALUES (83,'ليس من كل الماء يكون الولد , وما يقدر أن يكون كان','94','0','0');
INSERT INTO [t24831] VALUES (84,'من أحب أن يمثل له العباد قياما فليتبوأ مقعده من النار','95','0','0');
INSERT INTO [t24831] VALUES (85,'قبض رسول الله صلى الله عليه وسلم وقد قرأت محكم القرآن وأنا مختون ابن عشر','96','0','0');
INSERT INTO [t24831] VALUES (86,'قبض رسول الله صلى الله عليه وسلم وأنا ابن خمس عشرة سنة','97','0','0');
INSERT INTO [t24831] VALUES (87,'رسول الله صلى الله عليه وسلم أخبرني: أن آخر شراب أشربه لبن حتى أموت','98','0','0');
INSERT INTO [t24831] VALUES (88,'أمرنا رسول الله صلى الله عليه وسلم أن نحثي في وجوه المداحين التراب','99','0','0');
INSERT INTO [t24831] VALUES (89,'أهل الدرجات العلى يراهم الذين أسفل منهم كما يرى الكوكب الدري في أفق السماء , وإن أبا بكر','100','0','0');
INSERT INTO [t24831] VALUES (90,'تكون فتنة الرجل في أهله وماله وولده وجاره , تكفرها الصلاة الحديث','101','0','0');
INSERT INTO [t24831] VALUES (91,'ابن آدم , إنك ما دعوتني ورجوتني أغفر لك كل ما كان فيك , ولو لقيتني بقراب الأرض خطيئة','102','0','0');
INSERT INTO [t24831] VALUES (92,'أنا أغنى الشركاء عن الشرك فمن عمل عملا وأشرك فيه غيري فأنا منه بريء وهو للذي','103','0','0');
INSERT INTO [t24831] VALUES (93,'إذا هم عبدي بحسنة ولم يعملها كتبتها له حسنة , فإن عملها: كتبت له عشر حسنات إلى سبع مائة','104','0','0');
INSERT INTO [t24831] VALUES (94,'استقرضت عبدي فلم يقرضني , وشتمني عبدي ولم ينبغي له شتمي , يقول: وادهراه وأنا الدهر.','105','0','0');
INSERT INTO [t24831] VALUES (95,'المؤمن يغار , والله أشد غيرة','106','0','0');
INSERT INTO [t24831] VALUES (96,'الحسنة بعشر أمثالها , والصوم لي , وأنا أجزي به , إنه يذر طعامه وشرابه من أجلي , ولخلوف فم','107','0','0');
INSERT INTO [t24831] VALUES (97,'لا تسبوا الدهر: فإن الله هو الدهر','108','0','0');
INSERT INTO [t24831] VALUES (98,'اختصمت الجنة والنار , فقالت الجنة: يا رب , ما لها يدخلها ضعفاء الناس وسقاطهم','109','0','0');
INSERT INTO [t24831] VALUES (99,'الله وتر، يحب الوتر','110','0','0');
INSERT INTO [t24831] VALUES (100,'الله يحب الرفق في الأمر كله','111','0','0');
INSERT INTO [t24831] VALUES (101,'أتاني جبريل في كفه كالمرآة البيضاء , فيها كالنكتة السوداء فذكر حديث','112','0','0');
INSERT INTO [t24831] VALUES (102,'من جر ثيابه أو ثوبه من الخيلاء، لم ينظر الله إليه، يوم القيامة','113','0','0');
INSERT INTO [t24831] VALUES (103,'الزمان قد استدار كهيئته يوم خلق الله السماوات والأرض , السنة اثنا عشر','114','0','0');
INSERT INTO [t24831] VALUES (104,'ألا لا ترجعن بعدي ضلالا، يضرب بعضكم رقاب بعض , ألا فليبلغ الشاهد','115','0','0');
INSERT INTO [t24831] VALUES (105,'كل العمل كفارة , والصوم لي وأنا أجزي به , ولخلوف فم الصائم أطيب عند الله من ريح','116','0','0');
INSERT INTO [t24831] VALUES (106,'الله عز وجل لا ينظر إلى من يجر إزاره، أو قال ثيابه، بطرا','117','0','0');
INSERT INTO [t24831] VALUES (107,'ما طلعت الشمس على خير يوم من يوم الجمعة؛ فيه خلق الله آدم , وفيه دخل الجنة , وفيه خرج','118','0','0');
INSERT INTO [t24831] VALUES (108,'رفعت إلي سدرة المنتهى , فإذا أربعة أنهار: نهران ظاهران , ونهران باطنان , فأما الظاهران','119','0','0');
INSERT INTO [t24831] VALUES (109,'إزرة المؤمن إلى أنصاف الساقين','120','0','0');
INSERT INTO [t24831] VALUES (110,'إذا كان أحدكم في صلاته فإنه يناجي ربه , فلا يتفلن عن يمينه ولا بين يديه، ولكن عن يساره','121','0','0');
INSERT INTO [t24831] VALUES (111,'ثلاثة لا يكلمهم الله , ولا ينظر إليهم يوم القيامة , ولا يزكيهم , ولهم عذاب أليم: ملك كذاب','122','0','0');
INSERT INTO [t24831] VALUES (112,'من خالقك، وخالق من قبلك، وخالق من بعدك؟ قال: الله الحديث','123','0','0');
INSERT INTO [t24831] VALUES (113,'الله قبل وجه أحدكم إذا كان في الصلاة، فلا يتنخم أحدكم قبل وجهه إذا كان في','124','0','0');
INSERT INTO [t24831] VALUES (114,'بعثت إلى أمة أميين، فيهم الصغير، والشيخ الفاني، والعجوز، فليقرءوا القرآن على سبعة','125','0','0');
INSERT INTO [t24831] VALUES (115,'رأيت جبريل واقفا على السدرة له ستمائة جناح، تسد أجنحته ما بين المشرق','126','0','0');
INSERT INTO [t24831] VALUES (116,'عبدي عند ظنه بي، وأنا معه حين يذكرني، إن ذكرني في نفسه ذكرته في نفسي، وإن ذكرني وحده','127','0','0');
INSERT INTO [t24831] VALUES (117,'صلاة الجماعة تفضل صلاة الفذ بسبع وعشرين درجة','128','0','0');
INSERT INTO [t24831] VALUES (118,'كل خطوة يخطوها أحدكم إلى الصلاة تكتب له حسنة، وتمحى عنه بها سيئة قال أو قال: تكتب له حسنة','129','0','0');
INSERT INTO [t24831] VALUES (119,'في الجنة شجرة، يسير الراكب في ظلها مائة سنة لا يقطعها','130','0','0');
INSERT INTO [t24831] VALUES (120,'دخلت امرأة النار من أجل هرة لها؛ أوثقتها، فلا هي أطعمتها، ولا هي تركتها تأكل من خشاش','131','0','0');
INSERT INTO [t24831] VALUES (121,'لا يزال الله في حاجة أحدكم ما كان في حاجة أخيه، وإنما امرؤ فك عن امرئ حلقة بها عنه حاجته','132','0','0');
INSERT INTO [t24831] VALUES (122,'للمملوك طعامه وكسوته بالمعروف، ولا يكلف من العمل ما لا يطيق','133','0','0');
INSERT INTO [t24831] VALUES (123,'ليت رجلا صالحا يحرسني الليلة. قالت: إذ سمعت صوت السلاح، فقال رسول الله صلى الله عليه','134','0','0');
INSERT INTO [t24831] VALUES (124,'قريش، والأنصار، وأسلم، وغفار، ومن كان من أشجع، وجهينة، أو جهينة، وأشجع حلفاء موالي، ليس','135','0','0');
INSERT INTO [t24831] VALUES (125,'رأيت كأن الناس اجتمعوا، فقام أبو بكر فنزع ذنوبا، وفي نزعه ضعف، ثم قام عمر فاستحالت غربا','136','0','0');
INSERT INTO [t24831] VALUES (126,'عبدا خيره ربه بين أن يؤتيه من زهرة الدنيا ما شاء وبين ما عنده، فاختار ما عنده، فبكى أبو','137','0','0');
INSERT INTO [t24831] VALUES (127,'ألا إنكم تعيبون أسامة، وتطعنون في إمارته، وقد فعلتم ذلك بأبيه من قبل، وإن كان لخليقا','138','0','0');
INSERT INTO [t24831] VALUES (128,'أنزل على رسول الله صلى الله عليه وسلم القرآن وهو ابن ثلاث وأربعين سنة، فأقام بمكة عشر','139','0','0');
INSERT INTO [t24831] VALUES (129,'اهتز العرش لموت سعد بن معاذ','140','0','0');
INSERT INTO [t24831] VALUES (130,'المهاجرون، والأنصار بعضهم أولياء بعض في الدنيا والآخرة، والطلقاء من قريش، والعتقاء من','141','0','0');
INSERT INTO [t24831] VALUES (131,'من أصحابي من لا يراني بعد أن أفارقه أبدا','142','0','0');
INSERT INTO [t24831] VALUES (132,'ما أقلت الغبراء ولا أظلت الخضراء من ذي لهجة أصدق من أبي ذر','143','0','0');
INSERT INTO [t24831] VALUES (133,'لا تسبوا أصحابي، فإن أحدكم لو أنفق مثل أحد ذهبا ما أدرك مد أحدهم ولا','144','0','0');
INSERT INTO [t24831] VALUES (134,'أما أنه أحد الثمانين الذين ثبتوا مع رسول الله صلى الله عليه وسلم يوم حنين، تكفل الله','145','0','0');
INSERT INTO [t24831] VALUES (135,'بينا أنا نائم أوتيت بقدح فيه لبن، فشربت حتى رأيت الري يخرج من أظافيري، ثم ناولت فضلي عمر','146','0','0');
INSERT INTO [t24831] VALUES (136,'بينا أنا نائم إذ رأيت أني في الجنة، فإذا قصر مبني، إلى جانبه جارية، فقلت: لمن هذا القصر؟','147','0','0');
INSERT INTO [t24831] VALUES (137,'بينا أنا نائم رأيت كأن الناس يعرضون، عليهم قمص إلى ذيلهم وأسفل من ذلك، فعرض علي عمر، عليه','148','0','0');
INSERT INTO [t24831] VALUES (138,'على فراشه لابس مرط عائشة، فأذن له، فكلمه بحاجته، ثم خرج، ثم جاء عمر','149','0','0');
INSERT INTO [t24831] VALUES (139,'كنت أفتل قلائد بدن رسول الله صلى الله عليه وسلم، فيبعث بها، ثم لا يمسك عن شيء مما أحل','150','0','0');
INSERT INTO [t24831] VALUES (140,'كنت أفتل القلائد لهدي رسول الله صلى الله عليه وسلم، فيبعث بها وهو مقيم عندنا، ثم لا يحرم','151','0','0');
INSERT INTO [t24831] VALUES (141,'تركت أن أمسحه، كلما أتيت عليه، منذ رأيت رسول الله صلى الله عليه وسلم','152','0','0');
INSERT INTO [t24831] VALUES (142,'صلاة المسافر ركعتان وصلاها أبو بكر ركعتين، وصلاها عمر ركعتين، وصلاها عثمان ركعتين صدرا من','153','0','0');
INSERT INTO [t24831] VALUES (143,'يسعى ثلاثة طوافات من أول طوافه، ويمشي أربعة أطواف مشيا، ثم يصلي ركعتين، ثم يطوف بين الصفا','154','0','0');
INSERT INTO [t24831] VALUES (144,'أحسنت','155','0','0');
INSERT INTO [t24831] VALUES (145,'لا يلبس القميص، ولا العمامة، ولا السراويل، ولا الخفين، إلا أن لا يجد نعلين، فإن لم يجد','156','0','0');
INSERT INTO [t24831] VALUES (146,'نهى النبي صلى الله عليه وسلم أن تنتقب المرأة وهي محرمة','157','0','0');
INSERT INTO [t24831] VALUES (147,'من لم يجد إزارا فليلبس السراويل، ومن لم يجد النعلين فليلبس الخفين','158','0','0');
INSERT INTO [t24831] VALUES (148,'كنت أطيب رسول الله صلى الله عليه وسلم عند إحرامه حين يحرم، وعند حله حين يرمي الجمر قبل أن','159','0','0');
INSERT INTO [t24831] VALUES (149,'قد ضمخ رأسه بالمسك، أفترى ذلك كان طيبا أم لا','160','0','0');
INSERT INTO [t24831] VALUES (150,'كأني أنظر إلى وبيص الطيب في مفارق رسول الله صلى الله عليه وسلم وهو','161','0','0');
INSERT INTO [t24831] VALUES (151,'كنت أطيب رسول الله صلى الله عليه وسلم عند إحرامه حين يحرم، وعند حله حين','162','0','0');
INSERT INTO [t24831] VALUES (152,'إذا قدم مكة نزل بذي طوى، ويبيت حتى يصلي صلاة الصبح، قال ابن عمر: ومصلى رسول الله صلى الله','163','0','0');
INSERT INTO [t24831] VALUES (153,'إن عبد الله بن عباس، والمسور بن مخرمة تماريا وهما بالأبواء، فقال ابن عباس: يغسل المحرم','164','0','0');
INSERT INTO [t24831] VALUES (154,'حلق رأسه في حجة الوادع','165','0','0');
INSERT INTO [t24831] VALUES (155,'أيؤذيك هوامك؟ فقلت: نعم. قال: فأنزل الله عز وجل {فمن كان منكم مريضا أو به أذى من رأسه','166','0','0');
INSERT INTO [t24831] VALUES (156,'صم ثلاثة أيام أو أطعم ستة مساكين مدين مدين، أو أنسك شاة، أي ذلك فعلت أجزأ','167','0','0');
INSERT INTO [t24831] VALUES (157,'حلق رسول الله صلى الله عليه وسلم وأصحابه عام الحديبية غير عثمان، وأبي قتادة، واستغفر رسول','168','0','0');
INSERT INTO [t24831] VALUES (158,'ولدت امرأة أبي بكر الصديق وهي محرمة، فأمرت أن تقضي ما يقضي الحاج غير أن لا تطوف بالبيت','169','0','0');
INSERT INTO [t24831] VALUES (159,'أحابستنا هي؟ فقيل: إنها قد أفاضت. قال: فلا إذا','170','0','0');
INSERT INTO [t24831] VALUES (160,'أحابستنا هي؟ قالت: فقلت: يا رسول الله صلى الله عليه وسلم إنها قد أفاضت، وطافت بالبيت، ثم','171','0','0');
INSERT INTO [t24831] VALUES (161,'لا تشتروا الثمرة حتى يبدو صلاحها','172','0','0');
INSERT INTO [t24831] VALUES (162,'رخص في العرايا، يأخذها أهل البيت بخرصها تمرا، يأكلونه رطبا','173','0','0');
INSERT INTO [t24831] VALUES (163,'هل لك في الدين والدين من حقك؟ كأنه أومى بالشطر قال: نعم. قال: هلم إلى ما غبر','174','0','0');
INSERT INTO [t24831] VALUES (164,'رأيت الناس إذا ابتاعوا الطعام جزافا نهوا أن يبيعوه حتى يؤووه إلى','175','0','0');
INSERT INTO [t24831] VALUES (165,'نهى رسول الله صلى الله عليه وسلم عن بيع الطعام إذا اشتراه أحد حتى','176','0','0');
INSERT INTO [t24831] VALUES (166,'كانوا يشترون الطعام من الركبان في عهد رسول الله صلى الله عليه وسلم، فيبعث عليهم من يمنعهم','177','0','0');
INSERT INTO [t24831] VALUES (167,'من باع عبدا فماله للذي باعه إلا أن يشترط المبتاع، ومن باع نخلا بعد أن تؤبر فثمرتها للذي','178','0','0');
INSERT INTO [t24831] VALUES (168,'البيعان بالخيار من بيعهما ما لم يفترقا، أو يكون بيعهما عن خيار، فإذا كان بيعهما عن خيار','179','0','0');
INSERT INTO [t24831] VALUES (169,'البيعان بالخيار ما لم يتفرقا، إلا أن يكون بيع خيار','180','0','0');
INSERT INTO [t24831] VALUES (170,'لا يحتكر إلا خاطئ','181','0','0');
INSERT INTO [t24831] VALUES (171,'كنت أشتري الطعام فنهاني رسول الله صلى الله عليه وسلم أن أبيعه حتى','182','0','0');
INSERT INTO [t24831] VALUES (172,'لا يبيع أحدكم على بيع أخيه، ولا يخطب على خطبة أخيه، حتى يأذن له','183','0','0');
INSERT INTO [t24831] VALUES (173,'نهى عن بيع الطعام إذا اشتراه أحدكم حتى يستوفيه فيقبضه','184','0','0');
INSERT INTO [t24831] VALUES (174,'الأخلاء ثلاثة: فأما خليل، فيقول: لك ما أعطيته، وما أمسكت فليس لك، ذلك ماله، وأما خليل،','185','0','0');
INSERT INTO [t24831] VALUES (175,'من صلى الغداة فهو في ذمة الله، فلا يطلبنكم الله بشيء من ذمته','186','0','0');
INSERT INTO [t24831] VALUES (176,'الرجل ليحاسب يوم القيامة حتى يسأل عن ولده، وزوجته فما رغبت فيما بعد','187','0','0');
INSERT INTO [t24831] VALUES (177,'هذا الأمر لا يزال فيكم وأنتم ولاته ما لم تحدثوا عملا ينزعه الله فيكم، فإذا فعلتم ذلك، سلط','188','0','0');
INSERT INTO [t24831] VALUES (178,'أورث امرأة أشيم الضبابي من دية زوجها فورثها عمر','189','0','0');
INSERT INTO [t24831] VALUES (179,'فقضى رسول الله صلى الله عليه وسلم في جنينها غرة عبد أو أمة','190','0','0');
INSERT INTO [t24831] VALUES (180,'لا إله إلا الله وحده، صدق وعده، ونصر عبده، وهزم الأحزاب وحده','191','0','0');
INSERT INTO [t24831] VALUES (181,'بعث رسول الله صلى الله عليه وسلم ثلاثمائة رجل وأمر عليهم أبا عبيدة بن الجراح، قال: وزودهم','192','0','0');
INSERT INTO [t24831] VALUES (182,'يجمع بين صلاة الظهر والعصر في السفر، إذا كان على ظهر سير، ويجمع بين المغرب','193','0','0');
INSERT INTO [t24831] VALUES (183,'يصلي وفي رجليه نعلان، فبزق فمسح بصاقه بنعله في التراب، والمسجد يومئذ فيه','194','0','0');
INSERT INTO [t24831] VALUES (184,'ما طلع النجم غداة قط وبقوم أو بقرية عاهة، إلا خفت أو ارتفعت عنهم فقلت: عمن هذا يا أبا','195','0','0');
INSERT INTO [t24831] VALUES (185,'إنهم ليبكون وإنه ليعذب وقد احترم ذلك.. .','196','0','0');
INSERT INTO [t24831] VALUES (186,'فقضى فيه رسول الله صلى الله عليه وسلم: أن على أهل الأموال الحفظ بالنهار وعلى أهل المواشي','197','0','0');
INSERT INTO [t24831] VALUES (187,'أتيت بدابة أشبه الدواب بالبغل، مضطرب الأذنين، يضع خطوة عند منتهى طرفه فذكر حديث','198','0','0');
INSERT INTO [t24831] VALUES (188,'أمر يحيى بن زكريا بخمس؛ ليعمل بهن، ويأمر بني إسرائيل أن يعملوا بهن','199','0','0');
INSERT INTO [t24831] VALUES (189,'تحشر نار من حضرموت تحشر الناس. قالوا: بم تأمرنا يا رسول الله؟ قال: عليكم','200','0','0');
INSERT INTO [t24831] VALUES (190,'من حفظ من أول سورة الكهف عشر آيات عصم من فتنة الدجال','201','0','0');
INSERT INTO [t24831] VALUES (191,'من شرب الخمر في الدنيا لم يشربها في الآخرة إلا أن يتوب','202','0','0');
INSERT INTO [t24831] VALUES (192,'إذا كان أحدكم على طعامه، فلا يعجلن عنه حتى يقضي حاجته منه، وإن أقيمت','203','0','0');
INSERT INTO [t24831] VALUES (193,'أمر بزكاة الفطر أن تؤدى قبل خروج الناس إلى الصلاة','204','0','0');
INSERT INTO [t24831] VALUES (194,'البيعان بالخيار من بيعهما ما لم يتفرقا، أو يكون بيعهما عن خيار، فإذا كان بيعهما عن خيار','205','0','0');
INSERT INTO [t24831] VALUES (195,'يعتكف العشر الأوائل من رمضان','206','0','0');
INSERT INTO [t24831] VALUES (196,'فأمر بهما رسول الله صلى الله عليه وسلم فرجما قريبا من حيث يوضع الجنائز عند المسجد فقال','207','0','0');
