INSERT INTO [t25783] VALUES (1,'الحمد لله الذي أطعمنا وسقانا وكفانا وآوانا، فكم ممن لا كافي له ولا','2','0','0');
INSERT INTO [t25783] VALUES (2,'عجب ربنا من رجلين: رجل ثار عن وطائه ولحافه من حبه وأهله إلى صلاته، فيقول لملائكته: انظروا','3','0','0');
INSERT INTO [t25783] VALUES (3,'ما هذا الوادي فقيل: وادي الأزرق، فقال: كأني أنظر إلى موسى بن عمران منهبطا له جؤار أو خؤار','4','0','0');
INSERT INTO [t25783] VALUES (4,'زينة الحج التلبية','5','0','0');
INSERT INTO [t25783] VALUES (5,'عليكم بالبياض من الثياب فليلبسها أحياؤكم وكفنوا فيها موتاكم فإنها من خير','6','0','0');
INSERT INTO [t25783] VALUES (6,'قدم النبي فدخل البيت فلبست ثيابي ثم انطلقت وقد خرج من البيت، وهو وأصحابه يستلمون ما بين','7','0','0');
INSERT INTO [t25783] VALUES (7,'لا تقطع يد في عذق ولا عام سنة','8','0','0');
INSERT INTO [t25783] VALUES (8,'كان رسول الله يصلي بنا فيقوم فيقرأ في الظهر والعصر في الركعتين الأوليين بأم الكتاب','9','0','0');
INSERT INTO [t25783] VALUES (9,'إن يوم القيامة عند عقر أو عفر الحوض أذود عنه الناس لأهل اليمن، والله إني لأضربهم بعصاي','10','0','0');
INSERT INTO [t25783] VALUES (10,'يرى فيه أباريق الفضة كعدد نجوم السماء','11','0','0');
INSERT INTO [t25783] VALUES (11,'مكتوب بين عيني الدجال كافر، يقرأه كل مؤمن','12','0','0');
INSERT INTO [t25783] VALUES (12,'إن الله بعثني رحمة للعالمين وهدى للعالمين، وأمرني ربي بمحق المعازف والمزامير والأوثان','13','0','0');
INSERT INTO [t25783] VALUES (13,'بينما أمير المؤمنين عثمان يخطب الناس، إذ قام إليه رجل، فقال له رجل من أصحابه: لا يمنعك','14','0','0');
INSERT INTO [t25783] VALUES (14,'دباغها طهورها','15','0','0');
INSERT INTO [t25783] VALUES (15,'وفضل الله المجاهدين على القاعدين أجرا عظيما درجات منه} [النساء: ٩٦] إلى آخر الآية، قال:','16','0','0');
INSERT INTO [t25783] VALUES (16,'من كذب علي ما لم أقل فليتبوأ بيتا من جهنم','17','0','0');
INSERT INTO [t25783] VALUES (17,'رجلان من أمتي يقوم أحدهم من الليل يعالج نفسه إلى الطهور وعليه عقد، فيتوضأ فإذا وضأ يديه','18','0','0');
INSERT INTO [t25783] VALUES (18,'كان رسول الله يطوف على تسع نسوة في ضحوة','19','0','0');
INSERT INTO [t25783] VALUES (19,'يا بني لو شهدتنا ونحن مع النبي عليه السلام إذ أصابتنا السماء لحسبت أن ريحنا ريح','20','0','0');
INSERT INTO [t25783] VALUES (20,'أن رسول الله كان يحتجم بثلاث: اثنتين على الأخدعين، وواحدة على الكاهل','21','0','0');
INSERT INTO [t25783] VALUES (21,'إن في أبوال الإبل وألبانها شفاء للذربة بطونهم','22','0','0');
INSERT INTO [t25783] VALUES (22,'إن من أبغض عباد الله من يقتدي بسيئة المؤمن ويترك حسنته','23','0','0');
INSERT INTO [t25783] VALUES (23,'أخبرني من رمقهم كلهم فما رأى أحدا منهم يصلي قبل','24','0','0');
INSERT INTO [t25783] VALUES (24,'أما بعد فإن عامل كورة كذا وكذا من الشام كتب إلي أن كره للمسلمين مباحته الماء وغلا عليهم','25','0','0');
INSERT INTO [t25783] VALUES (25,'كان رسول الله يأمرنا بصيام عاشوراء، ويحثنا عليه، ويتعاهدنا عنده، فلما فرض رمضان لم يأمرنا','26','0','0');
INSERT INTO [t25783] VALUES (26,'أمرنا رسول الله أن نتوضأ من لحوم الإبل ولا نتوضأ من لحوم الغنم، وأن نصلي في دمن الغنم ولا','27','0','0');
INSERT INTO [t25783] VALUES (27,'إني لأعرف رجلا لا تنقصه الفتنة شيئا، قال: قلنا: من هو؟ قال: محمد بن مسلمة الأنصاري قال:','28','0','0');
INSERT INTO [t25783] VALUES (28,'إذا عمل في الناس الخطيئة، فمن رضيها ممن غاب عنها فهو كمن شهدها، ومن كرهها ممن شهد فهو كمن','29','0','0');
INSERT INTO [t25783] VALUES (29,'لا إله إلا الله الحليم العظيم، لا إله إلا الله رب العرش الكريم، لا إله إلا الله رب العرش','30','0','0');
INSERT INTO [t25783] VALUES (30,'ما من عبد يصوم يوما في سبيل الله ابتغاء وجه الله إلا باعد الله بين وجهه والنار سبعين','31','0','0');
INSERT INTO [t25783] VALUES (31,'لا يجتمع في النار اجتماعا يضر مؤمن قتل كافرا ثم سدد بعده','32','0','0');
INSERT INTO [t25783] VALUES (32,'آخركم موتا في النار، فمات أبو هريرة، ثم مات أبو محذورة، ثم مات سمرة','33','0','0');
INSERT INTO [t25783] VALUES (33,'إن صدقت رؤياك زوجت امرأة ذات ولد فيأكلون كسبك. قال: ورأيت ثورا خرج من جحر، ثم ذهب يعود','34','0','0');
INSERT INTO [t25783] VALUES (34,'قل: ربي الله ثم استقم قال: قلت: يا رسول الله ما أكبر ما تخاف علي؟ قال: فأخذ بلسان نفسه ثم','35','0','0');
INSERT INTO [t25783] VALUES (35,'كان يتمثل بهذا البيت كثيرا:','36','0','0');
INSERT INTO [t25783] VALUES (36,'كيف أنت؟ وكيف أصبحت يا فلان؟ فيقول: بخير أحمد الله، فيقول له رسول الله: جعلك الله بخير،','37','0','0');
INSERT INTO [t25783] VALUES (37,'بخير، أحمد الله، قال عطاء: فذكرت ذلك لأبي البختري، فقال: إني آخذها إني','38','0','0');
INSERT INTO [t25783] VALUES (38,'إذا شتم أحدكم أخاه فلا يشتم عشيرته ولا أباه ولا أمه، ولكن ليقل إن كان يعلم ذلك: إنك لؤوم،','39','0','0');
INSERT INTO [t25783] VALUES (39,'كفارة وطهور فقال الأعرابي: بل حمى تفور على شيخ كبير تزيره القبور، فقام رسول','40','0','0');
INSERT INTO [t25783] VALUES (40,'إذا ابتلي العبد المسلم ببلاء في جسده قال الله للملك: اكتب له صالح عمله الذي كان يعمل، فإن','41','0','0');
INSERT INTO [t25783] VALUES (41,'هكذا ذهاب العلم، لقد دفن اليوم علم كثير','42','0','0');
INSERT INTO [t25783] VALUES (42,'اللهم اجعلنا ممن نريد بقولنا وعملنا وجهك والدار الآخرة وأثبنا عليه أجرا','43','0','0');
INSERT INTO [t25783] VALUES (43,'أن، أويسا القرني، كان إذا حدث وقع، حديثه من قلوبنا موقعا لا يقع لحديث','44','0','0');
INSERT INTO [t25783] VALUES (44,'الولاية بدعة والإرجاء بدعة والشهادة بدعة','45','0','0');
INSERT INTO [t25783] VALUES (45,'أنه كان يرى أن، هذه، الآية التي في سورة الأنعام في أهل الصلاة {إن الذين فرقوا دينهم','46','0','0');
INSERT INTO [t25783] VALUES (46,'ما نظر الله إلى الجنة إلا قال: طيبي لأهلك، قال: فزادت طيبا على ما كانت حتى يدخلها','47','0','0');
INSERT INTO [t25783] VALUES (47,'إن غرس جنة عدن بيده، ثم قال حين فرغ منها: {قد أفلح المؤمنون} [المؤمنون: ١] ، ثم أغلقت فلم','48','0','0');
INSERT INTO [t25783] VALUES (48,'إن القتل قد استحر يوم اليمامة بقراء القرآن وإني أخشى أن يستحر القتل','49','0','0');
INSERT INTO [t25783] VALUES (49,'كان أهل الكتاب يسدلون أشعارهم، وكان المشركون يفرقون روءسهم، قال: وكان رسول الله عليه','50','0','0');
INSERT INTO [t25783] VALUES (50,'رأيت حسان بن ثابت له ناصية قد سدلها بين عينيه','51','0','0');
INSERT INTO [t25783] VALUES (51,'ألا إني أوتيت الكتاب ومثله، ألا إني أوتيت القرآن ومثله، ألا يوشك رجل شبعان على أريكته','52','0','0');
INSERT INTO [t25783] VALUES (52,'أنهم قالوا في كل شيء فيه قمار فهو من الميسر، حتى لعب الصبيان بالجوز','53','0','0');
INSERT INTO [t25783] VALUES (53,'بينا نفر في البيت إذ طلبوا فقال الطالب: من في البيت؟ فقالوا: قولوا لهم: في البيت خنازير،','54','0','0');
INSERT INTO [t25783] VALUES (54,'ما من مسلم يتوفى له ثلاث من الولد لم يبلغوا الحنث، إلا تلقوه من أبواب الجنة الثمانية من','55','0','0');
INSERT INTO [t25783] VALUES (55,'ارحموا ترحموا، اغفروا يغفر الله لكم، ويل لأقماع القول، ويل للمصرين الذين يصرون على ما','56','0','0');
INSERT INTO [t25783] VALUES (56,'ركبت إلى عمر بن الخطاب لثلاث خصال أسأله عنها، لم يعملني شيئا غيرهن، فقدمت على عمر، وكان','57','0','0');
INSERT INTO [t25783] VALUES (57,'تدرون أي يوم ذاك؟ قالوا: الله ورسوله أعلم، قال: فإن ذلك اليوم يوم ينادى آدم، فيناديه ربه،','58','0','0');
INSERT INTO [t25783] VALUES (58,'كان إذا توجه لحاجة يحب أن يسمع: يا نجيح يا راشد يا مبارك','59','0','0');
INSERT INTO [t25783] VALUES (59,'كتب يزيد بن أبي مسلم إلى جابر بن زيد يسأله عن بدء الخلق، فقال: العرش، والماء، والقلم،','60','0','0');
