INSERT INTO [t25787] VALUES (116,'مسألة ٢٤ إذا وجد سماعه على كتاب ولم يذكر أنه سمعه جاز له أن يرويه','328','93','0');
INSERT INTO [t25787] VALUES (117,'مسألة ٢٥ إذا كتب إليه رجل بحديث جاز أن يرويه عنه فيقول أخبرني فلان بهذا مكاتبة','329','93','0');
INSERT INTO [t25787] VALUES (118,'مسألة ٢٦ تجوز رواية الحديث على المعنى إذا كان عالما بمعنى الحديث','330','93','0');
INSERT INTO [t25787] VALUES (119,'مسألة ٢٧ يرجح أحد الخبرين على الآخر بكثرة الرواة في أحد المذهبين ولا يرجح في المذهب الآخر وهو مذهب بعض الناس','332','93','0');
INSERT INTO [t25787] VALUES (120,'مسائل الإجماع','333','0','0');
INSERT INTO [t25787] VALUES (121,'مسألة ١ إجماع العلماء على حكم الحادثة حجة مقطوع بها','333','120','0');
INSERT INTO [t25787] VALUES (122,'مسألة ٢ إجماع أهل كل عصر حجة','343','120','0');
INSERT INTO [t25787] VALUES (123,'مسألة ٣ إذا قالت الصحابة قولا وخالفهم واحد أو اثنان لم يكن ذلك إجماعا','345','120','0');
INSERT INTO [t25787] VALUES (124,'مسألة ٤ إجماع أهل المدينة ليس بحجة','349','120','0');
INSERT INTO [t25787] VALUES (125,'مسألة ٥ اتفاق أهل بيت رسول الله صلى الله عليه وسلم ليس بحجة','352','120','0');
INSERT INTO [t25787] VALUES (126,'مسألة ٦ لا يعتبر في صحة الإجماع اتفاق العامة','355','120','0');
INSERT INTO [t25787] VALUES (127,'مسألة ٧ يصح انعقاد الإجماع عن القياس','356','120','0');
INSERT INTO [t25787] VALUES (128,'مسألة ٨ انقراض العصر ليس بشرط في صحة الإجماع في أصح الوجوه','359','120','0');
INSERT INTO [t25787] VALUES (129,'مسألة ٩ إذا اختلفت الصحابة في الحادثة على قولين ثم أجمع التابعون على أحدهما لم تصر المسألة إجماعا في قول عامة أصحابنا','362','120','0');
INSERT INTO [t25787] VALUES (130,'مسألة ١٠ إذا أدرك التابعي عصر الصحابة وهو من أهل الاجتهاد اعتبر رضاه في صحة الإجماع','368','120','0');
INSERT INTO [t25787] VALUES (131,'مسألة ١١ إذا اختلف الصحابة على قولين لم يجز للتابعين إحداث قول ثالث','371','120','0');
INSERT INTO [t25787] VALUES (132,'مسألة ١٢ إذا اختلفت الصحابة في مسألتين على قولين فذهبت طائفة منهما إلى حكم واحد وصرحت بالتسوية بينهما وذهبت الطائفة الأخرى إلى حكم آخر وصرحت بالتسوية بينهما فهل يجوز لمن بعدهما أن يأخذ بقول أحدهما في مسألة وبقول الآخر في المسألة الأخرى فيه وجهان','374','120','0');
INSERT INTO [t25787] VALUES (133,'مسألة ١٣ إذا قال الصحابي قولا وظهر ذلك في علماء الصحابة وانتشر ولم يعرف له مخالف كان ذلك إجماعا مقطوعا به','375','120','0');
INSERT INTO [t25787] VALUES (134,'مسألة ١٤ إذا قال الصحابي قولا ولم ينتشر لم يكن ذلك حجة ويقدم القياس عليه في قوله الجديد','379','120','0');
INSERT INTO [t25787] VALUES (135,'مسألة ١٥ إذا قال واحد من الصحابة قولا يخالف القياس لم يجعل ذلك توقيفا ويقدم القياس عليه','383','120','0');
INSERT INTO [t25787] VALUES (136,'مسائل التقليد','385','0','0');
INSERT INTO [t25787] VALUES (137,'مسألة ١ التقليد في أصول الديانات لا يجوز','385','136','0');
INSERT INTO [t25787] VALUES (138,'مسألة ٢ لا يجوز للعالم تقليد العالم','387','136','0');
INSERT INTO [t25787] VALUES (139,'مسألة ٣ إذا نزلت بالعالم نازلة وخاف فوت وقتها لم يجز له تقليد غيره','396','136','0');
INSERT INTO [t25787] VALUES (140,'مسألة ٤ يجوز للعامي تقليد العالم','398','136','0');
INSERT INTO [t25787] VALUES (141,'مسألة ٥ يجوز للعامي تقليد من شاء من العلماء','399','136','0');
INSERT INTO [t25787] VALUES (142,'مسائل القياس','400','0','0');
INSERT INTO [t25787] VALUES (143,'مسألة ١ القياس والاستدلال طريق لإثبات الأحكام في العقليات','400','142','0');
INSERT INTO [t25787] VALUES (144,'مسألة ٢ يجوز ورود التعبد بالقياس في الشرعيات','403','142','0');
INSERT INTO [t25787] VALUES (145,'مسألة ٣ القياس طريق الأحكام الشرعية','408','142','0');
INSERT INTO [t25787] VALUES (146,'مسألة ٤ إذا حكم صاحب الشرع بحكم في عين ونص على علته وجب إثبات الحكم في كل موضع وجدت فيه العلة وهو قول النظام والقاشاني والنهرواني وغيره من نفاة القياس وهو مذهب الكرخي','420','142','0');
INSERT INTO [t25787] VALUES (147,'مسألة ٥ يجوز إثبات الحدود والكفارات والمقدرات بالقياس','424','142','0');
INSERT INTO [t25787] VALUES (148,'مسألة ٦ يجوز ابتداء الأحكام بالقياس وإن لم يكن عليها نقل في الجملة','427','142','0');
INSERT INTO [t25787] VALUES (149,'مسألة ٧ يجوز إثبات الأسامي بالقياس في قول كثير من أصحابنا','428','142','0');
INSERT INTO [t25787] VALUES (150,'مسألة ٨ يجوز إثبات القياس على ما ثبت بالإجماع','431','142','0');
INSERT INTO [t25787] VALUES (151,'مسألة ٩ يجوز القياس على ما ورد به الخبر مخالفا للقياس وهو الذي يسميه أصحاب أبي حنيفة موضع الاستحسان','432','142','0');
INSERT INTO [t25787] VALUES (152,'مسألة ١٠ إذا ثبت الحكم في الفرع بالقياس على أصل جاز أن يجعل هذا الفرع أصلا لفرع آخر يقاس عليه بعلة أخرى في أحد الوجهين وهو قول أبي عبد الله البصري من أصحاب أبي حنيفة رحمه الله','434','142','0');
INSERT INTO [t25787] VALUES (153,'مسألة ١١ العلة الواقفة صحيحة','436','142','0');
INSERT INTO [t25787] VALUES (154,'مسألة ١٢ يجوز أن يجعل الاسم علة للحكم','438','142','0');
INSERT INTO [t25787] VALUES (155,'مسألة ١٣ يجوز أن يجعل نفي صفة علة الحكم','440','142','0');
INSERT INTO [t25787] VALUES (156,'مسألة ١٤ لا يصح رد الفرع إلا الأصل إلى بعلة مقتضية للحكم أو شبه يدل عليه','442','142','0');
INSERT INTO [t25787] VALUES (157,'مسألة ١٥ الطرد والجريان شرط في صحة العلة وليس بدليل على صحتها','444','142','0');
INSERT INTO [t25787] VALUES (158,'مسألة ١٦ إذا أثرت العلة في موضع من الأصول دل على صحتها وإن لم يكن ذلك أصل العلة','448','142','0');
INSERT INTO [t25787] VALUES (159,'مسألة ١٧ لا يجوز تخصيص العلة المستنبطة وتخصيصها نقض لها','450','142','0');
INSERT INTO [t25787] VALUES (160,'مسألة ١٨ التسوية بين الأصل والفرع في مسألة النقض لا يدفع النقض','454','142','0');
INSERT INTO [t25787] VALUES (161,'مسألة ١٩ لا يجوز للمستدل أن ينقض علة السائل بأصل نفسه','456','142','0');
INSERT INTO [t25787] VALUES (162,'مسألة ٢٠ لا يجوز للسائل أن يعارض المسؤول بعلة منتقضة على أصله','458','142','0');
INSERT INTO [t25787] VALUES (163,'مسألة ٢١ القلب معارضة صحيحة','459','142','0');
INSERT INTO [t25787] VALUES (164,'مسألة ٢٢ قلب التسوية صحيح وذلك مثل أن يقول المخالف في مسألة النية في الوضوء إنها طهارة بمائع فلم تفتقر إلى النية كإزالة النجاسة فيقول الشافعي رضي الله عنه أقلب علته فأقول طهارة بمائع فاستوى حكمها وحكم الجامد في النية كإزالة النجاسة','461','142','0');
INSERT INTO [t25787] VALUES (165,'مسألة ٢٣ جعل المعلول علة والعلة معلولا لا يمنع من صحة العلة وذلك مثل أن يقول الشافعي رضي الله عنه في ظهار الذمي من صح طلاقه صح ظهاره كالمسلم فيقول الحنفي المسلم لم يصح ظهاره لأنه يصح طلاقه بل صح طلاقه لأنه يصح ظهاره','463','142','0');
INSERT INTO [t25787] VALUES (166,'مسألة ٢٤ إذا تعارضت في الأصل علتان إحداهما تقتضي حمل الفرع عليه والأخرى لا تقتضي حمل الفرع عليه جاز القول بهما إذا لم يتنافيا','465','142','0');
INSERT INTO [t25787] VALUES (167,'مسألة ٢٥ إذا تعارضت علتان إحداهما ناقلة والأخرى مبقية على الأصل فالناقلة أولى','467','142','0');
INSERT INTO [t25787] VALUES (168,'مسألة ٢٦ إذا كانت إحدى العلتين تقتضي الحظر والأخرى تقتضي الإباحة فالتي تقتضي الحظر أولى في قول بعض أصحابنا وهو قول أبي الحسن الكرخي','468','142','0');
INSERT INTO [t25787] VALUES (169,'مسألة ٢٧ إذا كانت إحدى العلتين توجب الحد والأخرى تسقطه فهما سواء','469','142','0');
INSERT INTO [t25787] VALUES (170,'مسألة ٢٨ إذا كانت إحدى العلتين تقتضي العتق والأخرى لا تقتضيه فهما سواء','471','142','0');
INSERT INTO [t25787] VALUES (171,'مسألة ٢٩ إذا كانت إحدى العلتين أكثر فروعا من الأخرى كانت أكثرهما فروعا أولى','472','142','0');
INSERT INTO [t25787] VALUES (172,'مسألة ٣٠ إذا كانت إحدى العلتين أقل أوصافا من الأخرى فالقليلة الأوصاف أولى','473','142','0');
INSERT INTO [t25787] VALUES (173,'مسألة ٣١ إذا كانت إحدى العلتين منتزعة من أصلين والأخرى من أصل واحد قدمت من أصلين في قول بعض أصحابنا','474','142','0');
INSERT INTO [t25787] VALUES (174,'مسألة ٣٢ إذا كانت إحدى العلتين صفة ذاتية والأخرى حكمية فالحكمية أولى ومن أصحابنا من قال الذاتية أولى','475','142','0');
INSERT INTO [t25787] VALUES (175,'مسألة ٣٣ القول بالاستحسان باطل وهو ترك القياس لما يستحسن الإنسان من غير دليل','476','142','0');
INSERT INTO [t25787] VALUES (176,'مسائل الاجتهاد','480','0','0');
INSERT INTO [t25787] VALUES (177,'مسألة ١ الحق من قول المختلفين في أصول الديانات واحد وما عداه باطل','480','176','0');
INSERT INTO [t25787] VALUES (178,'مسألة ٢ الحق من قول المجتهدين في الفروع واحد وعلى ذلك دليل يجب طلبه وإصابته وما سواه باطل وهو قول أبي إسحاق الإسفراييني','482','176','0');
INSERT INTO [t25787] VALUES (179,'مسألة ٣ لا يجوز أن يتكافأ دليلان في الحادثة بل لا بد أن يكون لأحدهما مزية على الآخر وترجيح','494','176','0');
INSERT INTO [t25787] VALUES (180,'مسألة ٤ تخريج الشافعي رضي الله عنه المسألة على قولين جائز','495','176','0');
INSERT INTO [t25787] VALUES (181,'مسألة ٥ إذا ذكر في القديم قولا ثم ذكر في الجديد غيره فمذهبه هو الثاني والأول مرجوع عنه','498','176','0');
INSERT INTO [t25787] VALUES (182,'مسألة ٦ إذا نص الشافعي رحمه الله في مسألة على قولين ثم أعاد المسألة وذكر فيها أحد القولين أو فرع على أحد القولين كان ذلك اختيارا للقول المعاد والقول المفرع عليه في قول المزني رحمه الله','499','176','0');
INSERT INTO [t25787] VALUES (183,'مسألة ٧ إذا نص رحمه الله في مسألة على حكم ونص في غيرها على حكم آخر وأمكن الفصل بين المسألتين لم ينقل جواب إحداهما إلى الأخرى بل تحمل كل واحدة منهما على ظاهرها','500','176','0');
INSERT INTO [t25787] VALUES (184,'مسألة ٨ لا يجوز أن ينسب إلى الشافعي رضي الله عنه ما يخرج على قوله فيجعل قولا له','501','176','0');
INSERT INTO [t25787] VALUES (185,'مسألة ٩ إذا قال الشافعي رحمه الله في مسألة بقول ثم قال ولو قال قائل بكذا كان مذهبا لم يجز أن يجعل ذلك قولا له','502','176','0');
INSERT INTO [t25787] VALUES (186,'مسألة ١٠ يجوز الاجتهاد بحضرة النبي صلى الله عليه وسلم','503','176','0');
INSERT INTO [t25787] VALUES (187,'مسألة ١١ كان للنبي صلى الله عليه وسلم أن يجتهد في الحوادث ويحكم فيها بالاجتهاد وكذلك سائر الأنبياء عليهم السلام','505','176','0');
INSERT INTO [t25787] VALUES (188,'مسألة ١٢ يجوز الخطأ على رسول الله صلى الله عليه وسلم في اجتهاده إلا أنه لا يقرأ عليه بل ينبه عليه','508','176','0');
