INSERT INTO [t28453] VALUES (1,'أشد الناس عذابا يوم القيامة من أشركه الله في سلطانه فجار في حكمه» . قال الفضل بن سليمان:','22','0','0');
INSERT INTO [t28453] VALUES (2,'من تعرض للتهمة فلا يلومن من أساء به الظن، ومن كتم سره كان الخيار إليه، ومن أفشاه كان','32','0','0');
INSERT INTO [t28453] VALUES (3,'هذا والدي حقا ... وما كنت به عقا بذلت المال في رفق ... وما كنت به نزقا فلما خف من مالي ...','34','0','0');
INSERT INTO [t28453] VALUES (4,'أنت ومالك لأبيك»','34','0','0');
INSERT INTO [t28453] VALUES (5,'ما تصنع بعهدي يا عبد الرحمن؟ قال: أتخذه إماما ولا أعصيه. قال: اردد علي عهدي. قال: تعزلني','34','0','0');
INSERT INTO [t28453] VALUES (6,'خرجت مع عبد الله بن أبي سرح في غزوة إفريقية، فلما دنا من جرجير ملك الغرب، وهو رجل من','36','0','0');
INSERT INTO [t28453] VALUES (7,'قتل علي بن أبي طالب عليه السلام، فقالت عائشة: فإن تك ناعيا فلقد نعاه ... نعي ليس في فيه','42','0','0');
INSERT INTO [t28453] VALUES (8,'حضر قوم من قريش مجلس معاوية بن أبي سفيان، فيهم عمرو بن العاص، وعبد الله بن صفوان بن أمية','53','0','0');
INSERT INTO [t28453] VALUES (9,'ألست من قتلة عثمان؟ ، قال: لا، ولكنني ممن حضره فلم ينصره. قال: وما منعك من نصره؟ قال: لم','53','0','0');
INSERT INTO [t28453] VALUES (10,'فأمر بإخراج جواريه، فقال لهن: تغنين لمعبد؟ ففعلن. فقال ابن عمر: هذا الحداء. فقال لهن:','58','0','0');
INSERT INTO [t28453] VALUES (11,'لا تنتفين من ولد نكحت أمه، واعلم أن كل أمانة مؤداة، وأن الرغائب في ركعتي','59','0','0');
INSERT INTO [t28453] VALUES (12,'«من كثر ماله اشتد حسابه، ومن كثر تبعه كثر شياطينه، وأن العبد كلما ازداد من السلطان قربا،','59','0','0');
INSERT INTO [t28453] VALUES (13,'لما ادعى معاوية زيادا، وآثر عمرو بن العاص على أهل بيته، وقربهما دونهم جزع بنو أمية من ذلك','61','0','0');
INSERT INTO [t28453] VALUES (14,'دفن فاطمة بنت رسول الله صلى الله عليه، قام علي على القبر، وأنشأ يقول: لكل اجتماع من','70','0','0');
INSERT INTO [t28453] VALUES (15,'رحم الله مالكا وما ملك، لو كان من جبل لكان فندا، أو من حجر لكان صلدا، على مثل مالك فلتبك','70','0','0');
INSERT INTO [t28453] VALUES (16,'أتي عمر ببرود، فقال للذي أتاه بها: أخرج لي خيرها وشرها، ثم قال: علي بالحسن، فلما أتاه دفع','71','0','0');
INSERT INTO [t28453] VALUES (17,'وليناكم قريبا، وعدلنا عليكم خير من خطبنا فيكم، وإن أعش يأتكم الكلام على جهته، إن شاء الله "','73','0','0');
INSERT INTO [t28453] VALUES (18,'رددت أمر المسلمين إليك فدبرهم برأيك، واتق الله الذي إليه معادك، فقد أخرجت من رقبتي ذلك.','75','0','0');
INSERT INTO [t28453] VALUES (19,'ما نطعمك يا ابن حسان؟ قال: سمكا. قال: فما نسقيك؟ قال: سويقا. فقال نعيم بن عمرو بن الأهتم،','82','0','0');
INSERT INTO [t28453] VALUES (20,'عبد الرحمن بن حسان كان يشبب بابنة معاوية، ويذكرها في شعره، فقال الناس لمعاوية: لو جعلته','83','0','0');
INSERT INTO [t28453] VALUES (21,'إذا أتي بطعام: أطعام يد أم طعام يدين؟ قال: فإذا قال له: طعام يدين. لم يأكل، وهو الشواء.','91','0','0');
INSERT INTO [t28453] VALUES (22,'" فلما أهذرا في التهاجي وأفحشا، كتب معاوية بن أبي سفيان، وهو الخليفة يومئذ إلى سعيد بن','93','0','0');
INSERT INTO [t28453] VALUES (23,'أهدى المقوقس صاحب الإسكندرية إلى رسول الله صلى الله عليه وآله وسلم مارية ابنة شمعون','100','0','0');
INSERT INTO [t28453] VALUES (24,'لما كانت فتنة علي ومعاوية كلم أهل مكة عثمان بن شيبة من بني عبد الدار أن يتولى أمرهم حتى','107','0','0');
INSERT INTO [t28453] VALUES (25,'أبا مسلم الخولاني، وكان رجلا من عباد أهل الشام، قام إلى معاوية، فقال: يا معاوية، على ما','109','0','0');
INSERT INTO [t28453] VALUES (26,'أوصي من آمن بالله وصدقني بولاية علي بن أبي طالب، من تولاه فقد تولاني، ومن تولاني فقد تولى','116','0','0');
INSERT INTO [t28453] VALUES (27,'خذلني الناس، حتى ولدي وأهلي، فلم يبق معي إلا اليسير ممن ليس عنده من الدفع أكثر من صبر','117','0','0');
INSERT INTO [t28453] VALUES (28,'ابننا عندك، فامنن علينا، وأحسن إلينا في فدائه، فإنا سنرفع لك في الفداء. قال: «من هو» ؟ ،','119','0','0');
INSERT INTO [t28453] VALUES (29,'زوج رسول الله صلى الله عليه وآله زيد بن حارثة مولاته أم أيمن، فولدت له أسامة بن زيد وبه','120','0','0');
INSERT INTO [t28453] VALUES (30,'لما هاجر زيد بن حارثة إلى المدينة نزل على كلثوم بن الهدم "، قال الواقدي: وأما عاصم بن عمرو','120','0','0');
INSERT INTO [t28453] VALUES (31,'اللهم بعلمك الغيب، وبقدرتك على الخلق، أحيني ما علمت الحياة خيرا لي، وتوفني ما كانت الوفاة','120','0','0');
INSERT INTO [t28453] VALUES (32,'أيتها العصابة التي أخرجتها عادة المراء والضلالة، وصدف بها عن الحق الهوى والزيغ، إني نذير','122','0','0');
INSERT INTO [t28453] VALUES (33,'دخل على الحجاج، فقال له: يا خبثة، شيخا جوالا في الفتن، مع أبي تراب مرة، ومع ابن الزبير','123','0','0');
INSERT INTO [t28453] VALUES (34,'الناس قد رفعوا أعينهم ومدوا أعناقهم إلى بني عبد المطلب، فلو نظرنا إلى رجل منهم فيه لوثة','126','0','0');
INSERT INTO [t28453] VALUES (35,'لا قود إلا بالسيف» ، ونهى عن المثلة. ثم قال لشريك: أرأيت لو رماه بسهم فلم يقتله ثم ثنى فلم','127','0','0');
INSERT INTO [t28453] VALUES (36,'إن لم تجد من صحبة الرجال بدا، فعليك بمن إن صحبته زانك، وإن خفضت له صانك، وإن وعدك لم','129','0','0');
INSERT INTO [t28453] VALUES (37,'أخبرنا عنا وعن بني عبد شمس.','129','0','0');
INSERT INTO [t28453] VALUES (38,'ألا تخاف أن تؤتى من قبل ظهرك؟ فيقول: إذا أمكنت عدوي من ظهري فلا أبقى الله عليه إن أبقى','130','0','0');
INSERT INTO [t28453] VALUES (39,'مصر قد افتتحت، ألا وإن محمد بن أبي بكر أصيب رحمه الله، وعند الله نحتسبه، أما والله إن كان','131','0','0');
INSERT INTO [t28453] VALUES (40,'فخما مفخما، يتلألأ وجهه تلألؤ القمر ليلة البدر، أطول من المربوع، وأقصر من المشذب، عظيم','134','0','0');
INSERT INTO [t28453] VALUES (41,'الخط، فقال: «علم أوتيه نبي، فمن وافق علمه علم ذلك النبي فقد علم، ومن لم يصبه فقد','138','0','0');
INSERT INTO [t28453] VALUES (42,'اشترك ثلاثة في ظهر امرأة، فولدت، فجاءت بغلام، فتنازعه القوم كلهم يدعيه، فدعا عمر بن','138','0','0');
INSERT INTO [t28453] VALUES (43,'ثلاثة نفر تقدموا إليه، وقد اشتركوا في ظهر امرأة، فقال: أنتم شركاء متشاكسون، وقد جاءت بولد','138','0','0');
INSERT INTO [t28453] VALUES (44,'طعامان وشرابان في إناء واحد، لا حاجة لي به، وإن كنت لا أحرمه، ولكن أحب أن يراني الله','142','0','0');
INSERT INTO [t28453] VALUES (45,'توشكون أن يغدو أحدكم في حلة، ويروح في أخرى، وأن يغدا على أحدكم بجفنة، ويراح عليه بأخرى،','144','0','0');
INSERT INTO [t28453] VALUES (46,'ما لك يا أبا الحسن؟ هل لك من حاجة» ؟ فسكت، حتى أعاد ذلك ثلاث مرات، ثم قال: «فلعلك تريد','145','0','0');
INSERT INTO [t28453] VALUES (47,'«يأتي الأنصار في دورهم فيدعو لهم بالبركة، فيجتمعون إليه، فيذكرهم ويحذرهم وينذرهم، ويأتونه','145','0','0');
INSERT INTO [t28453] VALUES (48,'" دخل عبد الله بن الزبير على معاوية بن أبي سفيان، وعنده جماعة، فيهم مروان بن الحكم، وسعيد','150','0','0');
INSERT INTO [t28453] VALUES (49,'إذا زنت أمة أحدكم فليجلدها، ولا يثرب عليها ثلاثا، فإن زنت الرابعة فليبعها، ولو بضفير من','150','0','0');
INSERT INTO [t28453] VALUES (50,'مروان على هذا المنبر أمر للناس بنصف عطائهم. وقال: إن المال قصر، وقد أمرت لكم بالنصف','150','0','0');
INSERT INTO [t28453] VALUES (51,'أجود الناس حيا وميتا حاتم. قال معاوية: فكيف ذلك؟ فوالله إن الرجل من قريش ليعطي في مجلس','160','0','0');
INSERT INTO [t28453] VALUES (52,'ماوية بنت عفزر كانت ملكة، وكانت تتزوج من أرادت، وأنها بعثت غلمانا لها، وأمرتهم أن يأتوها','163','0','0');
INSERT INTO [t28453] VALUES (53,'أنا لك كأبي زرع لأم زرع» . قالت: يا رسول الله، وما حديث أبي زرع وأم زرع؟ فقال رسول الله صلى','177','0','0');
INSERT INTO [t28453] VALUES (54,'دخل عمرو بن معد يكرب الزبيدي على عمر بن الخطاب، وعنده الربيع بن زياد، وشريك بن الأعور','184','0','0');
INSERT INTO [t28453] VALUES (55,'الله أكبر، أحمد رسول الله صلى الله عليه وآله وسلم، في الكتاب الأول، صدق صدق، ثم قال:','186','0','0');
INSERT INTO [t28453] VALUES (56,'ما أسكر كثيره فقليله حرام»','189','0','0');
INSERT INTO [t28453] VALUES (57,'كل مسكر حرام، وكل مسكر خمر» حدثني أخي هارون بن أبي بكر، عن محمد بن المغيرة بن إسماعيل','189','0','0');
INSERT INTO [t28453] VALUES (58,'منكم لمن سبقني فرأى قبلي، ورأيت بعده، والله ما رأيت خصاصة إلا ألصقها رسول الله صلى الله','190','0','0');
INSERT INTO [t28453] VALUES (59,'هززت ذوائب الرجال إليك، إذ لم أجد معولا إلا عليك، وما زلت أستدل المعروف عليك، وأجعل','193','0','0');
INSERT INTO [t28453] VALUES (60,'«لا يبعدن ابن هند إن كانت فيه لمخارج لا تجدها في أحد بعده، والله إن كنا لنفرقه فيتفارق','198','0','0');
INSERT INTO [t28453] VALUES (61,'" الحمد لله الذي له الخلق والأمر، وملك الدنيا والآخرة، يؤتي الملك من يشاء، وينزع الملك ممن','206','0','0');
INSERT INTO [t28453] VALUES (62,'الأئمة من قريش» ، ما أنكرنا إمرة الأنصار، ولكانوا لها أهلا، ولكنه قول لا شك فيه ولا خيار،','223','0','0');
INSERT INTO [t28453] VALUES (63,'النعمة إذا حدثت حدث لها حساد حسبها وأعداء قدرها، وإن الله لم يحدث لنا نعما ليحدث لها حساد','232','0','0');
INSERT INTO [t28453] VALUES (64,'أتي عمر بجوهر كسرى، وضع في المسجد، فطلعت عليه الشمس فصار كالجمر، فقال لخازن بيت المال:','238','0','0');
INSERT INTO [t28453] VALUES (65,'جاء رجل إلى علي عليه السلام يستشفع به إلى عثمان، فقال: «حمال الخطايا! لا والله لا أعود','239','0','0');
INSERT INTO [t28453] VALUES (66,'يخطب، فأكب الناس حوله، فقال: «اجلسوا يا أعداء الله» ! فصاح به طلحة: «إنهم ليسوا أعداء الله','239','0','0');
INSERT INTO [t28453] VALUES (67,'أما لكتاب الله ناشد غيرك» ! فجلس، ثم قام آخر فقال مثل مقالته، فقال: «اجلس» ، فأبى أن يجلس،','239','0','0');
INSERT INTO [t28453] VALUES (68,'زنباع بن روح بن سلامة الجذامي يعشر من يمر به للحارث بن أبي شمر، قال: فعمدنا إلى ما معنا','245','0','0');
INSERT INTO [t28453] VALUES (69,'كيف علمك بمضر» ؟ قال: يا رسول الله، أنا أعلم الناس بهم. تميم هامتها وكاهلها الشديد الذي','246','0','0');
INSERT INTO [t28453] VALUES (70,'أجيزوا بطن عرفة، فإنما هم إذ أسلموا إخوانكم» . قال: " فعلمنا النبي صلى الله عليه وسلم','246','0','0');
INSERT INTO [t28453] VALUES (71,'أذن عمر للناس فدخل عمرو بن براقة، وكان شيخا كبيرا يعرج، فأنشد أبياتا، يقول فيها: ما إن','247','0','0');
INSERT INTO [t28453] VALUES (72,'يعطي المهاجرين والأنصار، فقال له: فرات، من الذي يقول: " الفقر يزري بالفتى في قومه ...','247','0','0');
INSERT INTO [t28453] VALUES (73,'أشعر أهل الجاهلية زهير بن أبي سلمى، وكان أشعر أهل الإسلام ابنه كعب، ومعن بن أوس " عن','248','0','0');
INSERT INTO [t28453] VALUES (74,'قل شعرا تقتضيه الساعة وأنا أنظر إليك» . ثم أبده بصره، فانبعث عبد الله بن رواحة، يقول: إني','250','0','0');
INSERT INTO [t28453] VALUES (75,'لا تزيدوا في مهور النساء على أربعين أوقية، ولو كانت بنت ذي الغصة، يعني يزيد بن الحصين','251','0','0');
INSERT INTO [t28453] VALUES (76,'أتت امرأة إلى عمر بن الخطاب رضي الله عنه، فقالت: يا أمير المؤمنين إن زوجي يصوم النهار،','251','0','0');
