INSERT INTO [t29677] VALUES (251,'الحجة الأولى: أن الرجوع إلى الكتاب يفيد ما يفيده الحفظ من ظن صحة الدليل .. فإن قلت: إن الحفظ يفيد العلم فيأمن الخطأ ... قلت: هذا ممنوع لوجهين:','411','1','0');
INSERT INTO [t29677] VALUES (252,'أحدهما: الحافظ لأدلة الاجتهاد وإن علم أنه حافظ لها فثبوتها عن النبي - صلى الله عليه وسلم - مظنون','411','1','0');
