INSERT INTO [t29677] VALUES (265,'الحجة السابعة: في الآية في الدين والشهادة .. فبين أن الكتابة هى الغاية القصوى في الاحتراز من الشك والبعد من الريب','421','1','0');
INSERT INTO [t29677] VALUES (266,'الحجة الثامنة: أن الجماهير قد أجازوا رواية لفظ النبي - صلى الله عليه وسلم - بالمعنى','422','1','0');
INSERT INTO [t29677] VALUES (267,'الحجة التاسعة: أجمعت جماهير العترة على اختيار الإمام في الاجتهاد .. فلم نعلم أحدا منهم اختبر أحدا من الأئمة في الحفظ','423','1','0');
INSERT INTO [t29677] VALUES (268,'الحجة العاشرة: ثبت عن رسول الله - صلى الله عليه وسلم - أنه قال: (أن الله فرض فرائض فلا تضيعوها .. ) الحديث','423','1','0');
INSERT INTO [t29677] VALUES (269,'الحجة الحادية عشر: نقل الإجماع على أن حفظ شيء من القرآن واجب، وعلى أن من حفظ الفاتحة مع البسملة، وسورة أخرى معها، فقد أدى فرض الحفظ وأنه لا يلزمه حفظ أكثر من ذلك','425','1','0');
INSERT INTO [t29677] VALUES (270,'الرد على السيد حيث استدل ببيت من الشعر على نقض قول الإمام الغزالي أن المجتهد يكفيه حفظ متن مختصر في كل فن، ولا يلزمه حفظه عن ظهر قلبه بل يكفيه أن يعرفه نظرا','426','1','0');
INSERT INTO [t29677] VALUES (271,'الجزء الثاني','428','0','0');
INSERT INTO [t29677] VALUES (272,'قال السيد: ويروى عن الشافعي أنه قال: لا علم إلا ما يدخل به الحمام','432','271','0');
INSERT INTO [t29677] VALUES (273,'أقول: هذه الحجة الثانية من حجج السيد في هذه المسألة والجواب عليه من وجوه:','432','271','0');
INSERT INTO [t29677] VALUES (274,'الأول: من أين صح لك هذا عن الشافعي- رضي الله تعالى عنه -','432','271','0');
