INSERT INTO [t29677] VALUES (277,'قال السيد: وكيف يكون حال هذا المجتهد الذي يحتاج إلى كتبه في عيون المسائل إذا اغتصبت هل يبطل اجتهاده','435','271','0');
INSERT INTO [t29677] VALUES (278,'أقول: هذه الحجة الثالثة من حجج السيد وما هي إلا قعقعة في العبارة وبيان ما ذكره يظهر بالكلام في عشرة أنظار','435','271','0');
INSERT INTO [t29677] VALUES (279,'النظر الأول من قبيل المعارضات وهو أن نقول: إيراد مثل هذا الكلام ممكن في المجتهد والمقلد','435','271','0');
INSERT INTO [t29677] VALUES (280,'النظر الثاني من قبيل المعارضة أيضا، وذلك أن الأمة أجمعت على أنه يجب على المجتهد أن يرجع في طلب الأدلة عند حدوث الحادث إلى من في بلده من العلماء','436','271','0');
INSERT INTO [t29677] VALUES (281,'النظر الثالث أيضا من قبيل المعارضة، ذلك أنه قد ثبت أن العالم يسمى في الحقيقة العرفية عالما ومجتهدا في حال نومه وغفلته','437','271','0');
INSERT INTO [t29677] VALUES (282,'النظر الرابع من قبيل المعارضة أيضا، وذلك أن الأمة أجمعت على جوز نسيان المجتهد','438','271','0');
