INSERT INTO [t29677] VALUES (284,'النظر السادس: أن السيد قد حام على اختيار مذهب الأشعرية في أنه لا يشتق اسم الفاعل من شيء إلا وذلك الشيء قائم بالفاعل','439','271','0');
