INSERT INTO [t29677] VALUES (295,'البحث الثاك سلمنا أنه تجريح صحيح، لكنه مخالف لنص الغزالي','450','271','0');
INSERT INTO [t29677] VALUES (296,'البحث الرابع: احتج السيد على أن الغزالي يعسر الاجتهاد','451','271','0');
