INSERT INTO [t29677] VALUES (312,'وثانيهما أنا نبين أن افتراقنا في معرفة العربية ليس يقتضي تعسير الاجتهاد على الإطلاق لوجهين','458','271','0');
INSERT INTO [t29677] VALUES (313,'أحدهما: أن أكثر آيات الأحكام وأحاديثه لا تحتاج إلى قراءة العربية في فهم معناها، والدليل على ذلك حجتان','458','271','0');
