INSERT INTO [t29677] VALUES (317,'أقول: الجواب على ما ذكره من تجهيل هذا الصاحب الجليل من وجوه','462','271','0');
INSERT INTO [t29677] VALUES (318,'الوجه الأول: أنا قد بينا أنه لا طريق لنا إلى العلم بجهل الصحابي إلا إقراره بذلك','462','271','0');
INSERT INTO [t29677] VALUES (319,'الوجه الثاني: أن الظاهر خلاف ما ذكر','463','271','0');
INSERT INTO [t29677] VALUES (320,'الوجه الثالث: أنه ممن نقلت عنه الفتيا من الصحابة - رضي الله تعالى عنهم -','464','271','0');
INSERT INTO [t29677] VALUES (321,'الوجه الرابع: معارضة الغزالي بقول من هو أرجح منه في ذلك','464','271','0');
INSERT INTO [t29677] VALUES (322,'الوجه الخامس: أن كلام السيد إنما هو في تعسير الاجتهاد ويلتحق بهذا فائدتان','466','271','0');
INSERT INTO [t29677] VALUES (323,'الفائدة الأولى: أن أبا هريرة ثقة مقبول لا مطعن في قبول روايته عند أهل التحقيق','466','271','0');
