INSERT INTO [t29677] VALUES (327,'الوجه الثالث: أنه لا خلاف أن طريقة أبى هريرة كانت مستقيمة','476','271','0');
INSERT INTO [t29677] VALUES (328,'الوجه الرابع: أن قواعد العلم المتفق عليها ما يقتضي أنه لا يقبل المتعارضان معا','477','271','0');
INSERT INTO [t29677] VALUES (329,'الوجه الخامس: أن أبا هريرة قد روى مناقب علي - رضي الله تعالى عنه - في الصحاح','478','271','0');
