INSERT INTO [t29677] VALUES (331,'ويلحق بذلك فائدة تعلق بتمام الذب عن أبي هريرة - رضي الله عنه - وذلك أن بعض من يتهمه في الحديث احتج على تهمته أن هذا يدل على أنه يتعمد الكذب أو يتهم بذلك والجواب من وجوه','489','271','0');
INSERT INTO [t29677] VALUES (332,'الوجه الأول: أنه لو صح التشكيك في صدق مثل أبي هريرة - رضي الله عنه -','490','271','0');
