INSERT INTO [t29677] VALUES (340,'ورابعها: أنه قد نقل عن ابن عباس - رضي الله تعالى عنه -','508','271','0');
INSERT INTO [t29677] VALUES (341,'قال السيد: قال الغزالي: وتردد الشافعي في كون الحسن البصري مجتهدا، وزعم الغزالي أن أبا حنيفة لم يكن مجتهدا','508','271','0');
INSERT INTO [t29677] VALUES (342,'أقول: قد شرع السيد يشكك في علم هذين الإمامين الكبيرين، والعلمين الشهيرين - رضي الله تعالى عنهما - والجواب عليه، وأما الاستدلال على ذلك مسالك','509','271','0');
INSERT INTO [t29677] VALUES (343,'المسلك الأول: أنه قد ثبت بالتواتر فضلهما','509','271','0');
INSERT INTO [t29677] VALUES (344,'الحسلك الثاني: أنه رواية العلماء لمذاهبهما','510','271','0');
INSERT INTO [t29677] VALUES (345,'المسلك الثالث: أن نقول الإجماع منعقد على اجتهادهما','510','271','0');
INSERT INTO [t29677] VALUES (346,'المسلك الرابع: أنا قد قدمنا نصوص كثير من الأئمة على أن أحد الطرق الدالة على اجتهاد العالم هي انتصابه للفتيا، ورجوع المسلمين إليه من غير نكير','510','271','0');
INSERT INTO [t29677] VALUES (347,'قوله: وأبو حنيفة هو الإمام الأعظم الذي طبق مذهبه أكثر العالم','511','271','0');
INSERT INTO [t29677] VALUES (348,'قوله: وأما ما قدح به على الإمام أبي حنيفة من عدم العلم بالعربية','513','271','0');
INSERT INTO [t29677] VALUES (349,'قوله: وأما قوله: بأبا قبيس، فالجواب عنه من وجوه','514','271','0');
INSERT INTO [t29677] VALUES (350,'الأول: أن هذا يحتاج إلى طريق صحيحة','514','271','0');
INSERT INTO [t29677] VALUES (351,'الثاني: إن ثبت بطريق صحيحة، فإنه لم يصح، ولم يشتهر كصحة الفتيا عنه','514','271','0');
INSERT INTO [t29677] VALUES (352,'الثالث: أنا لو قدرنا أن ذلك صح عنه بطريق معلومة لم يقدح به','515','271','0');
INSERT INTO [t29677] VALUES (353,'الرابع: سلمنا أن هذا لحن لا وجه له، فإن كثيرا ممن يعرف العربية قد يتعمد اللحن','515','271','0');
INSERT INTO [t29677] VALUES (354,'قوله: وأما قدحه عليه بالرواية عن المضعفين، وقوله: إن ذلك ليس إلا لقلة معرفته بالحديث، فهو وهم فاحش، ولا يتكلم بهذا منصف، والجواب عن ذلك يتبين بنكر محامل','515','271','0');
INSERT INTO [t29677] VALUES (355,'المحمل الأول: أنه قد علم من مذهب أبي حنيفة - رضي الله عنه - أنه يقبل المجهول، ولا شك أنه يقبله حيث لا يعارضه حديث الثقة','515','271','0');
INSERT INTO [t29677] VALUES (356,'المحمل الثاني: أن يكون ضعف أولئك الرواة الذين يروي عنهم مختلفا فيه، وهو يعلم وجه التضعيف، وحجة المضعف، وهذا شيء مشهور، وقد ذكر أهل العلم أولئك الضعفاء','518','271','0');
INSERT INTO [t29677] VALUES (357,'المحمل الثالث: أن يكون إنما روى عن أولئك، وذكر حديثهم على المتابعة والاستشهاد','520','271','0');
INSERT INTO [t29677] VALUES (358,'المحمل الرابع: أن يكون ذلك على طريقة الحفاظ الكبار من أئمة الأثر','522','271','0');
INSERT INTO [t29677] VALUES (359,'قال السيد: وقال الرازي: إن لم نقل بجواز تقليد الميت، أشكل الأمر، لأنه ليس في زماننا مجتهد، وذكروا أن الغزالى لم يبلغ مرتبة الاجتهاد','524','271','0');
INSERT INTO [t29677] VALUES (360,'أقول: كلام السيد هذا يشتمل على الاستدلال على صعوبة الاجتهاد بعدم اجتهاد الرازي والغزالي، والجواب عليه من وجوه','524','271','0');
INSERT INTO [t29677] VALUES (361,'الوجه الأول: إلزام السيد ما يقتضيه كلامه، لا يلزم من جهلهما أن يكون غيرهما جاهلا','524','271','0');
INSERT INTO [t29677] VALUES (362,'الثاني: أنه لا ملازمة بين دعواهما لعدم الاجتهاد وتعسر الاجتهاد','524','271','0');
INSERT INTO [t29677] VALUES (363,'الثالث: أن السيد ذكر في كتابه أنهما غير محققين ولا موفقين بهذا اللفظ، ثم احتج على تعسر الاجتهاد بجهلهما','525','271','0');
INSERT INTO [t29677] VALUES (364,'الرابع -وهو التحقيق-: وهو أن نقول: لا ريب عند كل منصف ممن له معرفة بتصانيف هذين الرجلين أنهما من أهل التمكن من الاجتهاد','525','271','0');
INSERT INTO [t29677] VALUES (365,'ويلتحق بهذا فائدة: وهي أنه لا شك أن هذين الرجلين من كبار أهل العلوم العقلية والنقلية النظرية، ورؤوس الطائفة الأشعرية','527','271','0');
INSERT INTO [t29677] VALUES (366,'قال السيد: وذكر بعض فقهاء الشافعية تعسر الاجتهاد','529','271','0');
INSERT INTO [t29677] VALUES (367,'أقول: هذه الروايات عن بعض أصحاب الشافعي قد جعلها السيد لكلامه تماما، ولاحتجاجه ختاما، ولا بد من التعرض لوجوه يكشف النقاب عن وجه الصواب وإن كانت هذه الشبهة مما لا تحتمل الجواب','529','271','0');
INSERT INTO [t29677] VALUES (368,'الوجه الأول: أن الشافعي - رضي الله تعالى عنه - من قدماء العلماء، فلنذكر بعدهم معرفتين','531','271','0');
INSERT INTO [t29677] VALUES (369,'المعرفة الأولى: ذكر جماعة من علماء سادات العترة','533','271','0');
INSERT INTO [t29677] VALUES (370,'المعرفة الثانية: في ذكر بعض من كان بعد المتقدمين من أصحاب الشافعي','547','271','0');
INSERT INTO [t29677] VALUES (371,'الوجه الثاني: من الجواب على توعير السيد لمسالك العلم','552','271','0');
INSERT INTO [t29677] VALUES (372,'الوجه الثالث: إذا ادعى جماعة من أصحاب الشافعي جهل الأمة','554','271','0');
INSERT INTO [t29677] VALUES (373,'الوجه الرابع: الدليل قائم على غلط من قال بذلك ووهمه','554','271','0');
INSERT INTO [t29677] VALUES (374,'الوجه الخامس: قد ثبت أن الأمة معصومة، وأن إجماعها حجة','555','271','0');
INSERT INTO [t29677] VALUES (375,'الوجه السادس: أنا نعلم أن المدعي لجهل الأمة متهور مجازف','555','271','0');
INSERT INTO [t29677] VALUES (376,'الوجه السابع: أن في الحديث الصحيح أن رسول الله - صلى الله عليه وسلم - سئل عن الكبر، فقال: هو بطر الحق، وغمص الناس','555','271','0');
INSERT INTO [t29677] VALUES (377,'قال السيد: المسألة الثالثة في رواية كفار التأويل وفساقه','557','271','0');
INSERT INTO [t29677] VALUES (378,'أقول: الكلام في هذه المسألة يتم في فصلين، أحدهما: في تتبع كلام السيد، والثاني: في نكر الأدلة','557','271','0');
INSERT INTO [t29677] VALUES (379,'الفصل الأول: واعلم أنه يرد على كلامه إشكالات','558','271','0');
INSERT INTO [t29677] VALUES (380,'الإشكال الأول: أن السيد قال: قد قدمنا أن قاضي القضاة روى الإجماع على رد روايتهم','558','271','0');
INSERT INTO [t29677] VALUES (381,'الإشكال الثاني: أن السيد قد أثبت قاعدة كبيرة: وهي أن كل من كذب متأولا، فهو غير مقبول وقاضي القضاة على أصل السيد من جملة من كذب متأولا','558','271','0');
INSERT INTO [t29677] VALUES (382,'الإشكال الثالث: أن السيد قال في حق ابن الصلاح لما اعتقد أنه روى الأجماع على صحة صحيحي البخاري ومسلم','558','271','0');
INSERT INTO [t29677] VALUES (383,'الإشكال الرابع: أن السيد روى هذا عن أبي الحسين','558','271','0');
INSERT INTO [t29677] VALUES (384,'الإشكال الخامس: أنه قال: إن الرواية عن المتأولين ركون إليهم','558','271','0');
INSERT INTO [t29677] VALUES (385,'الإشكال السادس: المعتزلة أنفسهم من جملة الذين ظلموا لخلافهم','558','271','0');
INSERT INTO [t29677] VALUES (386,'الأشكال السابع: أنك قد حكيت عن قاضي القضاة أنه يقبل فساق التأويل','561','271','0');
INSERT INTO [t29677] VALUES (387,'الإشكال الثامن: أنك ساويت بين المتعمد للمعصية والمتأول','561','271','0');
INSERT INTO [t29677] VALUES (388,'الإشكال التاسع: أن السيد قد منع من الرواية عن العلماء إلا بعد تحصيل إسناد صحيح','561','271','0');
INSERT INTO [t29677] VALUES (389,'الإشكال العاشر: أن رواية القاضي معارضة بأرجح منها','562','271','0');
INSERT INTO [t29677] VALUES (390,'قال السيد: وكذلك السيد أبو طالب حكى الإجماع في كفار التأويل','562','271','0');
INSERT INTO [t29677] VALUES (391,'أقول: يرد على هذه الدعوى إشكالات','563','271','0');
INSERT INTO [t29677] VALUES (392,'الإشكال الأول: أن السيد قد أقر أن الشيخ أحمد روى عن أبي طالب قريبا من الإجماع','563','271','0');
INSERT INTO [t29677] VALUES (393,'الإشكال الثاني: أن السيد روى هذا الإجماع','564','271','0');
INSERT INTO [t29677] VALUES (394,'الإشكال الثالث: قد أصل أصلا في إلزامه لابن الصلاح','565','271','0');
INSERT INTO [t29677] VALUES (395,'الإشكال الرابع: أنه يحتاج في إسناد هذا إلى إسناد صحيح','565','271','0');
INSERT INTO [t29677] VALUES (396,'الإشكال الخامس: لو قدرنا، فالسيد يحتاج إلى تعديله','565','271','0');
INSERT INTO [t29677] VALUES (397,'الإشكال السادس: ما تقدم على تقدير صحة هذا وإلا فالظاهر أن السيد مقر بعدم صحته قال: وكذلك ابن الحاجب','565','271','0');
INSERT INTO [t29677] VALUES (398,'أقول: يرد على كلامه هذا إشكالات','565','271','0');
INSERT INTO [t29677] VALUES (399,'الإشكال الأول: أن السيد قد قال: إن علماء الأشعرية كفار تصريح','565','271','0');
INSERT INTO [t29677] VALUES (400,'الإشكال الثاني: سلمنا أن ابن الحاجب','566','271','0');
INSERT INTO [t29677] VALUES (401,'الإشكال الثالث: أن السيد قد أقر فيما تقدم أن ابن الحاجب لم يرو الإجماع على رد كفار التأويل','566','271','0');
INSERT INTO [t29677] VALUES (402,'الإشكال الرابع: أنا نبين للسيد ضعف مأخذه من كلام ابن الحاجب الإشكال الخامس: من أين حصل للسيد إسناد صحيح إلى ابن الحاجب','568','271','0');
INSERT INTO [t29677] VALUES (403,'قال السيد: ومن روى الإجماع، قبلت روايته','568','271','0');
INSERT INTO [t29677] VALUES (404,'أقول: لما فرغ السيد من دعوى الإجماع رأى ذلك معارضا، ويرد على كلامه إشكالات','568','271','0');
INSERT INTO [t29677] VALUES (405,'الإشكال الأول: قال: ومن روى الإجماع قبلت روايته، لأنها مثبتة','568','271','0');
INSERT INTO [t29677] VALUES (406,'الإشكال الثاني: أن السيد توهم أن راوي الإجماع مثبت، وراوي الخلاف ناف','569','271','0');
INSERT INTO [t29677] VALUES (407,'الإشكال الثالث: أن السيد رجح رواية الإجماع بمخالفتها للأصل','570','271','0');
INSERT INTO [t29677] VALUES (408,'الإشكال الرابع: أنه رجح بمخالفة الأصل والترجيح بها مختلف فيه','570','271','0');
INSERT INTO [t29677] VALUES (409,'الإشكال الخامس: أنه روى الإجماع على قبولهم','572','271','0');
INSERT INTO [t29677] VALUES (410,'قال السيد: وقال آخرون الخلاف واقع','572','271','0');
INSERT INTO [t29677] VALUES (411,'أقول: في كلامه هذا إشكالات','573','271','0');
INSERT INTO [t29677] VALUES (412,'الإشكال الأول: أن السيد ذكر أنه لا يجوز أن يقدم على ما لا يأمن كونه كذبا','573','271','0');
INSERT INTO [t29677] VALUES (413,'الإشكال الثاني: أن السيد قال: فهو مروي من أئمتنا عن المؤيد','573','271','0');
INSERT INTO [t29677] VALUES (414,'الأشكال الثالث: أن يقول للسيد: إذا كنت مقرا أن المؤيد بالله خالف في هذه المسألة فإما أن تقول','574','271','0');
INSERT INTO [t29677] VALUES (415,'قال السيد: وأما الهادي والقاسم عليهما السلام، فروي عنهما عدم القبول، وقال أبو مضر بالقبول','574','271','0');
INSERT INTO [t29677] VALUES (416,'أقول: يرد على كلامه هذا إشكالات','574','271','0');
INSERT INTO [t29677] VALUES (417,'الإشكاك الأول: أنا نقول لما تعارضت الروايات عن القاسم ويحيى شرع يرفع التعارض بالترجيح','574','271','0');
INSERT INTO [t29677] VALUES (418,'الإشكال الثاني: أنه احتج لرواية أبي جعفر بأنها توافق','574','271','0');
INSERT INTO [t29677] VALUES (419,'الأشكال الثالث: أنا نبين للسيد أن للهادي والقاسم أصولا توجب قبولهم','575','271','0');
INSERT INTO [t29677] VALUES (420,'الإشكال الرابع: سلمنا أن تخريج القبول مساو لتخريج الرد غير راجح عليه، فالقبول أولى','576','271','0');
INSERT INTO [t29677] VALUES (421,'الإشكال الخامس: أن القاضي أبا مضر من أئمة مذهب الزيدية الجلة وقد روى عن الهادي والقاسم قبول المتأولين رواية','576','271','0');
INSERT INTO [t29677] VALUES (422,'الإشكال السادس: أن السيد يحتاج في نسبة هذا التخريج إلى أبي جعفر إسنادا صحيحا','576','271','0');
INSERT INTO [t29677] VALUES (423,'قال السيد: وقال الشيخان: إنهم لا يقبلون','577','271','0');
INSERT INTO [t29677] VALUES (424,'أقول: في كلامه هذا إشكالات','577','271','0');
INSERT INTO [t29677] VALUES (425,'الإشكال الأول: أنه نكر لا يقبل من عوف أنه عاند','577','271','0');
INSERT INTO [t29677] VALUES (426,'تنبيه: غير خاف على أهل النظر أن أهل العلوم العقلية','579','271','0');
INSERT INTO [t29677] VALUES (427,'الإشكال الثاني: أن السيد أوهم أن الشيخ أبا الحسن والرازي يقولان بمثل مقالته','580','271','0');
INSERT INTO [t29677] VALUES (428,'الإشكال الثالث: أن العلم بالأمور الوجدانيات، مما لا يصح أن يحتج به','582','271','0');
INSERT INTO [t29677] VALUES (429,'الإشكال الرابع: أن العلم بتعمد الباطل والظن','583','271','0');
INSERT INTO [t29677] VALUES (430,'قال السيد: وأما الدليل، فقد احتج القائلون','586','271','0');
INSERT INTO [t29677] VALUES (431,'أقول: كلام السيد هدا دال على أن القائلين ما احتجوا إلا بحجتين','586','271','0');
INSERT INTO [t29677] VALUES (432,'قال السيد: واحتج الرادون بوجوه ... الأول قولة تعالى {يا أيها الذين آمنوا إن جاءكم فاسق بنبأ فتبينوا}','586','271','0');
INSERT INTO [t29677] VALUES (433,'أقول: كلام السيد في الاحتجاج بهذه الآية يحتمل إيراد إشكالات كثيرة نذكر منها ما حضر','587','271','0');
INSERT INTO [t29677] VALUES (434,'الإشكال الأول: أن نقول: احتجاج السيد بهذه الآية ونحن ننازعه في ذلك من طريقين','587','271','0');
INSERT INTO [t29677] VALUES (435,'الطريق الأولى أنه قد ورد في السمع ما يدل على أن الفاسق كان في ذلك الزمان يطلق على الكافر','587','271','0');
INSERT INTO [t29677] VALUES (436,'الطريق الثانية: سلمنا لك أن النصوص القرآنية لم يدل على أن الفاسق يختص في عرف ذلك الزمان بالكافر','591','271','0');
INSERT INTO [t29677] VALUES (437,'الإشكال الثاني: أنا نقول قد ورد في اللغة ما يدل على أن الفسق تعمد المعصية، وأن الفاسق المتعمد','591','271','0');
INSERT INTO [t29677] VALUES (438,'الإشكال الثالث: أن المتأولين غير موجودين في ذلك الزمان','594','271','0');
INSERT INTO [t29677] VALUES (439,'الإشكال الرابع: أنها جاءت أدلة على أن المتأول في الكبيرة التي ليست بكفر يسمى مسلما','596','271','0');
INSERT INTO [t29677] VALUES (440,'الاشكال الخامس: أن في هذه الأدلة ما يدل على أن المتأول غير الكافر','601','271','0');
INSERT INTO [t29677] VALUES (441,'الإشكال السادس: أن الاستدلال بهذه الآية الكريمة من قبيل مفهوم المخالفة','602','271','0');
INSERT INTO [t29677] VALUES (442,'الإشكال السابع: أن الآية الكريمة نزلت في حقوق المخلوقين','604','271','0');
INSERT INTO [t29677] VALUES (443,'الأشكال الثامن: أن الله تعالى قال: فتبينوا، ولم يقل: فلا تقبلوه','606','271','0');
INSERT INTO [t29677] VALUES (444,'الإشكال التاسع: قال: إنه علق الحكم على صفة وهي الفسق، قلنا','607','271','0');
INSERT INTO [t29677] VALUES (445,'الإشكال العاشر: أن السيد ادعى أن الآية في معنى العموم','609','271','0');
INSERT INTO [t29677] VALUES (446,'الإشكال الحادي عشر: أن السيد عظم الكلام في تفسير القرآن العظيم','609','271','0');
INSERT INTO [t29677] VALUES (447,'الإشكال الثاني عشر: بقي على السيد بقية في الاستدلال بهذه الآية','609','271','0');
INSERT INTO [t29677] VALUES (448,'الإشكال الثالث عشر: بقي على السيد بقية، وذلك أنه قد علم أن العموم مختلف في الاحتجاج به','611','271','0');
INSERT INTO [t29677] VALUES (449,'الاشكال الرابع عشر: أن الآية وردت بلفظ الأمر','612','271','0');
INSERT INTO [t29677] VALUES (450,'الإشكال الخامس عشر: أن في أهل العلم من يقول: إن ألفاظ العموم مشتركة بين العموم والخصوص','613','271','0');
INSERT INTO [t29677] VALUES (451,'الإشكال السادس عشر: أن لهذه الآية معارضات كثيرة','614','271','0');
INSERT INTO [t29677] VALUES (452,'الإشكال السابع عشر: أن لهذه الآية مخصصا','614','271','0');
INSERT INTO [t29677] VALUES (453,'قال السيد: ومن ذلك {ولا تركنوا} الآية','614','271','0');
INSERT INTO [t29677] VALUES (454,'أقول: يرد على استدلال السيد بهذه الآية إشكالات','615','271','0');
INSERT INTO [t29677] VALUES (455,'الإشكال الأول: أن معنى الآية ظني مختلف فيه، ويدل على ذلك مح ما تقدم وجوه','615','271','0');
INSERT INTO [t29677] VALUES (456,'الأول: أجمع العقلاء من أهل الإسلام وغيرهم على أن الإنسان يرجع إلى تصديق عدوه وقبول كلامه','619','271','0');
INSERT INTO [t29677] VALUES (457,'الثاني: أنه يجور العمل بخبر الفساق بالإجماع','620','271','0');
INSERT INTO [t29677] VALUES (458,'الثالث: أنه يجوز نكاح الفاسقة بغير الزنى','620','271','0');
INSERT INTO [t29677] VALUES (459,'الرابع: أنه يجوز شهادة الكافر الكتابي عند الحاجة','624','271','0');
INSERT INTO [t29677] VALUES (460,'الخامس: أن شهادة بعضهم على بعض مقبولة','625','271','0');
INSERT INTO [t29677] VALUES (461,'الإشكال الثاني: أن الاحتجاج بهذه الآية لا يصح','625','271','0');
INSERT INTO [t29677] VALUES (462,'الإشكال الثالث: أن الآية عامة','629','271','0');
INSERT INTO [t29677] VALUES (463,'الإشكال الرابع: قد قالل: إن معرفة التفسير المحتاج إليه من القرآن صعب شديد، مدركه بعيد','630','271','0');
INSERT INTO [t29677] VALUES (464,'الإشكال الخامس: بقي على السيد أن يبين أن هذه الآية وردت على سبب أو لم ترد على سبب','630','271','0');
INSERT INTO [t29677] VALUES (465,'الإشكال السادس: أن هذا العموم مخصوص','630','271','0');
INSERT INTO [t29677] VALUES (466,'الإشكال السابع: أن الآية من قبيل العموم','631','271','0');
