INSERT INTO [t29677] VALUES (721,'الإشكال الثالث: سلمنا للسيد أن ذلك مذهب الأوزري','942','700','0');
INSERT INTO [t29677] VALUES (722,'الإشكال الرابع: أن هذا المفهوم من الأوزري إن كان حسنا فلا معنى لإبطاله وإن وإن قبيحا فنسبته إليه لمجرد الفهم والحدس من قبيل سوء الظن المحرم','943','700','0');
INSERT INTO [t29677] VALUES (723,'الإشكال الخامس: سلمنا أن هذا مذهب شيخ الأوزري، فكيف ينسبه إلى طائفة من المحدثين','943','700','0');
INSERT INTO [t29677] VALUES (724,'الأشكال السادس: سلمنا أنه يلزمهم','943','700','0');
INSERT INTO [t29677] VALUES (725,'الإشكال السابع: أن السيد إنما نقل هذا المذهب بالفهم والحدس','944','700','0');
INSERT INTO [t29677] VALUES (726,'الإشكال الثامن: أن السيد مدح الأوزري بأنه المحدث الضابط، فكيف استحق المدح بهذه الكتب وهي عند السيد من رواية الكفار والفساق المصرحين','944','700','0');
INSERT INTO [t29677] VALUES (727,'قوله: قال: وأما الفصل الأول، وهو أن كل ما في هذه الكتب من حديث فصحيح ففيه موضعان: حكاية المذهب، والدليل','944','700','0');
INSERT INTO [t29677] VALUES (728,'الجواب عن السيد في هذا من وجوه','945','700','0');
INSERT INTO [t29677] VALUES (729,'الوجه الأول: أنه حكى الخلاف هي هذه المسألة','945','700','0');
INSERT INTO [t29677] VALUES (730,'الوجه الثاني: أن السيد غلط على ابن الصلاح، ولم ينقل عنه مذهبه','945','700','0');
INSERT INTO [t29677] VALUES (731,'قوله: قال: وليت شعري كيف كان هذا الإجماع، أكان بأن طاف هذا السائل جميع البقاع أم بأن جمع له علماء الأمة وأذن فيهم بهذا السؤال','946','700','0');
INSERT INTO [t29677] VALUES (732,'في كلام السيد هذا مباحث','947','700','0');
INSERT INTO [t29677] VALUES (733,'البحث الأول: أنه أثبت في كلامه أن سائلا سأل الأمة، والرجل إنما قال: لو أن رجلا سأل الفقهاء','947','700','0');
INSERT INTO [t29677] VALUES (734,'البحث الثاني: أن كلام السيد هذا يلزمه زيادة شروط في رواية الإجماع لا نعلم أن أحدا اشترطها','947','700','0');
INSERT INTO [t29677] VALUES (735,'البحث الثالث: أن السيد ادعى في كتابه إجماعات كثيرة، ولم يحصل فيها شيء من هذه الشرائط','948','700','0');
INSERT INTO [t29677] VALUES (736,'البحث الرابع: أن السيد ادعى على الرجل في أول كلامه أنه ادعى إجماع الفقهاء ثم ألزمه هنا أن يجمع له الأمة في صعيد واحد','948','700','0');
INSERT INTO [t29677] VALUES (737,'البحث الخامس: وأي إجماع صحيح بغير علماء أهل البيت وشيعتهم','948','700','0');
INSERT INTO [t29677] VALUES (738,'البحث السادس: أنه ادعى إجماع العلماء','948','700','0');
INSERT INTO [t29677] VALUES (739,'البحث السابع: أنك إما أن تنكر الإجماع السكوتي أم لا','949','700','0');
INSERT INTO [t29677] VALUES (740,'البحث الثامن: أقصى ما في الباب أنه ظهر للسيد غلط هذا الرجل الذي ادعى الإجماع','950','700','0');
INSERT INTO [t29677] VALUES (741,'البحث التاسع: يتفضل السيد ويخبرنا من الذي يقول من أهل البيت بطلاق زوجة هذا الحالف','951','700','0');
INSERT INTO [t29677] VALUES (742,'البحث العاشر: أن الظاهر إجماعهم عليهم السلام على ذلك','951','700','0');
INSERT INTO [t29677] VALUES (743,'البحث الحادي عشر: أن بين دفتي البخاري ما ليس من كلام رسول الله قطعا','952','700','0');
INSERT INTO [t29677] VALUES (744,'البحث الثاني عشر: قول النووي: إن بعض الحفاظ قد استدركوا على البخاري ومسلم','952','700','0');
INSERT INTO [t29677] VALUES (745,'البحث الثالث عشر: أنه لا طريق إلى العلم بأن الحديث المتلقى بالقبول هو بنفسه لفظ رسول الله - صلى الله عليه وسلم -','954','700','0');
INSERT INTO [t29677] VALUES (746,'البحث الرابع عشر: أن السيد أنكر طريق معرفة إجماع الفقهاء على ذلك','955','700','0');
INSERT INTO [t29677] VALUES (747,'قوله: قال: والذي يذهب إليه علماؤنا أن في أخبار هذه الكتب الصحيح والمعلول','955','700','0');
INSERT INTO [t29677] VALUES (748,'الجواب: ما مرادك بأن ذلك فيها، هل كثير مساو للصحيح','955','700','0');
INSERT INTO [t29677] VALUES (749,'المضعف عليهما نوعان','960','700','0');
INSERT INTO [t29677] VALUES (750,'النوع الأول: المعلول','961','700','0');
INSERT INTO [t29677] VALUES (751,'النوع الثاني: مما يقدح عليهما به الرواية عن بعض من اختلف في جرحه وتعديله','962','700','0');
INSERT INTO [t29677] VALUES (752,'قوله: قال: والضابط في ذلك أن ما صححه أئمتنا هو صحيح','974','700','0');
INSERT INTO [t29677] VALUES (753,'الجواب على هذا من وجهين','974','700','0');
INSERT INTO [t29677] VALUES (754,'الأول: إما أن يريد ما أجمعوا على رده إجماعا معلوما فهو مردود، وهذا مسلم، لأن إجماعهم المعلوم عندنا حجة','975','700','0');
INSERT INTO [t29677] VALUES (755,'أو أن يريد أن ما اختلفوا فيه فهو مردود، وهذا خلاف إجماعهم على كل تقدير','979','700','0');
INSERT INTO [t29677] VALUES (756,'الوجه الثاني: أن كلام السيد حجة عليه لا له','982','700','0');
INSERT INTO [t29677] VALUES (757,'قوله قال: لأن روايتهم لا تخلو من ضعف، وإنما تقبل عند عدم المعارض','983','700','0');
INSERT INTO [t29677] VALUES (758,'فهذا قصر للعدالة على الأئمة عليهم السلام، وهذا غلو','983','700','0');
INSERT INTO [t29677] VALUES (759,'قوله: قال: لأنها رواية عمن لا تعلم عدالته ولا نزاهته عن فسق التأويل','984','700','0');
INSERT INTO [t29677] VALUES (760,'فهذه دعوى على الأمة بالجهل بذلك، وهي غير مقبولة','984','700','0');
INSERT INTO [t29677] VALUES (761,'قوله: قال: هذا إذا كان الناظر في الحديث مجتهدا','984','700','0');
INSERT INTO [t29677] VALUES (762,'فهذا مجرد دعوى لا تفتقر إلى جواب','984','700','0');
INSERT INTO [t29677] VALUES (763,'قوله: قال: ولأنه لا يرجح بالخبر حتى يعلم أله عير منسوخ','984','700','0');
INSERT INTO [t29677] VALUES (764,'فهذا الذي ذكره لا يجب على المجتهد عند أحد من أهل البيت ولا عند أحد من جماهير أهل الإسلام','984','700','0');
INSERT INTO [t29677] VALUES (765,'قوله: قال: ولأن الترجيح بالأخبار اجتهاد','986','700','0');
INSERT INTO [t29677] VALUES (766,'فهذا الاحتجاج ضعيف بمرة','986','700','0');
INSERT INTO [t29677] VALUES (767,'قوله: قال: فهل يستنتج العقيم ويستفتى من ليس بعليم؟','987','700','0');
INSERT INTO [t29677] VALUES (768,'الجواب على هذا من وجوه','987','700','0');
INSERT INTO [t29677] VALUES (769,'الوجه الأول: أن كلام السيد في المسألة الأولى دال دلالة واضحة على أنه يدعي أنه غير مجتهد، بل يدعي أنه لا مجتهد في الزمان','988','700','0');
INSERT INTO [t29677] VALUES (770,'الوجه الثاني: أن نقول: ما مرادك بهذا الإنتاج','988','700','0');
INSERT INTO [t29677] VALUES (771,'الوجه الثالث: أن نقول: ما قصدك " ويستفتى من ليس بعليم','989','700','0');
INSERT INTO [t29677] VALUES (772,'قوله: قال: وأنا أضرب لهذا مثالا','990','700','0');
INSERT INTO [t29677] VALUES (773,'في كلام السيد هذا أنظار','990','700','0');
INSERT INTO [t29677] VALUES (774,'النظر الأول: أنه تعرض لتفسير كلام المؤيد بالله من غير غرابة في ألفاظه','990','700','0');
INSERT INTO [t29677] VALUES (775,'النظر الثاني: أن السيد في كلامه هذا قد أجاز الترجيح بالأخبار لبعض المقلدين','992','700','0');
INSERT INTO [t29677] VALUES (776,'النظر الثالث: أني لم أوجب الترجيح بالأخبار على جميع المكلفين من العامة','992','700','0');
INSERT INTO [t29677] VALUES (777,'الئظر الرابع: أن نقول للسيد: أخبرنا عن هذه المسألة، هل هي عندك قطعية أو ظنية','993','700','0');
INSERT INTO [t29677] VALUES (778,'النظر الخامس: أنه وعد بضرب مثل، ولم يأت بما يصلح أن يسمى مثلا مضروبا عند البلغاء','993','700','0');
INSERT INTO [t29677] VALUES (779,'قوله: قال: فأما أن يكون له في كل مسألة أن يرجح ويخرج عن مذهب من كان قلده فالصحيح أن عليه التزام مذهب إمام معين، واحتج بوجوه: أحدها: بالإجماع','994','700','0');
INSERT INTO [t29677] VALUES (780,'ضعف كلام السيد في هذا يتبين بأنظار','994','700','0');
INSERT INTO [t29677] VALUES (781,'النظر الاول: أنه منع من جواز الترجيح للمقلد في كل مسألة','994','700','0');
INSERT INTO [t29677] VALUES (782,'النظر الثاني: أنا نبين أن مذهب المنصور هو ما ذكرنا','995','700','0');
INSERT INTO [t29677] VALUES (783,'النظر الثاني: أني أذهب إلى ما ذهب إليه المنصور من وجوب التزام مذهب','996','700','0');
INSERT INTO [t29677] VALUES (784,'النظر الرابع: أن السيد أراد أن ينصر الإمام المنصور، فاحتج له بما يتنزه المنصور من بناء مذهبه عليه. وقد وهم السيد في ثلاثة أوهام','997','700','0');
INSERT INTO [t29677] VALUES (785,'الوهم الأول: أنه عول علي إجماع العامة المقلدين','997','700','0');
INSERT INTO [t29677] VALUES (786,'الوهم الثاني: أنا لو سلمنا أن إجماعهم صحيح لما دل على مذهبه','998','700','0');
INSERT INTO [t29677] VALUES (787,'الوهم الثالث: وهم أن المقلدين مجمعون على الالتزام','998','700','0');
INSERT INTO [t29677] VALUES (788,'النظر الخامس: أن السيد ادعى أن التنقل في المذاهب ما وقع ألبتة','999','700','0');
INSERT INTO [t29677] VALUES (789,'النظر السادس: قول السيد: إن هذا لو وقع في زماننا لأنكره الناس، عجيب أيضا','1001','700','0');
INSERT INTO [t29677] VALUES (790,'النظر السابع: أن السيد جاوز حد العادة في الغلو','1002','700','0');
INSERT INTO [t29677] VALUES (791,'قوله: قال: الوجه الثاني: أنه لا يتميز على هذا الوجه المجتهد من المقلد','1003','700','0');
INSERT INTO [t29677] VALUES (792,'فهذا الوجه أضعف مما قبله، وهو لا يؤدي إلى ما ذكره','1003','700','0');
INSERT INTO [t29677] VALUES (793,'قوله: قال: الثالثة: أنه لا يلزم من ذلك التناقض.','1005','700','0');
INSERT INTO [t29677] VALUES (794,'الجواب عليه في هذا يتم بذكر أنظار','1005','700','0');
INSERT INTO [t29677] VALUES (795,'النظر الأول: أن السيد استدل، ثم استثنى','1005','700','0');
INSERT INTO [t29677] VALUES (796,'النظر الثاني: أن ما جاز في ذلك على المقلد جاز على المجتهد','1006','700','0');
INSERT INTO [t29677] VALUES (797,'النظر الثالث: أنه جعل القرينة الدالة على صرف الأمر من الحقيقة إلى المجاز مما يحتاج إلى اطلاع في العلوم','1006','700','0');
INSERT INTO [t29677] VALUES (798,'النظر الرابع: أن كلامه في هذا الفصل يستلزم اشتراط السفر والخطر في صحة الاجتهاد','1007','700','0');
INSERT INTO [t29677] VALUES (799,'النظر الخامس: أن السيد إما أن يكون يعرف هذه القرينة التي هول معرفتها أو لا','1007','700','0');
INSERT INTO [t29677] VALUES (800,'قوله: قال: الموضع الثاني في الدليل على أن في أخبار هذه الكتب المسماة بالصحاح ما هو غير صحيح','1008','700','0');
INSERT INTO [t29677] VALUES (801,'الجواب على ما ذكره من وجوه','1009','700','0');
INSERT INTO [t29677] VALUES (802,'الوجه الأول: أنا قد بينا من نص أهل البيت على قبول الخوارج','1009','700','0');
INSERT INTO [t29677] VALUES (803,'الوجه الثاني: أن حفاظ الحديث وأئمة النقل لم يقتصروا على تدوين الصحيح المجمع على صحته عند جميع الفرق','1020','700','0');
INSERT INTO [t29677] VALUES (804,'الوجه الثالث: أن المحدثين حين رأوا اختلاف الناس في من يقبل ولا يقبل أوجبوا بيان الإسناد','1023','700','0');
INSERT INTO [t29677] VALUES (805,'الوجه الرابع: أن اعتراض كتب الحديث الصحاح بأن فيها ما ليس بصحيح عند غيرهم عمل من لم يعرف ما معنى الصحيح عند أهله','1025','700','0');
INSERT INTO [t29677] VALUES (806,'ذكر بيان أحاديث معاوية في الكتب الستة، لتعرف عدم انفراده، وقلة ذلك، وعدم نكارته وفيه خمسة أقسام','1029','700','0');
