INSERT INTO [t29677] VALUES (808,'الحديث الأول: تحريم الوصل في شعور النساء','1029','700','0');
INSERT INTO [t29677] VALUES (809,'الحديث الثاني: " لا تزال طائفة من أمتي ظاهرين على الحق "','1030','700','0');
INSERT INTO [t29677] VALUES (810,'الحديث الثالث: النهي عن الركعتين بعد العصر','1031','700','0');
INSERT INTO [t29677] VALUES (811,'الحديث الرابع: النهي عن الإلحاق في المسألة','1032','700','0');
INSERT INTO [t29677] VALUES (812,'الحديث الخامس: " إن هذا الأمر لا يزال في قريش "','1033','700','0');
INSERT INTO [t29677] VALUES (813,'الحديث السادس: حد شارب الخمر','1034','700','0');
INSERT INTO [t29677] VALUES (814,'الحديث السابع: النهي عن لباس الحرير والذهب وجلود السباع','1035','700','0');
INSERT INTO [t29677] VALUES (815,'الحديث الثامن: حديث افتراق الأمة إلى نيف وسبعين فرقة','1036','700','0');
