INSERT INTO [t29677] VALUES (819,'الحديث الثاني عشر: حكم من سها في الصلاة','1039','700','0');
INSERT INTO [t29677] VALUES (820,'الحديث الثالث عشر: النهي عن النياحة','1040','700','0');
INSERT INTO [t29677] VALUES (821,'الحديث الرابع عشر: النهي عن التمادح','1040','700','0');
INSERT INTO [t29677] VALUES (822,'الحديث الخامس عشر: النهي عن كل مسكر','1041','700','0');
INSERT INTO [t29677] VALUES (823,'الحديث السادس عشر: كراهة رضى الداخل على القوم بقيامهم له','1042','700','0');
INSERT INTO [t29677] VALUES (824,'الحديث السابع عشر: النهي عن تتبع عورات الناس','1044','700','0');
INSERT INTO [t29677] VALUES (825,'الحديث الثامن عشر: النهي عن القران بين الحج والعمرة','1044','700','0');
