INSERT INTO [t29677] VALUES (827,'الحديث الموفي عشرين: روى عن أخته أم حبيبة أن النبي - صلى الله عليه وسلم - كان يصلي في الثوب الذي يجامعها فيه ما لم ير فيه أذى','1047','700','0');
INSERT INTO [t29677] VALUES (828,'الحديث الحادي والعشرون: روي عن أبيه النهي لمن أكل الثوم والبصل عن دخول مسجد النبي','1048','700','0');
