INSERT INTO [t29677] VALUES (830,'القسم الثاني: ما ورد في الفضائل المشهورة عن غيره','1049','700','0');
INSERT INTO [t29677] VALUES (831,'الحديث الأول: فضل إجابة المؤذن','1049','700','0');
