INSERT INTO [t29677] VALUES (833,'الحديث الثالث: في فضل حلق الذكر والاجتماع عليه','1051','700','0');
INSERT INTO [t29677] VALUES (834,'الحديث الرابع: النهي عن الغلوطات','1051','700','0');
INSERT INTO [t29677] VALUES (835,'الحديث الخامس: " ليلة القدر ليلة سبعة وعشرين من رمضان "','1054','700','0');
INSERT INTO [t29677] VALUES (836,'الحديث السادس: فضل حب الأنصار','1054','700','0');
INSERT INTO [t29677] VALUES (837,'الحديث السابع: " اشفعوا تؤجروا "','1054','700','0');
INSERT INTO [t29677] VALUES (838,'الحديث الثامن: " المؤذنون أطول الناس أعناقا يوم القيامة "','1055','700','0');
INSERT INTO [t29677] VALUES (839,'الحديث التاسع: " من التمس رضا الله بسخط الناس "','1055','700','0');
INSERT INTO [t29677] VALUES (840,'الحديث العاشر: تحريم وصل الشعر على النساء','1058','700','0');
INSERT INTO [t29677] VALUES (841,'الحديث الحادي عشر: " العينان وكاء السه "','1058','700','0');
INSERT INTO [t29677] VALUES (842,'القسم الثالث: ما يوافق مذهب المعترض من حديثه','1058','700','0');
INSERT INTO [t29677] VALUES (843,'الحديث الأول: " لا تنقطع الهجرة "','1058','700','0');
INSERT INTO [t29677] VALUES (844,'الحديث الثاني: النهي عن لباس الذهب إلا مقطعا','1059','700','0');
INSERT INTO [t29677] VALUES (845,'الحديث الثالث: " الصيام يوم كذا وكذا، ونحن متقدمون "','1060','700','0');
