INSERT INTO [t29701] VALUES (131,'أهدى غنما مقلدة','132','0','0');
INSERT INTO [t29701] VALUES (132,'نرسل كلابا لنا معلمة، قال: كل مما أمسكن عليك. قال: قلت: يا رسول الله وإن قتلن؟ قال: وإن','133','0','0');
INSERT INTO [t29701] VALUES (133,'من لاءمكم من خدمكم فأطعموهم مما تأكلون واكسوهم مما تلبسون، ومن لم يلائمكم فبيعوا، ولا','134','0','0');
INSERT INTO [t29701] VALUES (134,'ربنا الله الذي في السماء، تقدس اسمك , أمرك في السماء والأرض، كما رحمتك في السماء فاجعل','135','0','0');
INSERT INTO [t29701] VALUES (135,'أعيذكما بكلمات الله التامات من كل شيطان وهامة، ومن كل عين لامة.','136','0','0');
INSERT INTO [t29701] VALUES (136,'من اكتوى أو استرقى فلم يتوكل','137','0','0');
INSERT INTO [t29701] VALUES (137,'أحق ما أكل الرجل من أطيب كسبه، وأولاد الرجل من أطيب كسبه','138','0','0');
INSERT INTO [t29701] VALUES (138,'توقظ أهلها ليلة ثلاث وعشرين','139','0','0');
INSERT INTO [t29701] VALUES (139,'ينهاكم عن الحقل: من استغنى عن أرضه فليمنحها أخاه.','140','0','0');
INSERT INTO [t29701] VALUES (140,'مر رسول الله بتمرة مطروحة في الطريق، قال: لولا أني أخشى أن تكون من الصدقة','141','0','0');
INSERT INTO [t29701] VALUES (141,'حرم تجارة في الخمر','142','0','0');
INSERT INTO [t29701] VALUES (142,'الولاء لولي النعمة، ولمن أعطى الثمن. وكان لها زوج فخيرت فاختارت نفسها','143','0','0');
INSERT INTO [t29701] VALUES (143,'لو أن أحدهم قال حين يأتي أهله: قال منصور أراه قال: بسم الله , اللهم جنبنا الشيطان وجنب','144','0','0');
INSERT INTO [t29701] VALUES (144,'ما رأيت فرج رسول الله صلى الله عليه قط','145','0','0');
INSERT INTO [t29701] VALUES (145,'تسبيح الله في دبر كل صلاة مكتوبة عشرا','146','0','0');
INSERT INTO [t29701] VALUES (146,'امرأة شهرا توفي عنها زوجها ولم يدخل بها، ولم يفرض لها صداقا. فقال: لها مثل صداق أهلها','147','0','0');
INSERT INTO [t29701] VALUES (147,'لعن الله الواشمات والمتوشمات، والمتفلجات للحسن المغيرات خلق الله. فبلغ ذلك امرأة من بني','148','0','0');
INSERT INTO [t29701] VALUES (148,'ما منكم من أحد إلا قد كتب مقعده في الجنة ومقعده في النار. قالوا: يا رسول الله أفلا نتكل؟','149','0','0');
INSERT INTO [t29701] VALUES (149,'بئس ما لأحدهم يقول: نسيت آية كيت وكيت، بل هو نسي','150','0','0');
INSERT INTO [t29701] VALUES (150,'ما منكم من أحد إلا وقد وكل به قرينه من الجن وقرينه من الملائكة.','151','0','0');
INSERT INTO [t29701] VALUES (151,'النذر لا يرد شيئا، إنما يستخرج به من الشحيح','152','0','0');
INSERT INTO [t29701] VALUES (152,'لقد أمرت بالسواك حتى خشيت على فمي','153','0','0');
INSERT INTO [t29701] VALUES (153,'بزق دما ثم لم يتوضأ.','154','0','0');
INSERT INTO [t29701] VALUES (154,'إذا سجد، قال: سجد وجهي للذي خلقه وشق سمعه وبصره.','155','0','0');
INSERT INTO [t29701] VALUES (155,'صلى في كسوف الشمس ركعتين','156','0','0');
INSERT INTO [t29701] VALUES (156,'أوصي امرأ بأبيه، وأوصي امرأ بأبيه، وأوصي امرأ بأبيه.','157','0','0');
INSERT INTO [t29701] VALUES (157,'زكاة البقر والإبل والغنم، فأخبره، فما ولى، قال: يا رسول الله أعشرهم؟ قال: إنما العشور على','158','0','0');
INSERT INTO [t29701] VALUES (158,'خصلتان لا يحصيهما رجل مسلم إلا دخل الجنة، وهما يسير ومن يعمل بهما','159','0','0');
INSERT INTO [t29701] VALUES (159,'لم يحدث عمر السمر بعد اليوم، يعني كره','160','0','0');
INSERT INTO [t29701] VALUES (160,'أتيتك أبايعك على الهجرة، وتركت أبوي يبكيان.','161','0','0');
INSERT INTO [t29701] VALUES (161,'إذا جاء رمضان صفدت فيه الشياطين، وغلقت فيه أبواب النار، وفتحت فيه أبواب الجنة، ونادى','162','0','0');
INSERT INTO [t29701] VALUES (162,'نستلمها الحجر والركن اليماني؟ فقال: سمعت رسول الله صلى الله عليه , يقول: إن مسحهما يحطان','163','0','0');
INSERT INTO [t29701] VALUES (163,'البركة وسط القصعة، فكلوا من نواحيها، ولا تأكلوا من رأسها','164','0','0');
INSERT INTO [t29701] VALUES (164,'الله تعالى يقول: {اقتربت الساعة وانشق القمر} [القمر: ١] ، ألا إن القمر قد انشق على عهد','165','0','0');
INSERT INTO [t29701] VALUES (165,'إذا رجع أول ما يبدأ بالسواك','166','0','0');
INSERT INTO [t29701] VALUES (166,'لأوتي بالإناء وأنا حائض فأشرب منه، فيتناوله رسول الله صلى الله عليه فيضع فاه من حيث شربت','167','0','0');
INSERT INTO [t29701] VALUES (167,'ما بال رسول الله صلى الله عليه قائما منذ أنزل الله عليه القرآن','168','0','0');
INSERT INTO [t29701] VALUES (168,'ما لي أراكم تأتوني قلحا، استاكوا، فلولا أن أشق على أمتي لفرضت عليهم السواك كما فرضت عليهم','169','0','0');
INSERT INTO [t29701] VALUES (169,'لا صلاة إلا بقرآن؛ فاتحة الكتاب فما زاد. قال: وسمعت أبا عثمان , يقول: رأيت عمر يمد يديه','170','0','0');
INSERT INTO [t29701] VALUES (170,'لا يأكل طعاما إلا استن، فقال: لأن أكون استقبلت من أمري ما استدبرت أحب إلي من','171','0','0');
INSERT INTO [t29701] VALUES (171,'يغسل ذكره ويتوضأ ويرقد','172','0','0');
INSERT INTO [t29701] VALUES (172,'يمسح رأسه مرة','173','0','0');
INSERT INTO [t29701] VALUES (173,'ليس على المسلم في فرسه ولا عبده صدقة','174','0','0');
INSERT INTO [t29701] VALUES (174,'إني اتخذت خاتما من ذهب فاتخذ الناس خواتيم من ذهب. فنبذه، وقال: إني لست ألبسه','175','0','0');
INSERT INTO [t29701] VALUES (175,'الفتنة من هاهنا، إن الفتنة من هاهنا ثلاثا من حيث يطلع قرنا شيطان','176','0','0');
INSERT INTO [t29701] VALUES (176,'وقت رسول الله صلى الله عليه لأهل الشام الجحفة، ولأهل المدينة ذا الحليفة، ولأهل نجد','177','0','0');
INSERT INTO [t29701] VALUES (177,'اليهود إذا سلموا عليكم يقولون: السام عليكم، فردوا عليهم: وعليكم','178','0','0');
INSERT INTO [t29701] VALUES (178,'أسلم سالمها الله، وغفار غفر الله لها، وعصية عصت الله ورسوله','179','0','0');
INSERT INTO [t29701] VALUES (179,'رأى ابن عمر شيئا وهو في الصلاة، فظن أنها عقرب، فأهوى إليها برجله فإذا هي','180','0','0');
INSERT INTO [t29701] VALUES (180,'يصلي على راحلته أينما توجهت به','181','0','0');
INSERT INTO [t29701] VALUES (181,'يأتي قباء ماشيا وراكبا','182','0','0');
INSERT INTO [t29701] VALUES (182,'يلبس المحرم ثوبا مصبوغا بورس أو بزعفران','183','0','0');
INSERT INTO [t29701] VALUES (183,'من لم يجد نعلين فليلبس خفين، وليقطع أسفل من الكعبين','184','0','0');
INSERT INTO [t29701] VALUES (184,'الضب , فقال: لا آكله ولا أحرمه','185','0','0');
INSERT INTO [t29701] VALUES (185,'من اقتنى كلبا إلا كلب صيد أو ماشية انتقص من عمله كل يوم قيراطان','186','0','0');
INSERT INTO [t29701] VALUES (186,'من اشترى طعاما فلا يبعه حتى يقبضه','187','0','0');
INSERT INTO [t29701] VALUES (187,'من ابتاع عبدا وله مال فماله للبائع، إلا أن يشترط المبتاع','188','0','0');
INSERT INTO [t29701] VALUES (188,'إني رجل أخدع في البيع، قال: إذا بعت فقل: لا خلابة.','189','0','0');
INSERT INTO [t29701] VALUES (189,'بيع الولاء وعن هبته','190','0','0');
INSERT INTO [t29701] VALUES (190,'كنا نتقي الكلام عند الانبساط إلى النساء مخافة أن ينزل فينا القرآن، فلما توفي رسول الله','191','0','0');
INSERT INTO [t29701] VALUES (191,'يتناجى اثنان دون الثالث','192','0','0');
INSERT INTO [t29701] VALUES (192,'يبايعنا على السمع والطاعة، ويلقنهم فيما استطعتم','193','0','0');
INSERT INTO [t29701] VALUES (193,'أقر بالسمع والطاعة لعبد الله عبد الملك أمير المؤمنين، على سنة الله وسنة رسوله فيما','194','0','0');
INSERT INTO [t29701] VALUES (194,'أتؤذيك هوام رأسك؟ قلت: نعم.','195','0','0');
INSERT INTO [t29701] VALUES (195,'أصدق بجلال البدن التي نحرت أو بجلودها','196','0','0');
INSERT INTO [t29701] VALUES (196,'نبيع أمهات الأولاد على عهد رسول الله صلى الله عليه','197','0','0');
INSERT INTO [t29701] VALUES (197,'من أسلف فليسلف في كيل معلوم ووزن معلوم','198','0','0');
INSERT INTO [t29701] VALUES (198,'ما من رجل يذنب ذنبا، فيتوضأ فيحسن الوضوء، ثم يصلي ركعتين، ويستغفر الله إلا غفر','199','0','0');
INSERT INTO [t29701] VALUES (199,'أرحنا بالصلاة','200','0','0');
