INSERT INTO [t29704] VALUES (1,'هذا ابن أخي عهد إلي أنه ابنه، وقال عبد بن زمعة: يا رسول الله هذا أخي ابن زمعة , وولد على','2','0','0');
INSERT INTO [t29704] VALUES (2,'الولد للفراش وللعاهر الحجر.','3','0','0');
INSERT INTO [t29704] VALUES (3,'الله تعالى يعذب الذين يعذبون الناس في الدنيا','4','0','0');
INSERT INTO [t29704] VALUES (4,'أنكح أختي ابنة أبي سفيان. قالت: فقال لي رسول الله صلى الله عليه وسلم: أوتحبين ذلك؟ قالت:','5','0','0');
INSERT INTO [t29704] VALUES (5,'يقوم على باب حجرتي والحبشة يلعبون بالحراب في المسجد , وإنه ليسترني بردائه، لكي أنظر إلى','6','0','0');
INSERT INTO [t29704] VALUES (6,'فإن أحب الناس إلي غنى بعدي لأنت، وإن أعز الناس علي فقرا بعدي لأنت، وإني كنت نحلتك جداد','7','0','0');
INSERT INTO [t29704] VALUES (7,'{حتى إذا استيأس الرسل وظنوا أنهم قد كذبوا} [يوسف: ١١٠] أكذبوا أم كذبوا؟ قالت: بل كذبوا.','8','0','0');
INSERT INTO [t29704] VALUES (8,'{وإن خفتم ألا تقسطوا في اليتامى فانكحوا ما طاب لكم من النساء مثنى وثلاث ورباع فإن خفتم','9','0','0');
INSERT INTO [t29704] VALUES (9,'أفلح أخا أبي القعيس استأذن علي فأبيت أن آذن له حتى أستأذنك في ذلك. فقال لي رسول الله صلى','10','0','0');
INSERT INTO [t29704] VALUES (10,'فأخطأ المسلمون يومئذ بأبيه يحسبونه من العدو فتراشقوه بأسيافهم، فطفق حذيفة يقول: أبي أبي.','11','0','0');
INSERT INTO [t29704] VALUES (11,'من تبنى رجلا في الجاهلية دعاه الناس إليه وورث من ميراثه، حتى أنزل الله تعالى في ذلك:','12','0','0');
INSERT INTO [t29704] VALUES (12,'ما من مصيبة تصيب المسلم إلا كفر الله عنه بها حتى الشوكة يشاكها','13','0','0');
INSERT INTO [t29704] VALUES (13,'إني كنت تحت رفاعة فطلقني فبت طلاقي، فتزوجت بعده عبد الرحمن بن الزبير وإنه ما معه يا رسول','14','0','0');
INSERT INTO [t29704] VALUES (14,'حرموا من الرضاعة ما تحرمون من النسب','15','0','0');
INSERT INTO [t29704] VALUES (15,'لا إله إلا الله، ويل للعرب من شر قد اقترب، فتح اليوم من ردم يأجوج ومأجوج مثل هذه، وحلق','16','0','0');
INSERT INTO [t29704] VALUES (16,'إنما أنا بشر، وإنه يأتيني الخصم، فلعل بعضهم أن يكون أبلغ من بعض فأقضي له بذلك وأحسب أنه','17','0','0');
INSERT INTO [t29704] VALUES (17,'لم يقبض نبي قط حتى يرى مقعده من الجنة ثم يحيى، فلما اشتكى وحضره القبض ورأسه على فخذ عائشة','18','0','0');
INSERT INTO [t29704] VALUES (18,'اغسلوا ثوبي هذين اللذين كان يلبس، فكفنوني فيهما , فإن الحي هو أفقر إلى الجديد من','19','0','0');
INSERT INTO [t29704] VALUES (19,'سمع الله لمن حمده ربنا ولك الحمد، ثم يقول وهو قائم قبل أن يسجد: اللهم أنج الوليد بن','20','0','0');
INSERT INTO [t29704] VALUES (20,'تفضل صلاة الجمع صلاة أحدكم وحده بخمسة وعشرين جزءا , وتجتمع ملائكة الليل وملائكة النهار في','21','0','0');
INSERT INTO [t29704] VALUES (21,'لأصومن النهار ولأقومن الليل ما عشت له. فقلت له: قد قلت بأبي أنت وأمي، قال: فإنك لا تستطيع','22','0','0');
INSERT INTO [t29704] VALUES (22,'مثل المجاهد في سبيل الله , والله أعلم بمن يجاهد في سبيله كمثل الصائم القائم، وتوكل الله','23','0','0');
INSERT INTO [t29704] VALUES (23,'لولا أن رجالا من المؤمنين لا تطيب أنفسهم أن يتخلفوا عني ولا أجد ما أحملهم عليه ما تخلفت','24','0','0');
INSERT INTO [t29704] VALUES (24,'أمرت أن أقاتل الناس حتى يقولوا لا إله إلا الله، فمن قال: لا إله إلا الله , فقد عصم مني','25','0','0');
INSERT INTO [t29704] VALUES (25,'ما من بني آدم من مولود إلا يمسه الشيطان حين يولد فيستهل صارخا من مس الشيطان، غير مريم','26','0','0');
INSERT INTO [t29704] VALUES (26,'لا تقوم الساعة حتى تضطرب أليات نساء دوس على ذي الخلصة.','27','0','0');
INSERT INTO [t29704] VALUES (27,'المقسطون في الدنيا على منابر من نور يوم القيامة بين يدي الرحمن لما أقسطوا له في','28','0','0');
INSERT INTO [t29704] VALUES (28,'هل نرى ربنا يوم القيامة؟ فقال رسول الله صلى الله عليه وسلم: هل تمارون في القمر ليلة البدر','29','0','0');
INSERT INTO [t29704] VALUES (29,'لا يدخل الجنة إلا مؤمن، إن الله تعالى يؤيد هذا الدين بالرجل الفاجر','30','0','0');
INSERT INTO [t29704] VALUES (30,'لا إله إلا الله , كلمة أحاج لك بها عند الله. قال أبو جهل وعبد الله بن أبي أمية: أترغب عن','31','0','0');
INSERT INTO [t29704] VALUES (31,'يدخل الجنة من أمتي زمرة هي سبعون ألفا , تضيء وجوههم إضاءة القمر ليلة البدر. فقام عكاشة بن','32','0','0');
INSERT INTO [t29704] VALUES (32,'رأيت عمرا الخزاعي يجر قصبه في النار، وكان أول من سيب السوائب. وذكر الحديث حدثنا أبو','33','0','0');
INSERT INTO [t29704] VALUES (33,'إنه لن يبسط أحد ثوبه حتى أقضي مقالتي هذه ثم يجمع إليه ثوبه، إلا وعى ما','34','0','0');
INSERT INTO [t29704] VALUES (34,'{وأنذر عشيرتك الأقربين} [الشعراء: ٢١٤] .','35','0','0');
INSERT INTO [t29704] VALUES (35,'جاء أهل اليمن هم أرق أفئدة وأضعف قلوبا، الإيمان يمان، والحكمة يمانية، والسكينة في أهل','36','0','0');
INSERT INTO [t29704] VALUES (36,'يأتي الشيطان أحدكم في صلاته فيلبس عليه حتى لا يدري كم صلى، فإذا وجد أحدكم ذلك فليسجد','37','0','0');
INSERT INTO [t29704] VALUES (37,'توضئوا مما مسته النار','38','0','0');
INSERT INTO [t29704] VALUES (38,'عن الوصال، فقال له رجل من المسلمين: فإنك يا رسول الله تواصل. فقال: وأيكم مثلي , إني أبيت','39','0','0');
INSERT INTO [t29704] VALUES (39,'لكل نبي دعوة، فأريد إن شاء الله أن أختبئ دعوتي شفاعة لأمتي يوم القيامة','40','0','0');
INSERT INTO [t29704] VALUES (40,'اللهم أيده بروح القدس.','41','0','0');
INSERT INTO [t29704] VALUES (41,'لا تخيروني على موسى، فإن الناس يصعقون فأكون أول من يفيق، فإذا موسى باطش بجانب العرش فلا','42','0','0');
INSERT INTO [t29704] VALUES (42,'حين أراد قدوم مكة: منزلنا غدا إن شاء الله بخيف بني كنانة حيث تقاسموا على','43','0','0');
INSERT INTO [t29704] VALUES (43,'هذا جبريل عليه السلام وهو يقرأ عليك السلام. فقلت: وعليه السلام ورحمة','44','0','0');
INSERT INTO [t29704] VALUES (44,'إنما يشد الرحل إلى ثلاثة مساجد: إلى المسجد الحرام ومسجدكم هذا وإيلياء','45','0','0');
INSERT INTO [t29704] VALUES (45,'ينزل ربنا كل ليلة حين يبقى ثلث الليل الآخر إلى السماء الدنيا فيقول: من يدعوني فأستجيب له؟','46','0','0');
INSERT INTO [t29704] VALUES (46,'سيكون فتن القاعد فيها خير من القائم، والقائم فيها خير من الماشي، والماشي فيها خير من','47','0','0');
INSERT INTO [t29704] VALUES (47,'لا تقوم الساعة حتى تقتتل فئتان دعواهما واحد','48','0','0');
INSERT INTO [t29704] VALUES (48,'من أطاعني فقد أطاع الله، ومن عصاني فقد عصى الله، ومن أطاع أميري فقد أطاعني، ومن عصا أميري','49','0','0');
INSERT INTO [t29704] VALUES (49,'الفخر والخيلاء في الفدادين أهل الوبر، والسكينة في أهل الغنم، والإيمان يمان والحكمة','50','0','0');
INSERT INTO [t29704] VALUES (50,'واقف بالحزورة في سوق مكة: إنك لخير أرض الله , وأحب أرض الله إلى الله، ولولا أني أخرجت منك','51','0','0');
INSERT INTO [t29704] VALUES (51,'أحدثكم بخير دور الأنصار؟ قالوا: نعم يا رسول الله. قال رسول الله صلى الله عليه وسلم: بنو','52','0','0');
INSERT INTO [t29704] VALUES (52,'اشتكت النار إلى ربها تعالى فقالت: يا رب أكل بعضي بعضا، فأذن لها بنفسين: نفس في الشتاء','53','0','0');
INSERT INTO [t29704] VALUES (53,'والله إني لأستغفر وأتوب في اليوم أكثر من سبعين مرة','54','0','0');
INSERT INTO [t29704] VALUES (54,'اللهم ارحمني ومحمدا ولا ترحم معنا أحدا. فلما سلم رسول الله صلى الله عليه وسلم , قال','55','0','0');
INSERT INTO [t29704] VALUES (55,'بينا راع في غنمه عدا عليه الذئب، فقال: من لها يوم السبع، يوم ليس لها راع غيري. وبينا رجل','56','0','0');
INSERT INTO [t29704] VALUES (56,'أنا الرحمن، وأنا خلقت الرحم وشققت لها من اسمي، فمن وصلها وصلته ومن قطعها قطعته','57','0','0');
INSERT INTO [t29704] VALUES (57,'عليكم بهذه الحبة السوداء , فإن فيها شفاء من كل شيء إلا من السام.','58','0','0');
INSERT INTO [t29704] VALUES (58,'إني ذاكر لك أمرا فلا عليك ألا تستعجلي حتى تستأمري أبويك. قالت: وقد علم أن أبوي لم يكونا','59','0','0');
INSERT INTO [t29704] VALUES (59,'اعدل. فقال: ويحك! ومن يعدل إذا لم أعدل، فقد خبت وخسرت إذا لم أعدل. فقال عمر بن الخطاب: يا','60','0','0');
INSERT INTO [t29704] VALUES (60,'كل شراب أسكر حرام','61','0','0');
INSERT INTO [t29704] VALUES (61,'إن الآخر زنا , يعني نفسه , فأعرض عنه رسول الله صلى الله عليه وسلم، فتنحى بشق وجهه الذي','62','0','0');
INSERT INTO [t29704] VALUES (62,'سجي ببرد حبرة.','63','0','0');
INSERT INTO [t29704] VALUES (63,'قضى أنه من أعمر رجلا عمرى له ولعقبه فإنها للذي أعمرها , قد بتها من صاحبها الذي أعطاها ,','64','0','0');
INSERT INTO [t29704] VALUES (64,'جعل الله الرحمة مائة جزء، فأمسك عنده تسعا وتسعين وأنزل في الأرض جزءا واحدا , فمن ذلك','65','0','0');
INSERT INTO [t29704] VALUES (65,'ليلة أسري به بإيلياء بقدحين خمر ولبن، فنظر إليهما ثم أخذ اللبن.','66','0','0');
INSERT INTO [t29704] VALUES (66,'قفلنا مع رسول الله صلى الله عليه وسلم من غزوة خيبر، فلما بدا له أحد قال رسول الله صلى','67','0','0');
INSERT INTO [t29704] VALUES (67,'إذا اشتد الحر فأبردوا عن الظهر , فإن شدة الحر من فيح جهنم','68','0','0');
INSERT INTO [t29704] VALUES (68,'إذا أقيمت الصلاة فلا تأتوها تسعون، وائتوها تمشون وعليكم السكينة، فما أدركتم فصلوا وما','69','0','0');
INSERT INTO [t29704] VALUES (69,'مثل المهجر إلى الصلاة كمثل الذي يهدي البدنة، ثم الذي على أثره كالذي يهدي البقرة، ثم الذي','70','0','0');
