INSERT INTO [t29707] VALUES (121,'لله عز وجل ملائكة سيارة يتبعون مجالس الذكر , فإذا وجدوا مجلس ذكر جلسوا معهم , فإذا تفرقوا','122','0','0');
INSERT INTO [t29707] VALUES (122,'مر رسول الله صلى الله عليه وسلم , بيهودي يبكى عليه , فقال: إن بكاء أهله عليه ليزيده','123','0','0');
INSERT INTO [t29707] VALUES (123,'الفأرة تقع في السمن فتموت فيه , قال: إن كان مائعا فأهرقوه , وإن كان جامدا ألقوها وما','124','0','0');
INSERT INTO [t29707] VALUES (124,'نعم الرجل أبو بكر , نعم الرجل عمر','125','0','0');
INSERT INTO [t29707] VALUES (125,'يفتتح الصلاة ب {بسم الله الرحمن الرحيم} [الفاتحة: ١] ؟ قال: إنك لتسألني عن شيء ما سألني','126','0','0');
INSERT INTO [t29707] VALUES (126,'ما كان النبي صلى الله عليه وسلم يصنع في بيته , قالت: يرقع ثوبه ويخصف','127','0','0');
INSERT INTO [t29707] VALUES (127,'إذا قضى صلاته من آخر الليل تطوعا , فإن كنت مستيقظة حدثني , وإن كنت نائمة اضطجع حتى يأتيه','128','0','0');
INSERT INTO [t29707] VALUES (128,'إن فتنة قد حضرت.','129','0','0');
INSERT INTO [t29707] VALUES (129,'إنها ستكون فتنة واختلاف. أو قال: اختلاف وفتنة , فقلنا: يا رسول الله ما تأمرنا؟ قال: عليكم','130','0','0');
INSERT INTO [t29707] VALUES (130,'امرأة من يهود خيبر أهدت لرسول الله صلى الله عليه وسلم شاة مسمومة , ثم علم أنها مسمومة ,','131','0','0');
INSERT INTO [t29707] VALUES (131,'لبى: لبيك اللهم لبيك , لبيك لا شريك لك لبيك , إن الحمد والنعمة لك والملك , لا شريك','132','0','0');
INSERT INTO [t29707] VALUES (132,'ما فعل النفر السود الجعاد القطاط؟ قال: قلت: هؤلاء منا وقد تخلفوا , قال: فما فعل الحمر','133','0','0');
INSERT INTO [t29707] VALUES (133,'لكل نبي دعوة , وإني أخرت دعوتي شفاعة لأمتي يوم القيامة','134','0','0');
INSERT INTO [t29707] VALUES (134,'يدخل بشفاعة رجل من أمتي مثل أحد الحيين: ربيعة ومضر , قال: قيل: يا رسول الله أوما ربيعة من','135','0','0');
INSERT INTO [t29707] VALUES (135,'كل مسكر حرام','136','0','0');
INSERT INTO [t29707] VALUES (136,'لا يدخل الجنة إلا مؤمن , وأيام التشريق أيام أكل وشرب','137','0','0');
INSERT INTO [t29707] VALUES (137,'{قل أعوذ برب الفلق} [الفلق: ١] . حتى ختمها {قل أعوذ برب الناس} [الناس: ١]','138','0','0');
INSERT INTO [t29707] VALUES (138,'ليبيتن أناس من أمتي على أشر وبطر ولعب ولهو , فيصبحون قردة وخنازير , باستحلالهم الحرام ,','139','0','0');
INSERT INTO [t29707] VALUES (139,'البائع والمبتاع بالخيار حتى يتفرقا , إلا أن تكون صفقة خيار , ولا تحل له أن يفارقه خشية أن','140','0','0');
INSERT INTO [t29707] VALUES (140,'إذا افتتح قائما ركع قائما , وإذا افتتح جالسا ركع جالسا','141','0','0');
INSERT INTO [t29707] VALUES (141,'أنه يقبض في وجعه هذا فبكيت , ثم أخبرني أني أول أهله لحاقا به فضحكت','142','0','0');
INSERT INTO [t29707] VALUES (142,'طيبت رسول الله صلى الله عليه قبل أن يحرم','143','0','0');
INSERT INTO [t29707] VALUES (143,'صلى رسول الله صلى الله عليه بين مكة والمدينة ركعتين لا يخاف إلا الله','144','0','0');
INSERT INTO [t29707] VALUES (144,'إذا دخل أهل الجنة الجنة وأهل النار النار , قال الله: من كان في قلبه حبة خير فأخرجوه.','145','0','0');
INSERT INTO [t29707] VALUES (145,'إن الشونيز ينفع بإذن الله من كل داء إلا الموت','146','0','0');
INSERT INTO [t29707] VALUES (146,'ما اسمك؟ قال: حزن , قال: بل أنت سهل , قال: يا رسول الله اسم سمانيه أبواي وعرفت به في','147','0','0');
INSERT INTO [t29707] VALUES (147,'ما شبع رسول الله صلى الله عليه وسلم من طعام ثلاثة أيام حتى فارق الدنيا','148','0','0');
INSERT INTO [t29707] VALUES (148,'اطلبوا الحوائج عند حسان الوجوه','149','0','0');
INSERT INTO [t29707] VALUES (149,'يصبح جنبا من قراف غير حلم , فيغتسل , ويصلي ويصوم','150','0','0');
INSERT INTO [t29707] VALUES (150,'يصلي على الجنائز فيكبر أربعا , فكبر يوما خمسا , فسألناه عن ذلك , فقال: كان رسول الله صلى','151','0','0');
INSERT INTO [t29707] VALUES (151,'استقيموا ولن تحصوا , واعلموا أن خير أعمالكم الصلاة , ولا يحافظ على الوضوء إلا','152','0','0');
INSERT INTO [t29707] VALUES (152,'يوتر ب سبح اسم ربك الأعلى , وقل يا أيها الكافرون , وقل هو الله أحد , وإذا سلم قال:','153','0','0');
INSERT INTO [t29707] VALUES (153,'الله عز وجل إذا أنعم على عبد نعمة في السراء أحب أن ترى نعمته عليه','154','0','0');
INSERT INTO [t29707] VALUES (154,'بعثني الله بالإسلام. قلت: وما الإسلام؟ قال: أن تقول أسلمت نفسي لله , وخليت وجهي لله ,','155','0','0');
INSERT INTO [t29707] VALUES (155,'العائد في هبته , كالعائد في قيئه','156','0','0');
INSERT INTO [t29707] VALUES (156,'الرجل يجد البلة لا تنقطع عنه؟ قال: يغسل فرجه , ثم يتوضأ وضوءه للصلاة , ثم ينضح في','157','0','0');
INSERT INTO [t29707] VALUES (157,'دخل القصر فبال , ثم خرج إلى مطهرة على باب المسجد فتوضأ , ومسح على جوربيه ونعليه , ثم دخل','158','0','0');
INSERT INTO [t29707] VALUES (158,'بعثني رسول الله صلى الله عليه وسلم إلى رجل تزوج امرأة أبيه آتيه برأسه','159','0','0');
INSERT INTO [t29707] VALUES (159,'كان موسى إذا اغتسل اعتزل وحده , فقالت بنو إسرائيل أو من قال منهم: ما يفعل ذلك إلا أنه آدر','160','0','0');
INSERT INTO [t29707] VALUES (160,'{غير المغضوب عليهم ولا الضالين} [الفاتحة: ٧] قال: رب اغفر لي آمين','161','0','0');
INSERT INTO [t29707] VALUES (161,'ما ذئبان جائعان في حظيرة وثيقة يأكلان ويفرسان بأسرع من حب الشرف وحب المال في دين المرء','162','0','0');
INSERT INTO [t29707] VALUES (162,'من أراد سخط الله ورضا الناس عاد حامده من الناس ذاما','163','0','0');
INSERT INTO [t29707] VALUES (163,'انكسفت الشمس , فقام علي، فركع خمس ركعات وسجد سجدتين , ثم فعل الثانية مثل ذلك , ثم سلم ,','164','0','0');
INSERT INTO [t29707] VALUES (164,'طيبته يعني النبي صلى الله عليه وسلم حين أراد أن يهل بأطيب الطيب , قدرت عليه من','165','0','0');
INSERT INTO [t29707] VALUES (165,'لا تتخذوا الضيعة فترغبوا في الدنيا.','166','0','0');
INSERT INTO [t29707] VALUES (166,'من لا يرحم الناس لا يرحمه الله','167','0','0');
INSERT INTO [t29707] VALUES (167,'كانت تحته أم كلثوم بنت عقبة , فقالت له وهي حامل: طيب نفسي بتطليقة , فطلقها تطليقة , ثم','168','0','0');
INSERT INTO [t29707] VALUES (168,'لا يبغض الأنصار رجل يؤمن بالله واليوم الآخر','169','0','0');
INSERT INTO [t29707] VALUES (169,'ما أحد أصبر على أذى يسمعه من الله عز وجل , يدعون له ولدا , وهو يعافيهم','170','0','0');
INSERT INTO [t29707] VALUES (170,'أيما عبد أبق فقد برئت منه الذمة','171','0','0');
INSERT INTO [t29707] VALUES (171,'إذا عوذ المريض قال: أذهب البأس رب الناس , اشف أنت الشافي , لا شفاء إلا شفاؤك , شفاء لا','172','0','0');
INSERT INTO [t29707] VALUES (172,'من يحرم الرفق يحرم الخير','173','0','0');
INSERT INTO [t29707] VALUES (173,'من جر ثيابه مخيلة لم ينظر الله إليه يوم القيامة','174','0','0');
INSERT INTO [t29707] VALUES (174,'من سره أن يقرأ القرآن غضا كما أنزل فليقرأه على قراءة ابن أم عبد، سل','175','0','0');
INSERT INTO [t29707] VALUES (175,'تعلمنا التشهد , وتشير بيدها وتقول: التحيات لله , الصلوات والطيبات الزاكيات , السلام علينا','176','0','0');
INSERT INTO [t29707] VALUES (176,'لقد برأ الله أهل هذه المدينة من الشرك , ولكن أخاف أن تضلهم النجوم , قالوا: يا رسول الله','177','0','0');
INSERT INTO [t29707] VALUES (177,'رأيت في يدي سوارين من ذهب فشقا علي , فقيل: انفخهما , فنفختهما , فطارا فأولتهما ,','178','0','0');
INSERT INTO [t29707] VALUES (178,'صلاة المسافر في السفر وصلاة الجمعة وصلاة الأضحى وصلاة الفطر ركعتان , تمام على لسان نبيكم','179','0','0');
INSERT INTO [t29707] VALUES (179,'إذا بعث أوصاهم أن يتقوا الله عز وجل.','180','0','0');
INSERT INTO [t29707] VALUES (180,'يتمتع , ويأمر بالمتعة , ويخبر أن رسول الله صلى الله عليه وسلم قد فعل , قال: فقال له رجل:','181','0','0');
INSERT INTO [t29707] VALUES (181,'ليس بالجعد ولا بالسبط , ولا بالقصير ولا بالطويل , وكان شعره إلى أنصاف أذنيه صلى الله عليه','182','0','0');
INSERT INTO [t29707] VALUES (182,'آخى بين الزبير وعبد الله بن مسعود','183','0','0');
INSERT INTO [t29707] VALUES (183,'إنهما ليعذبان , إن صاحبي هذين القبرين يعذبان , فائتياني بجريدة , فاستبقنا فسبقت صاحبي ,','184','0','0');
INSERT INTO [t29707] VALUES (184,'إن هذه الدرجات العلا ليراهم من تحتهم كما يرى الكوكب الدري بالأفق من أفاق السماء , وإن أبا','185','0','0');
INSERT INTO [t29707] VALUES (185,'المستبان ما قالا من شيء , فهو على البادئ حتى يعتدي المظلوم','186','0','0');
INSERT INTO [t29707] VALUES (186,'إذا تقارب الزمان لا تكاد رؤيا المؤمن تكذب , وأصدقهم رؤيا أصدقهم حديثا','187','0','0');
INSERT INTO [t29707] VALUES (187,'خير الصفوف المقدم , وشرها المؤخر','188','0','0');
INSERT INTO [t29707] VALUES (188,'علي أقضانا , وأبي أقرؤنا , وإنا لندع بعض ما يقول أبي , وأبي يقول: سمعته من رسول الله صلى','189','0','0');
INSERT INTO [t29707] VALUES (189,'قتل الجنان في البيوت','190','0','0');
INSERT INTO [t29707] VALUES (190,'ما كان في رأس رسول الله صلى الله عليه وسلم ولحيته عشرون طاقة بيضاء','191','0','0');
INSERT INTO [t29707] VALUES (191,'من باع عبدا وله مال فماله للبائع إلا أن يشترط المبتاع , ومن باع نخلا مؤبرا , فثمرتها','192','0','0');
INSERT INTO [t29707] VALUES (192,'كتب إليه كتابا , فرقع به دلوه , فقالت له ابنته: عمدت إلى كتاب سيد العرب فرقعت به دلوك ,','193','0','0');
INSERT INTO [t29707] VALUES (193,'ذكر عبد الله بن مسعود , فقال: والله لقد رأيته عبدا لآل محمد صلى الله عليه','194','0','0');
INSERT INTO [t29707] VALUES (194,'صل علي وعلى زوجي , فقال: صلى الله عليك وعلى زوجك.','195','0','0');
INSERT INTO [t29707] VALUES (195,'من عرض عليه طيب فلا يرده؛ فإنه طيب الرائحة خفيف الحمل','196','0','0');
INSERT INTO [t29707] VALUES (196,'يقرأ في الفجر يس وحم , ونحو ذلك','197','0','0');
INSERT INTO [t29707] VALUES (197,'مفتاح الجنة الصلاة , ومفتاح الصلاة الوضوء','198','0','0');
INSERT INTO [t29707] VALUES (198,'كل أمر ذي بال لا يبدأ فيه بالحمد لله أقطع','199','0','0');
INSERT INTO [t29707] VALUES (199,'الله عز وجل نظر في قلوب العباد فوجد قلب محمد صلى الله عليه وسلم خير قلوب العباد , فاصطفاه','200','0','0');
INSERT INTO [t29707] VALUES (200,'وصية عبد الله بن. . . . . قال: يرحمه الله لقد أتعب من بعده حدثنا إسحاق بن إبراهيم بن سنين','201','0','0');
INSERT INTO [t29707] VALUES (201,'الرجل يكون فينا رجل سوء , فيشرب الشراب فيموت , أيصلى عليه؟ قال: ما لي من تكلوا أخباركم ,','202','0','0');
INSERT INTO [t29707] VALUES (202,'كانت رسالة يونس بعدما نبذه الحوت ثنا ابن أبي العوام , قال: ثنا أزهر بن سعد , عن ابن عون ,','203','0','0');
INSERT INTO [t29707] VALUES (203,'خرجنا بأكلب لنا , فاستقبلنا عبد الله بن عمر , فقال: إذا أرسلتموها فقولوا: بسم الله اللهم','204','0','0');
INSERT INTO [t29707] VALUES (204,'من كان منكم محتجما فليحتجم يوم السبت','205','0','0');
