INSERT INTO [t29708] VALUES (1,'وضع الله الحرج إلا امرأ اقترض من عرض أخيه شيئا فذاك الذي حرج، فقالوا: هل علينا جناح أن','2','0','0');
INSERT INTO [t29708] VALUES (2,'ما خير ما أعطي العبد؟ قال: خلق حسن','3','0','0');
INSERT INTO [t29708] VALUES (3,'النصح لكل مسلم فأنا لكم ناصح','4','0','0');
INSERT INTO [t29708] VALUES (4,'يقرأ في الصبح: {والنخل باسقات لها طلع نضيد} [ق: ١٠]','5','0','0');
INSERT INTO [t29708] VALUES (5,'إني لأرى في وجه أبي حذيفة من دخول سالم علي. قال: فأرضعيه. قالت: وهو شيخ كبير، قال: أولست','6','0','0');
INSERT INTO [t29708] VALUES (6,'طيبت رسول الله بيدي هاتين لحرمه حين أحرم , ولحله قبل أن يطوف بالبيت، قالت: ولا أعلم أن','7','0','0');
INSERT INTO [t29708] VALUES (7,'فأمرها أن تجمع الظهر والعصر بغسل واحد , والمغرب والعشاء بغسل، وتغتسل للصبح غسلا وتدع','8','0','0');
INSERT INTO [t29708] VALUES (8,'أيتوضأ أحدنا ورجلاه في الخفين؟ قال: نعم إذا أدخلتهما وهما طاهرتان','9','0','0');
INSERT INTO [t29708] VALUES (9,'يأتي مسجد قباء كل سبت يصلي فيه','10','0','0');
INSERT INTO [t29708] VALUES (10,'لا تدخلوا على هؤلاء القوم إلا أن تكونوا باكين، فإن لم تكونوا باكين فلا تدخلوا عليهم، فإني','11','0','0');
INSERT INTO [t29708] VALUES (11,'حين أراد أن يوسع المسجد أراد أن يأخذ من العباس داره، فقال: لا أبيعها. قال: إذا آخذها منك.','12','0','0');
INSERT INTO [t29708] VALUES (12,'اعتد عليهم بالغذاء وإن جاء بها الراعي يحملها على يده وأخبرهم أنك تدع لهم الشاة الماخض','13','0','0');
INSERT INTO [t29708] VALUES (13,'أخرجوا المشركين من جزيرة العرب، وأجيزوا الوفد بنحو ما كنت أجيزهم. والثالثة خير إما أن سكت','14','0','0');
INSERT INTO [t29708] VALUES (14,'إن الرجل يتزوج المرأة. . . . .','15','0','0');
INSERT INTO [t29708] VALUES (15,'. . . . تلزمون فيه','16','0','0');
INSERT INTO [t29708] VALUES (16,'انهشوا اللحم نهشا فإنه أهنا. . . . .','17','0','0');
INSERT INTO [t29708] VALUES (17,'يغسل يديه ثلاثا قبل أن يدخلها الإناء , ويغسل وجهه ويستنشق ويتمضمض ثلاثا ثلاثا , ويغسل يده','18','0','0');
INSERT INTO [t29708] VALUES (18,'أكل لحما فصلى ولم يتوضأ','19','0','0');
INSERT INTO [t29708] VALUES (19,'يوشك أن يكون خير مال المسلم غنما يتبع بها شعف الجبال ومواقع القطر، يفر بدينه من','20','0','0');
INSERT INTO [t29708] VALUES (20,'إذا كنت بالبواد فارفع صوتك بالآذان، فإني سمعت رسول الله يقول: لا يسمعه شيء إلا شهد لربه','21','0','0');
INSERT INTO [t29708] VALUES (21,'نهى أن يبيع حاضر لباد','22','0','0');
INSERT INTO [t29708] VALUES (22,'لا يبيعن حاضر لباد','23','0','0');
INSERT INTO [t29708] VALUES (23,'أمرت جدتي أم كلثوم بنت عقبة بشاة فسلخت ثم ألبس مسكها، فهل ذلك إلا من ضرب','24','0','0');
INSERT INTO [t29708] VALUES (24,'يوم عرفة , وهو على المنبر , فقال: ما يمنعك أن تهل فقد سمعت عمر يهل مكانك هذا. فأهل ابن','25','0','0');
INSERT INTO [t29708] VALUES (25,'من صلى على جنازة فله قيراط','26','0','0');
INSERT INTO [t29708] VALUES (26,'{فخانتاهما} [التحريم: ١٠] .','27','0','0');
INSERT INTO [t29708] VALUES (27,'يقرؤها: {إنه عمل غير صالح} [هود: ٤٦]','28','0','0');
INSERT INTO [t29708] VALUES (28,'رجلا ظاهر من امرأته، فرأى بريق ساقها في القمر فوقع عليها , فأتى النبي فأمره أن','29','0','0');
INSERT INTO [t29708] VALUES (29,'إن أناسا يدخلون النار ثم يخرجون فيدخلون الجنة','30','0','0');
INSERT INTO [t29708] VALUES (30,'لما نزلت على النبي: {هو القادر على أن يبعث عليكم عذابا من فوقكم} [الأنعام: ٦٥] . قال:','31','0','0');
INSERT INTO [t29708] VALUES (31,'أتى النبي قبر عبد الله بن أبي، بعدما أدخل حفرته، فأمر به فأخرج، فوضعه على ركبتيه أو فخذيه','32','0','0');
INSERT INTO [t29708] VALUES (32,'فطلبت الأنصار ثوبا يكسونه فلم يجدوا قميصا يصلح عليه إلا قميص عبد الله بن أبي فكسوه','33','0','0');
INSERT INTO [t29708] VALUES (33,'رجل طاف بالبيت معتمرا أيأتي أهله قبل أن يطوف بين الصفا والمروة؟ فقال: لا أقربها حتى أطوف','34','0','0');
INSERT INTO [t29708] VALUES (34,'كان معاذ يصلي معك ثم يرجع فيؤمنا , وإنك أخرت الصلاة البارحة فجاء فأمنا فقرأ بسورة البقرة','35','0','0');
INSERT INTO [t29708] VALUES (35,'فألقى لنا البحر حوتا فأكلنا منه نصف شهر، وايتدمنا منه وادهنا بودكه حتى ثابت','36','0','0');
INSERT INTO [t29708] VALUES (36,'يأتي على الناس زمان يغزون فيه فئاما من الناس، فيقال: هل فيكم من صحب رسول الله؟ فيقال:','37','0','0');
INSERT INTO [t29708] VALUES (37,'اصطبح الناس الخمر يوم أحد ثم قتلوا شهداء.','38','0','0');
INSERT INTO [t29708] VALUES (38,'من شك في صلاته فلم يدر أثلاثا صلى أم أربعا، فليتحر أصوب ذلك في نفسه، ويسجد سجدتين وهو','39','0','0');
INSERT INTO [t29708] VALUES (39,'فجاء حين زالت الشمس فرمى، ثم تقدم أمامه فقام مقاما طويلا، ثم أتى الأخرى فرماها وقام عن','40','0','0');
INSERT INTO [t29708] VALUES (40,'قدم رسول الله فطاف بالبيت سبعا , وصلى خلف المقام ركعتين , وسعى بين الصفا والمروة سبعا و','41','0','0');
INSERT INTO [t29708] VALUES (41,'طاف بعد العصر ولا يدري أصلى أم لا','42','0','0');
INSERT INTO [t29708] VALUES (42,'لما وقع في عين ابن عباس الماء، أراد أن يتعالج منه، فقيل له: إنك تمكث كذا وكذا لا تصلي إلا','43','0','0');
INSERT INTO [t29708] VALUES (43,'{فليس عليهن جناح أن يضعن ثيابهن} [النور: ٦٠] .','44','0','0');
INSERT INTO [t29708] VALUES (44,'قرأ ابن عباس لتركبن طبقا عن طبق','45','0','0');
INSERT INTO [t29708] VALUES (45,'يقرأ: ٠ وما أرسلنا من قبلك من رسول ولا نبي ٠. ولا محدث عن عمرو، عن أبي الشعثاء جابر بن','46','0','0');
INSERT INTO [t29708] VALUES (46,'يرمل جار إليه غير محرم إلى المواقيت','47','0','0');
INSERT INTO [t29708] VALUES (47,'لو نزل أهل البصرة عند قول جابر بن زيد لأوسعهم عما في كتاب الله علما','48','0','0');
INSERT INTO [t29708] VALUES (48,'عن الغلام شاتان مكافأتان، وعن الجارية شاة','49','0','0');
INSERT INTO [t29708] VALUES (49,'إذا فوض إلى الرجل أمر امرأته فتزوجها ولم يفرض لها صداقا فليس لها إلا','50','0','0');
INSERT INTO [t29708] VALUES (50,'إذا نكح الرجل المرأة فقد فوض إليه','51','0','0');
INSERT INTO [t29708] VALUES (51,'في رجل نذر أن ينحر نفسه، قال: تنحر كبشا','52','0','0');
INSERT INTO [t29708] VALUES (52,'ليس للعبد خلاق إلا نادى سيده , وقرأ: {ضرب الله مثلا عبدا مملوكا لا يقدر على شيء} [النحل:','53','0','0');
INSERT INTO [t29708] VALUES (53,'إذا استقمت بنقد فبعت بنقد فلا بأس، وإذا استقمت بنقد فبعت بنسيئة فلا يصلح إنما هي ورق','54','0','0');
INSERT INTO [t29708] VALUES (54,'الله اتخذني عبدا قبل أن يتخذني رسولا','55','0','0');
INSERT INTO [t29708] VALUES (55,'حاج آدم موسى، فقال موسى: يا آدم أنت أبونا وأخرجتنا من الجنة.','56','0','0');
INSERT INTO [t29708] VALUES (56,'احفروا مكانه واطرحوه وميدوا عليه دلوا من ماء , وعلموا ويسروا ولا تعسروا. . . . .','57','0','0');
INSERT INTO [t29708] VALUES (57,'{والشجرة الملعونة في القرءان} [الإسراء: ٦٠] . قال: هي شجرة الزقوم','58','0','0');
INSERT INTO [t29708] VALUES (58,'قضى بالدية اثني عشر ألفا , قال: ولم أسمع هذا من أحد غيره فكان في مولى ابن عدي بن كعب، قتل','59','0','0');
INSERT INTO [t29708] VALUES (59,'قضى الولد للفراش من أجل ابن نوح عن عمرو، سمع عبيد بن عمير، يقول: يخرج من النار قوم بعدما','60','0','0');
INSERT INTO [t29708] VALUES (60,'إياكم والخمر فإنها مفتاح كل شر , أتى رجل، فقيل له: إما أن تحرق هذا الكتاب وإما أن تقتل','61','0','0');
INSERT INTO [t29708] VALUES (61,'لا يقبل آية من كتاب الله حتى يشهد عليها شاهدان، فجاء رجل من الأنصار بآيتين , فقال عمر: لا','62','0','0');
INSERT INTO [t29708] VALUES (62,'مر رجل يخبرنا عن فلان إلا يلقانا في الدنيا يلقانا في الآخرة','63','0','0');
INSERT INTO [t29708] VALUES (63,'أخذ الجزية من المجوس حتى شهد عبد الرحمن بن عوف أن النبي أخذها من مجوس','64','0','0');
INSERT INTO [t29708] VALUES (64,'يشهد الخير من الناس , فقال: ألم أنهك؟ قال: أطعت الله وعصيتك، فسكت عمر','65','0','0');
INSERT INTO [t29708] VALUES (65,'عن الكلالة؟ فقال: ما عدا الوالد والولد. قلت: فإن الله يقول: {إن امرؤ هلك ليس له ولد}','66','0','0');
INSERT INTO [t29708] VALUES (66,'إذا رميت بالحجر أو بالبندقة أو بالمعراض ثم ذكرت اسم الله فكل ولا قتل عن عمرو، عن محمد بن','67','0','0');
INSERT INTO [t29708] VALUES (67,'إني حلفت ألا آتي امرأتي سنتين. قال: ما أراه إلا وقد دخل عليك ابن.','68','0','0');
INSERT INTO [t29708] VALUES (68,'فتوضأ من شن معلق , فذكر وضوءا خفيفا يخفيه، ثم قام يصلي , فقمت فتوضأت وجئت فقمت عن يساره ,','69','0','0');
INSERT INTO [t29708] VALUES (69,'رأى نخامة في قبلة المسجد فحكها بحصاة وقال: ما أقبح هذا أن يكون تحته من','70','0','0');
INSERT INTO [t29708] VALUES (70,'يدخل الملك على النطفة بعدما تستقر في الرحم بأربعين أو خمس وأربعين ليلة، فيقول: أي رب شقي','71','0','0');
INSERT INTO [t29708] VALUES (71,'فأتى الخلاء، وإنه رجع فأتى بطعام , فقيل: يا رسول الله ألا تتوضأ؟ قال: لم أصل','72','0','0');
INSERT INTO [t29708] VALUES (72,'الله خلق ريحا في الجنة بعد الريح بسبع سنين، من دونها باب مغلق يأتيكم الروح من خلال ذلك','73','0','0');
INSERT INTO [t29708] VALUES (73,'بني تصيبهم العين فأسترقي لهم، قال: نعم، فلو كان شيء سابق القدر سبقته','74','0','0');
INSERT INTO [t29708] VALUES (74,'كأني أراكم بالكوم جاثين دون جهنم','75','0','0');
INSERT INTO [t29708] VALUES (75,'ليس بين العبد وبين سيده ربا','76','0','0');
INSERT INTO [t29708] VALUES (76,'ليس العنبر بركاز إنما هو شيء دسره البحر','77','0','0');
INSERT INTO [t29708] VALUES (77,'لا تصلوا الجمعة حتى تفيء الكعبة من وجهها.','78','0','0');
INSERT INTO [t29708] VALUES (78,'إذا أقيمت الصلاة فلا صلاة إلا المكتوبة','79','0','0');
INSERT INTO [t29708] VALUES (79,'من شك في صلاته، فلم يدر أثلاثا صلى أم أربعا، فليتحر أصوب ذلك في نفسه، ثم ليسجد سجدتين وهو','80','0','0');
INSERT INTO [t29708] VALUES (80,'قد ظلم من منع بني الأم نصيبهم من الدية','81','0','0');
INSERT INTO [t29708] VALUES (81,'إن سعدا توفي ولم يدر ما هو كائن وإنا نرى أن تردوا على هذا الغلام','82','0','0');
INSERT INTO [t29708] VALUES (82,'كنا نغلس على عهد رسول الله من جمع إلى منى','83','0','0');
INSERT INTO [t29708] VALUES (83,'كونوا على مشاعركم فإنكم على إرث من إرث إبراهيم.','84','0','0');
INSERT INTO [t29708] VALUES (84,'الإيلاء الذي يحلف ألا يأتي أهله أبدا','85','0','0');
INSERT INTO [t29708] VALUES (85,'أكان عمر يعشر المسلمين؟ قال: لا','86','0','0');
INSERT INTO [t29708] VALUES (86,'يقرءوها بالسين ٠ السراط المستقيم','87','0','0');
INSERT INTO [t29708] VALUES (87,'أن رجلا مات على عهد النبي ولم يترك وارثا إلا عبدا هو أعتقه , فأعطاه النبي','88','0','0');
INSERT INTO [t29708] VALUES (88,'أحب الصيام إلى الله صيام داود , وأحب الصلاة إلى الله صلاة داود كان ينام نصف الليل ويقوم','89','0','0');
INSERT INTO [t29708] VALUES (89,'لا تؤذوا الأحياء بسب بالأموات','90','0','0');
INSERT INTO [t29708] VALUES (90,'أوصى إلى حفصة ابنته في صدقته ما عاشت , فإذا ماتت فهو إلى ذي الرأي من','91','0','0');
INSERT INTO [t29708] VALUES (91,'أيما ميراث من ميراث الجاهلية اقتسم في الجاهلية فهو على قسمة الجاهلية , وما أدرك الإسلام','92','0','0');
INSERT INTO [t29708] VALUES (92,'استحوا من الله فإني لأدخل الكنيف فأغطي عورتي حياء من الله','93','0','0');
INSERT INTO [t29708] VALUES (93,'هي واحدة يعني البتة','94','0','0');
INSERT INTO [t29708] VALUES (94,'{ومن يقتل مؤمنا متعمدا فجزاؤه جهنم} [النساء: ٩٣] .','95','0','0');
INSERT INTO [t29708] VALUES (95,'من قتل معاهدا بغير حله حرم الله عليه الجنة أن يجد ريحها','96','0','0');
INSERT INTO [t29708] VALUES (96,'إذنك أن ترفع الحجاب وتسمع سوادي حتى أنهاك. قال سفيان: سوادي سري','97','0','0');
INSERT INTO [t29708] VALUES (97,'انصر فلانا فشاوره ولا تقدمه , وانصر فلانا فقدمه ولا تشاوره. فذكر ذلك من','98','0','0');
INSERT INTO [t29708] VALUES (98,'في المعاريض مندوحة عن الكذب عن بعض أهل العلم , قال: أعظم أخلاق الدين الزهد في الدنيا،','99','0','0');
INSERT INTO [t29708] VALUES (99,'رجلا سقطت عليه جرة من دير بالكوفة فأتى بها عليا , فقال: أقسمه أخماسا. ثم قال: خذ منها','100','0','0');
INSERT INTO [t29708] VALUES (100,'أعان علي جعدة بسبعمائة درهم من عطائه في خادم , فقال له: اشتريت خادما. قال: كنت معتكفا.','101','0','0');
INSERT INTO [t29708] VALUES (101,'الحبس حتى يتبين الإمام، فما حبس بعد ذلك فهو جور','102','0','0');
INSERT INTO [t29708] VALUES (102,'يتوضأ مرتين مرتين','103','0','0');
INSERT INTO [t29708] VALUES (103,'ادخل علي فإنك عبد ما بقي عليك درهم عليك درهم','104','0','0');
INSERT INTO [t29708] VALUES (104,'فقسمه على سبعة أسهم، فوجد فيه رغيفا فكسره على سبعة وجعل على كل قسم منها كسرة، ثم دعا','105','0','0');
INSERT INTO [t29708] VALUES (105,'لا ضمان إن صلوا. قال: لو كنت ستصنع إلي. . . . , قلت: يعني بين الصفا','106','0','0');
INSERT INTO [t29708] VALUES (106,'نزل فبال , فأتيته بماء , وقلت له: الصلاة.','107','0','0');
INSERT INTO [t29708] VALUES (107,'كره أن يصلي على أحد غير النبي','108','0','0');
INSERT INTO [t29708] VALUES (108,'هذا القرآن مأدبة الله فتعلموا من مأدبته ما استطعتم، إن هذا القرآن هو حبل الله الذي أمر','109','0','0');
INSERT INTO [t29708] VALUES (109,'تدفن الأجساد حيث تقبض الأرواح','110','0','0');
INSERT INTO [t29708] VALUES (110,'صلى بنا النبي في بيت. . . . . , وصليت أنا وهو في البيت , وصلت أم. . . . .','111','0','0');
INSERT INTO [t29708] VALUES (111,'كنا ندعو الرجل في الجاهلية الإمعة الذي يأتي الطعام ولا يدعى , وهم فيكم اليوم المحقب دينه','112','0','0');
INSERT INTO [t29708] VALUES (112,'اللبن نسبة عليه','113','0','0');
INSERT INTO [t29708] VALUES (113,'امرأة شهدت على رضاع، فقال عثمان: تحلف عند الكعبة.','114','0','0');
INSERT INTO [t29708] VALUES (114,'بأي شيء قرأ النبي في هذا اليوم يوم عيد؟ قال: ب: قاف واقتربت عن أبي عمرو الكوفي , سمع','115','0','0');
INSERT INTO [t29708] VALUES (115,'في هذا الصندوق خمسون ألفا لم أربطها برباط ولم أمنع منها سائلا','116','0','0');
INSERT INTO [t29708] VALUES (116,'ونحن ذاهبون إلى منى فسمع الناس يكبرون , فقال:. . . . . , قال: ما هو؟ قال {وألزمهم كلمة','117','0','0');
INSERT INTO [t29708] VALUES (117,'يتهادين الجرار بينهن','118','0','0');
INSERT INTO [t29708] VALUES (118,'مر بربعة قط إلا غمض عينيه , فكتب له أبوه , قال: لا تنظر إليه بأرض','119','0','0');
INSERT INTO [t29708] VALUES (119,'بأي شيء كنت طيبت رسول الله، قالت: بأطيب الطيب','120','0','0');
INSERT INTO [t29708] VALUES (120,'صلوا أرحامكم ولو بالسلام عن أبي يونس، سأل عطاء، عن رجل أهل بحج في غير أشهر الحج؟ قال:','121','0','0');
INSERT INTO [t29708] VALUES (121,'أفضل أوليائي عندي منزلة مؤمن خفيف الحاذ , ذو حظ من صلاة , كان قوته كفافا فصبر، وكان غامضا','122','0','0');
INSERT INTO [t29708] VALUES (122,'كنت أرعى منيحة في جوانب الكوفة فنزل بعير منها فخشيت أن يسبقني بنفسه فوجأت في خاصرته ,','123','0','0');
INSERT INTO [t29708] VALUES (123,'مما خلق الله لوحا من درة بيضاء دفتاه من ياقوتة حمراء قلمه نور وكتابه نور , ينظر الله فيه','124','0','0');
INSERT INTO [t29708] VALUES (124,'ربي أمرني أن أحفي شاربي وأعفي لحيتي','125','0','0');
INSERT INTO [t29708] VALUES (125,'فأمر به أن يجلد , وقال: أعط كل عضو حقه واجتنب رأسه. . . . . .','126','0','0');
INSERT INTO [t29708] VALUES (126,'ينكح العبد اثنتين ويطلق اثنتين ويعتد بحيضتين.','127','0','0');
INSERT INTO [t29708] VALUES (127,'من صلى أربعين صباحا في جماعة لا يفوته ركعة كانت له براءة من النار وبراءة من','128','0','0');
INSERT INTO [t29708] VALUES (128,'عن الزحام على الحجر , فقال: ما من أحد زاحم عليه فإني رأيت ابن عمر تزاحم عليه حتى','129','0','0');
INSERT INTO [t29708] VALUES (129,'خير غلاما من أبيه وأمه','130','0','0');
INSERT INTO [t29708] VALUES (130,'كيف تقرأ؟ قال:. . . . . .','131','0','0');
INSERT INTO [t29708] VALUES (131,'يأخذ منها أكثر مما أعطاها','132','0','0');
INSERT INTO [t29708] VALUES (132,'رأى أبا بكر وعمر، فقال: هذان سيدا كهول أهل الجنة من الأولين والآخرين إلا النبيين','133','0','0');
INSERT INTO [t29708] VALUES (133,'يدخل الجماع. فكرهه، فقيل له: إنك رجل مستتر , أو يأتي آخر انظر إلى عورة عندي عن رجل، عن','134','0','0');
INSERT INTO [t29708] VALUES (134,'من عنده بعير ببعيرين. فقال رجل: لا يزال في الناس خير ما دام فيهم مثلك. قال ابن عمر: إني','135','0','0');
INSERT INTO [t29708] VALUES (135,'من كان منكن يؤمن بالله واليوم الآخر فلا ترفع رأسها حتى يرفع الإمام من ضيق ثياب','136','0','0');
INSERT INTO [t29708] VALUES (136,'النفس تطمئن إذا أحرزت رزقها','137','0','0');
INSERT INTO [t29708] VALUES (137,'رجلا أتى عليا ليواليه، فأبى، فأتى ابن عباس فوالاه عن رجل، عن قتادة، قال: {الباقيات','138','0','0');
INSERT INTO [t29708] VALUES (138,'أول ما نزل في الخمر: {يسألونك عن الخمر والميسر} [البقرة: ٢١٩] . فدعا عمر فقرأت عليه،','139','0','0');
INSERT INTO [t29708] VALUES (139,'يخرج على الناس لا يسل أحدا إلا عن. . . . . . . . .','140','0','0');
INSERT INTO [t29708] VALUES (140,'لم ير من يتعرض قضاء رمضان بأسا','141','0','0');
INSERT INTO [t29708] VALUES (141,'قبل عثمان بن مظعون عند موته قال كان عمر بن عبد العزيز، إذا كتب إلى عماله فذكر زيادا ,','142','0','0');
INSERT INTO [t29708] VALUES (142,'أي الأعمال أحب إليك؟ قال: إدخال السرور على المؤمن قبل مماته.','143','0','0');
INSERT INTO [t29708] VALUES (143,'لا ينبغي الصلاة على أحد إلا على النبي','144','0','0');
INSERT INTO [t29708] VALUES (144,'لو أن حامل العلم أخذه بحقه وبما ينبغي لأحبهم الله، وملائكته والصالحون والناس، ولكنهم','145','0','0');
