INSERT INTO [t29711] VALUES (1,'لا تنكح الثيب حتى تستأمر، ولا تنكح البكر حتى تستأذن.','2','0','0');
INSERT INTO [t29711] VALUES (2,'لا تنكح الثيب حتى تستأمر، ولا البكر حتى تستأذن.','3','0','0');
INSERT INTO [t29711] VALUES (3,'ما هذا التكبير؟ قال: إنها لصلاة رسول الله صلى الله عليه وسلم','4','0','0');
INSERT INTO [t29711] VALUES (4,'إذا نادى المنادي، أدبر الشيطان وله ضراط، فإذا قضي أقبل، فإذا ثوب بها أدبر، فإذا قضي أقبل،','5','0','0');
INSERT INTO [t29711] VALUES (5,'قنت في صلاة الغداة في الركعة الآخرة بعد ما قال: سمع الله لمن حمده، شهرا، يقول في قنوته:','6','0','0');
INSERT INTO [t29711] VALUES (6,'يسجد في: إذا السماء انشقت، فقلت: يا أبا هريرة، ألم أرك تسجد في: إذا السماء انشقت؟ قال:','7','0','0');
INSERT INTO [t29711] VALUES (7,'من بنى لله مسجدا من مال حلال، بنى الله له بيتا في الجنة من در وياقوت','8','0','0');
INSERT INTO [t29711] VALUES (8,'هذه النوائح يجعلن يوم القيامة صفين في جهنم؛ صفا عن يمينهن، وصفا عن شمالهن، ينبحن على أهل','9','0','0');
INSERT INTO [t29711] VALUES (9,'قنت في صلاة العتمة شهرا، من بعد ما يقول: سمع الله لمن حمده، يقول في قنوته: اللهم أنج','10','0','0');
INSERT INTO [t29711] VALUES (10,'لا تنكح المرأة على عمتها ولا على خالتها','11','0','0');
INSERT INTO [t29711] VALUES (11,'نحر رسول الله صلى الله عليه وسلم عمن اعتمر من نسائه في حجة الوداع بقرة','12','0','0');
INSERT INTO [t29711] VALUES (12,'من أسوأ الناس سرقة، الذي يسرق صلاته.','13','0','0');
INSERT INTO [t29711] VALUES (13,'أقيمت الصلاة وصف الناس صفوفهم للصلاة، وخرج علينا رسول الله صلى الله عليه وسلم، فأقبل يمشي','14','0','0');
INSERT INTO [t29711] VALUES (14,'ستكون بعدي خلفاء يعملون بما يعلمون، ويعملون ما يؤمرون، وسيكون بعدهم خلفاء يعملون ما لا','15','0','0');
INSERT INTO [t29711] VALUES (15,'لا تقدموا صيام رمضان بصيام يوم ولا يومين، إلا رجل كان يصوم صياما','16','0','0');
INSERT INTO [t29711] VALUES (16,'من قرأ في ليلة: إذا زلزلت، كانت له عدل نصف القرآن، ومن قرأ: قل يا أيها الكافرون كانت له','17','0','0');
INSERT INTO [t29711] VALUES (17,'ثلاث من كن فيه فهو منافق، وإن صلى وصام وحج البيت، وزعم أنه مسلم: إذا حدث كذب، وإذا اؤتمن','18','0','0');
INSERT INTO [t29711] VALUES (18,'مسح على الجوربين والخفين والخمار، يعني العمامة','19','0','0');
INSERT INTO [t29711] VALUES (19,'من قام رمضان إيمانا واحتسابا غفر له ما تقدم من ذنبه، ومن قام ليلة القدر إيمانا واحتسابا','20','0','0');
INSERT INTO [t29711] VALUES (20,'رجلا أنكح ابنة له على عهد النبي صلى الله عليه وسلم ثيبا كانت تحت رجل، وكانت ولدت منه،','21','0','0');
INSERT INTO [t29711] VALUES (21,'ذبح عمن اعتمر من نسائه في حجة الوداع بقرة بينهن','22','0','0');
INSERT INTO [t29711] VALUES (22,'{يوم يقوم الناس لرب العالمين} [المطففين: ٦] ، يقول: نصف يوم خمسين ألف سنة يهون ذلك على','23','0','0');
INSERT INTO [t29711] VALUES (23,'إذا دخل أحدكم بيته، فلا يجلس حتى يركع ركعتين، فإن الله عز وجل جاعل له من ركعتيه في بيته','24','0','0');
INSERT INTO [t29711] VALUES (24,'الله عز وجل حبس عن مكة الفيل وسلط عليها رسوله والمؤمنين، ألا وإنها لم تحل لأحد كان قبلي،','25','0','0');
INSERT INTO [t29711] VALUES (25,'اللهم إني أعوذ بك من عذاب النار، ومن عذاب القبر، ومن فتنة المحيا والممات، ومن شر المسيح','26','0','0');
INSERT INTO [t29711] VALUES (26,'قرأ: إذا السماء انشقت، فسجد فيها، قال: قلت: يا أبا هريرة، أتسجد فيها؟ قال: لو لم أر النبي','27','0','0');
INSERT INTO [t29711] VALUES (27,'إذا صلى على الجنازة، قال: اللهم اغفر لحينا وميتنا، وشاهدنا وغائبنا، وصغيرنا وكبيرنا،','28','0','0');
INSERT INTO [t29711] VALUES (28,'استغفر للصف الأول ثلاثا، وللذي يليه مرتين، والذي يليه مرة','29','0','0');
INSERT INTO [t29711] VALUES (29,'من صام رمضان إيمانا واحتسابا غفر له ما تقدم من ذنبه','30','0','0');
INSERT INTO [t29711] VALUES (30,'قرأ: إذا السماء انشقت، فسجد فيها، فقلت: يا أبا هريرة، ألم أرك سجدت؟ قال: بلى، لو لم أر','31','0','0');
INSERT INTO [t29711] VALUES (31,'لا تنكح الأيم حتى تستأمر، ولا تنكح البكر حتى تستأذن قالوا: وكيف إذنها؟ قال: أن','32','0','0');
INSERT INTO [t29711] VALUES (32,'سمع الله لمن حمده، ثم قال، قبل أن يسجد: اللهم نج عياش بن أبي ربيعة، اللهم نج سلمة بن','33','0','0');
INSERT INTO [t29711] VALUES (33,'الله تبارك وتعالى يغار، وإن المؤمن يغار، وغيرة الله عز وجل أن يأتي المؤمن ما حرم الله','34','0','0');
INSERT INTO [t29711] VALUES (34,'إذا أدرك أحدكم أول سجدة من صلاة العصر قبل أن تغرب الشمس، فليتم صلاته، وإذا أدرك أول سجدة','35','0','0');
INSERT INTO [t29711] VALUES (35,'إني لأقربكم صلاة برسول الله صلى الله عليه وسلم.','36','0','0');
INSERT INTO [t29711] VALUES (36,'الله عز وجل حبس عن مكة الفيل، وسلط عليهم رسول الله والمؤمنين، ألا وإنها لم تحل لأحد قبلي،','37','0','0');
INSERT INTO [t29711] VALUES (37,'لا تقدموا قبل رمضان بيوم، إلا رجل قد كان يصوم صياما فليصم','38','0','0');
INSERT INTO [t29711] VALUES (38,'من صام رمضان إيمانا واحتسابا غفر له ما تقدم من ذنبه، ومن قام ليلة القدر إيمانا واحتسابا','39','0','0');
INSERT INTO [t29711] VALUES (39,'ألا أحدثكم حديثا عن الدجال، ما حدثه نبي قومه؟ إنه أعور، إنه يجيء معه مثل الجنة والنار،','40','0','0');
INSERT INTO [t29711] VALUES (40,'لا إله إلا الله، والله أكبر، والحمد لله، وسبحان الله، تحط خطاياها كما انحط ورق هذه','41','0','0');
INSERT INTO [t29711] VALUES (41,'المؤمن غر كريم، والفاجر خب لئيم','42','0','0');
INSERT INTO [t29711] VALUES (42,'لا تنكح المرأة وخالتها، ولا المرأة وعمتها','43','0','0');
INSERT INTO [t29711] VALUES (43,'اللهم إني أعوذ بك من عذاب النار، وعذاب القبر، ومن فتنة المحيا والممات، ومن شر المسيح','44','0','0');
INSERT INTO [t29711] VALUES (44,'إذا نودي بالصلاة أدبر الشيطان له ضراط حتى لا يسمع التأذين، فإذا قضي التأذين أقبل، حتى إذا','45','0','0');
INSERT INTO [t29711] VALUES (45,'صلاة الظهر فسلم بين ركعتين، فقال رجل من بني سليم، فقال: يا رسول الله، أقصرت الصلاة أم','46','0','0');
INSERT INTO [t29711] VALUES (46,'تحاج آدم وموسى، فقال آدم لموسى: أنت الذي اصطفاك الله على خلقه وفضلك برسالته، ثم صنعت الذي','47','0','0');
INSERT INTO [t29711] VALUES (47,'نهى عن كل ذي ناب من السباع وكل ذي مخلب من الطير، وحرم المجثمة والخلسة','48','0','0');
INSERT INTO [t29711] VALUES (48,'نهى أن يتقدم شهر رمضان بصيام يوم أو اثنين، إلا أن يكون رجل له صيام قبل ذلك فيأتي','49','0','0');
INSERT INTO [t29711] VALUES (49,'سيروا، سبق المفردون.','50','0','0');
INSERT INTO [t29711] VALUES (50,'يسجد في: إذا السماء انشقت قال: فقلت له: إني رأيتك سجدت؟ قال: لولا أني رأيت رسول الله صلى','51','0','0');
INSERT INTO [t29711] VALUES (51,'الأيم تستأمر في نفسها، والبكر لا تنكح إلا بإذنها.','52','0','0');
INSERT INTO [t29711] VALUES (52,'رخص رسول الله صلى الله عليه وسلم في قتل الأسودين في الصلاة.','53','0','0');
INSERT INTO [t29711] VALUES (53,'الله عز وجل حبس عن مكة الفيل، وسلط عليها رسوله والمؤمنين، وإنها لم تحل لأحد قبلي، ولن تحل','54','0','0');
INSERT INTO [t29711] VALUES (54,'ما أذن الله عز وجل لنبي إذنه لنبي يتغنى بالقرآن يجهر به.','55','0','0');
INSERT INTO [t29711] VALUES (55,'الله يغار، وغيرة الله أن يأتي المؤمن ما حرم الله عليه','56','0','0');
INSERT INTO [t29711] VALUES (56,'إن الله عز وجل بعثني بالسيف بين يدي الساعة، وجعل رزقي في ظل رمحي، وجعل الذل والصغار على','57','0','0');
INSERT INTO [t29711] VALUES (57,'التقى آدم وموسى، فقال موسى: يا آدم، أنت أبونا خلقك الله بيده، ونفخ فيك روحه، وأسجد لك','58','0','0');
INSERT INTO [t29711] VALUES (58,'المؤمن غر كريم، والفاجر خب لئيم','59','0','0');
INSERT INTO [t29711] VALUES (59,'من فطرة الإسلام؛ الغسل يوم الجمعة، والاستنان، وأخذ الشارب، وإعفاء اللحية؛ فإن المجوس تعفي','60','0','0');
INSERT INTO [t29711] VALUES (60,'لقد رأيتني وأنا في الحجر، وقريش تسألني عن مسراي، فسألوني عن أشياء من بيت المقدس لم','61','0','0');
INSERT INTO [t29711] VALUES (61,'أصدق كلمة قالها شاعر قط كلمة لبيد:','62','0','0');
INSERT INTO [t29711] VALUES (62,'ما أخرجك يا أبا بكر؟ , قال: خرجت للقاء رسول الله صلى الله عليه وسلم، والنظر في وجهه،','63','0','0');
INSERT INTO [t29711] VALUES (63,'أصدق كلمة قالها شاعر كلمة لبيد: ألا كل شيء ما خلا الله باطل','64','0','0');
