INSERT INTO [t29712] VALUES (1,'يركع بين النداء والصلاة ركعتين خفيفتين','2','0','0');
INSERT INTO [t29712] VALUES (2,'الربا في النسيئة','3','0','0');
INSERT INTO [t29712] VALUES (3,'إياكم والتبدع والتنطع , وعليكم بالعتيق','4','0','0');
INSERT INTO [t29712] VALUES (4,'لا يساوم رجل على سوم أخيه حتى يشتري أو يترك، ولا يخطب على خطبة أخيه حتى ينكح أو يترك ,','5','0','0');
INSERT INTO [t29712] VALUES (5,'انشد عالما أو متعلما , ولا تغد ما بين ذلك، فإن ما بين ذلك جاهل، وإن الملائكة تبسط أجنحتها','6','0','0');
INSERT INTO [t29712] VALUES (6,'من أطفأ من أخيه سيئة فكأنما أحيا موءودة','7','0','0');
INSERT INTO [t29712] VALUES (7,'تبعث هذه الأمة بيض الوجوه، بيض الأقدام، بيض الأذرع','8','0','0');
INSERT INTO [t29712] VALUES (8,'سيكون في أمتي اختلاف وفرقة قوية، يحسنون القيل ويسيئون الفعل، يقرؤون القرآن لا يجاوز','9','0','0');
INSERT INTO [t29712] VALUES (9,'سيظهر شرار أمتي على خيارهم , حتى يستخفي فيهم المؤمن كما يستخفي فيكم المنافق','10','0','0');
INSERT INTO [t29712] VALUES (10,'تعوذوا بالله من الفقر والمذلة والقلة وأن تظلم أو تظلم','11','0','0');
INSERT INTO [t29712] VALUES (11,'يا معشر القراء , ارفعوا رءوسكم , ولا تكونوا عيالا على الناس','12','0','0');
INSERT INTO [t29712] VALUES (12,'لو رأى رسول الله صلى الله عليه وسلم ما أحدث النساء لمنعهن الخروج إلى المساجد كما منعته','13','0','0');
INSERT INTO [t29712] VALUES (13,'دعاء الرجل لأخيه بظاهر الغيب لا يرد، وتقول الملائكة: لك مثله','14','0','0');
INSERT INTO [t29712] VALUES (14,'انهوا نساءكم عن الزينة والتبختر في المساجد، إنما لعنت بنو إسرائيل حيث لبست نساؤهم الزينة','15','0','0');
INSERT INTO [t29712] VALUES (15,'الشيطان ذئب الإنسان كذئب الغنم , يأخذ الناحية والقاصية والشاذة , فعليكم بالعامة والجماعة','16','0','0');
INSERT INTO [t29712] VALUES (16,'فآتيه بوضوئه وحاجته، فكان يقوم من الليل، فيقول: سبحان ربي وبحمده، سبحان ربي وبحمده، الهوى','17','0','0');
INSERT INTO [t29712] VALUES (17,'إذا زالت الأفياء وراحت الأرواح فاطلبوا إلى الله عز وجل حوائجكم؛ فإنها ساعة الأوابين {فإنه','18','0','0');
INSERT INTO [t29712] VALUES (18,'تصلي الضحى ثمان ركعات، تخفي ذلك وتغلق عليها بابها , ولا تدخل عليها','19','0','0');
INSERT INTO [t29712] VALUES (19,'لو أنسيت آية ثم لم أجد أحدا يذكرنيها إلا رجل ببرك الغماد رحلت إليه','20','0','0');
INSERT INTO [t29712] VALUES (20,'اتق الله يا ابن سيرين، وعليك بالسداد؛ فإنه قد ذهب بالذين كانوا يعلمون فيما أنزل','21','0','0');
INSERT INTO [t29712] VALUES (21,'وجنازة سعد بن معاذ بين أيدينا، يقول: اهتز لها عرش الرحمن عز وجل','22','0','0');
INSERT INTO [t29712] VALUES (22,'سووا بين أولادكم في العطية؛ فإني لو كنت مؤثرا أحدا على أحد لآثرت النساء على الرجال حدثنا','23','0','0');
INSERT INTO [t29712] VALUES (23,'دخل رسول الله صلى الله عليه وسلم المسجد وعليه ثوب نجراني غليظ الصنيعة , فأتاه أعرابي من','24','0','0');
INSERT INTO [t29712] VALUES (24,'إذا مرض العبد المؤمن، قال الله عز وجل لصاحب اليمين: أجر لعبدي صالح ما كان','25','0','0');
INSERT INTO [t29712] VALUES (25,'لما أتى قال: اكتب، ولا أكتب ثنا أبو شعيب، ثنا يحيى، ثنا الأوزاعي، ثنا حسان بن عطية، قال:','26','0','0');
INSERT INTO [t29712] VALUES (26,'ما من امرأة تخرج إلى المسجد تعصف ريحها فيقبل الله منها صلاة حتى ترجع','27','0','0');
INSERT INTO [t29712] VALUES (27,'ثلاث هن حق على المسلم يوم الجمعة: الاغتسال , والسواك , وأن يمس طيبا إن','28','0','0');
INSERT INTO [t29712] VALUES (28,'إذا أراد أن ينصرف من صلاته استغفر ثلاث مرات، ثم قال: اللهم أنت السلام، ومنك السلام،','29','0','0');
INSERT INTO [t29712] VALUES (29,'بكروا بالصلاة في اليوم الغيم , فإنه من ترك صلاة العصر حبط عمله حدثنا أبو شعيب، قال: حدثني','30','0','0');
INSERT INTO [t29712] VALUES (30,'لعن رسول الله صلى الله عليه وسلم المخنثين من الرجال , والمترجلات من النساء، قال رسول الله','31','0','0');
INSERT INTO [t29712] VALUES (31,'إني لأرى في وجهه سفعة من الشيطان. ثم أقبل فسلم، فقال رسول الله: هل حدثت نفسك حين أشرفت','32','0','0');
INSERT INTO [t29712] VALUES (32,'ست من كن فيه استكمل الإيمان: قتل أعداء الله بالسيف، والصيام في الصيف، وإسباغ الوضوء في','33','0','0');
INSERT INTO [t29712] VALUES (33,'الله عز وجل لا ينظر إلى صوركم ولا إلى أموالكم ولكن ينظر إلى صلاتكم وأعمالكم، فمن كان له','34','0','0');
INSERT INTO [t29712] VALUES (34,'أنشد بالله أن أسأل عما لم يكن؛ فإن الله عز وجل قد بين ما هو كائن','35','0','0');
INSERT INTO [t29712] VALUES (35,'لكل شيء حرمة وحرمة الدواب وجوهها','36','0','0');
INSERT INTO [t29712] VALUES (36,'عما يصلح للرجل من امرأته وهي حائض , قالت: ما فوق الإزار.','37','0','0');
INSERT INTO [t29712] VALUES (37,'ما أذن الله عز وجل لشيء إذنه لنبي يتغنى بالقرآن يجهر به. يعني بإذن','38','0','0');
INSERT INTO [t29712] VALUES (38,'يكره من الشاة سبعا: الذكر , والأنثيين والمثانة , والحيا , والمرارة , والغدة ,','39','0','0');
INSERT INTO [t29712] VALUES (39,'سووا بين أولادكم حدثنا أبو شعيب , قال: ثنا يحيى , ثنا الأوزاعي , في قول الله عز وجل','40','0','0');
INSERT INTO [t29712] VALUES (40,'حسب المرء دينه , وكرمه تقواه , وسؤدده خلقه , ومروءته عقله , فإن لم يكن هكذا فحمار خير','41','0','0');
INSERT INTO [t29712] VALUES (41,'يتجمعا جميعا: أبو القاسم ومحمد','42','0','0');
INSERT INTO [t29712] VALUES (42,'من تعلق رقية فقد أتى بابا من الشرك، ومن أتى عرافا فصدقه بما يقول فقد كفر بما أنزل على','43','0','0');
INSERT INTO [t29712] VALUES (43,'ليقم صاحب الريح فليتوضأ. فاستحيا الرجل أن يقوم، فقال رسول الله صلى الله عليه وسلم: ليقم','44','0','0');
INSERT INTO [t29712] VALUES (44,'مثل الراجع في صدقته كالكلب يقيء فيرجع في قيئه فيأكله','45','0','0');
INSERT INTO [t29712] VALUES (45,'لو وضعت إصبعي في خمر ما أحببت أن تتبعني','46','0','0');
INSERT INTO [t29712] VALUES (46,'إذا كنز الناس الذهب والفضة فاكنزوا هؤلاء الكلمات: اللهم إني أسألك الثبات في الأمر،','47','0','0');
INSERT INTO [t29712] VALUES (47,'إذا قام أحدكم من الليل فلا يدخل يده في الإناء حتى يفرغ عليها مرتين أو ثلاثا؛ فإن أحدكم لا','48','0','0');
INSERT INTO [t29712] VALUES (48,'توضئوا من هذا المغمر , فمن لم يفعل فأصابه شيء فلا يلومن إلا نفسه','49','0','0');
INSERT INTO [t29712] VALUES (49,'اغتسل عبد الله بن عمر من جنابة ثم توضأ، فقلت: ما رأيت أن الغسل كافيك؟ قال: بلى ولكن ظننت','50','0','0');
INSERT INTO [t29712] VALUES (50,'تجاوز الله عن أمتي ثلاثا: الخطأ والنسيان وما استكرهوا عليه.','51','0','0');
INSERT INTO [t29712] VALUES (51,'لا يمنعن جار جاره من موضع خشبة في جداره.','52','0','0');
INSERT INTO [t29712] VALUES (52,'نكون في أرض فتصيبنا المخمصة فنصيب الميتة فما يحل لنا منها؟ فقال رسول الله صلى الله عليه','53','0','0');
INSERT INTO [t29712] VALUES (53,'من شرب من الخمر شربة لم تقبل له توبة أربعين صباحا , فإن تاب تاب الله عليه، ثلاثا أو','54','0','0');
INSERT INTO [t29712] VALUES (54,'إني لأخرج إلى الحاجة في أحضان البقيع استحياء من ربي عز وجل','55','0','0');
INSERT INTO [t29712] VALUES (55,'كن النساء يصلين مع رسول الله صلى الله عليه وسلم صلاة الفجر متلفعات بمروطهن، يرجعن إلى','56','0','0');
