INSERT INTO [t29713] VALUES (1,'إذا حضرت الصلاة وحضر العشاء فابدءوا بالعشاء , ولا تعجلوا عن عشائكم.','2','0','0');
INSERT INTO [t29713] VALUES (2,'أخبري رسول الله صلى الله عليه وسلم بما يريد أن يأكل منه، فقالوا: هو ضب. فرفع يده. فقلت:','3','0','0');
INSERT INTO [t29713] VALUES (3,'لما توفي رسول الله صلى الله عليه وسلم اجتمع المهاجرون إلى أبي بكر , واجتمعت الأنصار في','4','0','0');
INSERT INTO [t29713] VALUES (4,'لا يمنع أحدكم جاره أن يغرز خشبة في جداره.','5','0','0');
INSERT INTO [t29713] VALUES (5,'شر الطعام طعام الوليمة يدعى إليها الأغنياء ويترك الفقراء , ومن لم يأتها فقد عصى الله','6','0','0');
INSERT INTO [t29713] VALUES (6,'الوزغ الفويسق','7','0','0');
INSERT INTO [t29713] VALUES (7,'من قام رمضان إيمانا واحتسابا فقد غفر له ما تقدم من ذنبه','8','0','0');
INSERT INTO [t29713] VALUES (8,'من قام رمضان إيمانا واحتسابا غفر له ما تقدم من ذنبه','9','0','0');
INSERT INTO [t29713] VALUES (9,'ما لعن رسول الله صلى الله عليه وسلم , ولا ضرب شيئا بيده قط , ولا ينتقم لنفسه شيئا يؤتى','10','0','0');
INSERT INTO [t29713] VALUES (10,'إذا اعتكف يدني إلي رأسه فأرجله , وكان لا يدخل البيت إلا لحاجة الإنسان','11','0','0');
INSERT INTO [t29713] VALUES (11,'اليوم الجمعة إن رسول الله صلى الله عليه وسلم كان يأمر بالغسل','12','0','0');
INSERT INTO [t29713] VALUES (12,'أيصلي الرجل في الثوب الواحد؟ فقال رسول الله صلى الله عليه وسلم: أوكلكم له','13','0','0');
INSERT INTO [t29713] VALUES (13,'قضى بالشفعة فيما لم يقسم , فإذا وقعت الحدود فلا شفعة فيه','14','0','0');
INSERT INTO [t29713] VALUES (14,'رمضان من صامه إيمانا واحتسابا غفر له ما تقدم من ذنبه','15','0','0');
INSERT INTO [t29713] VALUES (15,'ليس بكذاب الذي يمشي بصلح بين الناس فينمي خبرا أو يقوله','16','0','0');
INSERT INTO [t29713] VALUES (16,'إذا سمعتم النداء فقولوا مثل ما يقول المؤذن','17','0','0');
INSERT INTO [t29713] VALUES (17,'أبا بكر رضي الله عنه استأذن على النبي صلى الله عليه وسلم , ورسول الله صلى الله عليه وسلم','18','0','0');
INSERT INTO [t29713] VALUES (18,'الرجل ينام وهو جنب , فقال: نعم يتوضأ.','19','0','0');
INSERT INTO [t29713] VALUES (19,'يقوم الناس يوم القيامة لرب العالمين.','20','0','0');
INSERT INTO [t29713] VALUES (20,'حمل على فرس عتيق في سبيل الله , فوجده يباع , فأراد أن يبتاعه , فسأل عن ذلك رسول الله صلى','21','0','0');
INSERT INTO [t29713] VALUES (21,'بيع الولاء وعن هبته','22','0','0');
INSERT INTO [t29713] VALUES (22,'نهى عنهما وأعاب عليهما: متعة النساء , ومتعة الحج','23','0','0');
INSERT INTO [t29713] VALUES (23,'أيما امرئ قال لأخيه: كافر , فقد باء بها أحدهما','24','0','0');
INSERT INTO [t29713] VALUES (24,'فإني أحمد إليك الله الذي لا إله إلا هو , وأقر لك بالسمع والطاعة على سنة الله وسنة رسوله','25','0','0');
INSERT INTO [t29713] VALUES (25,'متى الساعة؟ قال: وماذا أعددت لها؟ قال: لا شيء , والله إني لقليل الصلاة قليل الصيام إلا','26','0','0');
INSERT INTO [t29713] VALUES (26,'ناس من أمتي عرضوا علي غزاة في سبيل الله يركبون هذا البحر , ملوكا على الأسرة أو مثل الملوك','27','0','0');
INSERT INTO [t29713] VALUES (27,'من صلى صلاة لم يقرأ فيها بفاتحة الكتاب فلم يصل إلا وراء الإمام','28','0','0');
INSERT INTO [t29713] VALUES (28,'الفطرة قص الشارب , وتقليم الأظافر , ونتف الإبط , وحلق العانة والختان','29','0','0');
INSERT INTO [t29713] VALUES (29,'نحر عن آل محمد في حجة الوداع بقرة واحدة','30','0','0');
INSERT INTO [t29713] VALUES (30,'من تصدق بصدقة من كسب طيب ولا يقبل الله عز وجل إلا طيبا , فإنما يضعها في كف الرحمن عز وجل','31','0','0');
INSERT INTO [t29713] VALUES (31,'حجم أبو طيبة رسول الله صلى الله عليه وسلم , فأعطاه صاعين أو صاعا من تمر , وأمر أهله أن','32','0','0');
INSERT INTO [t29713] VALUES (32,'إذا أسلم العبد فحسن إسلامه كفر الله تعالى عنه كل سيئة زلفها , وكتب له كل حسنة كان زلفها ,','33','0','0');
INSERT INTO [t29713] VALUES (33,'من نذر أن يطيع الله عز وجل فليطعه , ومن نذر أن يعصيه فلا يعصه','34','0','0');
INSERT INTO [t29713] VALUES (34,'حجبت الجنة بالمكاره , وحجبت النار بالشهوات','35','0','0');
INSERT INTO [t29713] VALUES (35,'ليس فيما دون خمس أواق من الورق صدقة , وليس فيما دون خمس ذود من الإبل صدقة , وليس فيما دون','36','0','0');
INSERT INTO [t29713] VALUES (36,'الولاء وعن هبته','37','0','0');
INSERT INTO [t29713] VALUES (37,'اليوم الذي احتلمت فيه أخبرت النبي صلى الله عليه وسلم , فقال: لا يدخل على','38','0','0');
INSERT INTO [t29713] VALUES (38,'صليت مع رسول الله صلى الله عليه وسلم العتمة , فقرأ فيها ب والتين','39','0','0');
INSERT INTO [t29713] VALUES (39,'ليصلي الصبح فينصرف والنساء متلفعات بمروطهن ما يعرفن من غلس','40','0','0');
INSERT INTO [t29713] VALUES (40,'لولا أن أشق على أمتي لأحببت أن لا أتخلف عن سرية تخرج في سبيل الله عز وجل , ولكن لا أجد ما','41','0','0');
INSERT INTO [t29713] VALUES (41,'نهى أن يمشي الرجل في نعل واحد','42','0','0');
INSERT INTO [t29713] VALUES (42,'لقد نزلت هذه الليلة سورة هي أحب إلي من الدنيا وما فيها.','43','0','0');
INSERT INTO [t29713] VALUES (43,'يا أهل الجنة , فيقولون: لبيبك ربنا وسعديك والخير في يديك , فيقول: هل رضيتم؟ فيقولون: وما','44','0','0');
INSERT INTO [t29713] VALUES (44,'يا أهل الجنة أرضيتم؟ فيقولون: ما لنا لا نرضى وقد أعطيتنا ما لم تعط أحدا من العالمين ,','45','0','0');
INSERT INTO [t29713] VALUES (45,'من أدرك من الصبح ركعة قبل أن تطلع الشمس فقد أدرك الصبح , ومن أدرك من العصر ركعة قبل أن','46','0','0');
INSERT INTO [t29713] VALUES (46,'إذا كان أحدكم يصلي فلا يدع أحدا يمر بين يديه , وليدرأه ما استطاع , فإن أبى فليقاتله؛','47','0','0');
INSERT INTO [t29713] VALUES (47,'إذا تثاءب أحدكم فليكظم ما استطاع','48','0','0');
INSERT INTO [t29713] VALUES (48,'نهى أن ينتبذ في الدباء والمزفت','49','0','0');
INSERT INTO [t29713] VALUES (49,'قال رجل لم يعمل حسنة قط: إذا مت أحرقوني، ثم اذروا نصفه في البر , ونصفه في البحر , فوالله','50','0','0');
INSERT INTO [t29713] VALUES (50,'لو وجدت مع امرأتي رجلا أمهله حتى آتي بأربعة شهداء.','51','0','0');
INSERT INTO [t29713] VALUES (51,'إذا كنت يقظانة تحدث معي بعد طلوع الفجر , وإن كنت نائمة اضطجع على شقه الأيمن حتى يأتيه','52','0','0');
INSERT INTO [t29713] VALUES (52,'إذا صلى جاء بين الركعتين، صلاة الفجر , فإن كنت يقظى كلمني , وجلس حتى تقام الصلاة , فيذهب','53','0','0');
INSERT INTO [t29713] VALUES (53,'يصلي إحدى عشرة ركعة ثم يضطجع على شقه الأيمن , فإن كنت يقظانة حدثني حتى يأتيه المؤذن ,','54','0','0');
INSERT INTO [t29713] VALUES (54,'الله تعالى لا يقبض العلم انتزاعا ينتزعه من الناس , ولكن يقبض العلم بقبض العلماء , حتى إذا','55','0','0');
INSERT INTO [t29713] VALUES (55,'الصوم في السفر , فقال: إن شئت فصم , وإن شئت فأفطر','56','0','0');
INSERT INTO [t29713] VALUES (56,'كفن في ثلاثة أثواب بيض ليس بها قميص ولا عمامة , أدرج فيها إدراجا هكذا أو','57','0','0');
INSERT INTO [t29713] VALUES (57,'فما بال قوم يشترطون شروطا ليس في كتاب الله عز وجل.','58','0','0');
INSERT INTO [t29713] VALUES (58,'ما مست يد رسول الله صلى الله عليه وسلم يد امرأة قط.','59','0','0');
