INSERT INTO [t29721] VALUES (1,'يذهب الصالحون أسلافا الأول فالأول حتى يبقى مثل حثالة أو حفالة التمر والشعير لا يبالي الله','2','0','0');
INSERT INTO [t29721] VALUES (2,'يتعوذ من عذاب القبر','3','0','0');
INSERT INTO [t29721] VALUES (3,'ابن آدم , قم إلي أمش إليك , امش إلي أهرول إليك , ابن آدم إن دنوت مني شبرا دنوت منك ذراعا','4','0','0');
INSERT INTO [t29721] VALUES (4,'ابن آدم إن عملت قراب الأرض خطيئة لم تشرك بي شيئا جعلت قراب الأرض مغفرة','5','0','0');
INSERT INTO [t29721] VALUES (5,'صلى النبي صلى الله عليه وسلم صلاة الخوف فصف طائفة معه وطائفة تلقاء العدو , فصلى النبي صلى','6','0','0');
INSERT INTO [t29721] VALUES (6,'ثلاثة يحبهم الله عز وجل , وثلاثة يشنؤهم الله عز وجل , فأما الذين يحبهم الله عز وجل: فرجل','7','0','0');
INSERT INTO [t29721] VALUES (7,'عرضت علي الأنبياء , فكان النبي يجئ ليس معه إلا الرجل , ويجئ الآخر ليس معه إلا الرجلان ,','8','0','0');
INSERT INTO [t29721] VALUES (8,'لا صلاة بعد الفجر حتى تشرق الشمس , ولا صلاة بعد العصر حتى تغيب الشمس','9','0','0');
INSERT INTO [t29721] VALUES (9,'إذا لم يجد أحدكم إلا ثوبا واحدا فليشدده على حقويه','10','0','0');
INSERT INTO [t29721] VALUES (10,'يوم حنين , ورسول الله صلى الله عليه وسلم على بغلته الشهباء التي أهداها له النجاشي , وزيد','11','0','0');
INSERT INTO [t29721] VALUES (11,'أنى لكم هذا التمر؟ قالوا: ابتعناه بصاعين من تمرنا , فقال: لا تفعل , ولكن بع تمرك ثم اشتر','12','0','0');
INSERT INTO [t29721] VALUES (12,'أن يؤكل لحم الأضاحي فوق ثلاثة أيام , قال: فقالوا: يا رسول الله إن لنا أعمالا , قال: كلوا','13','0','0');
INSERT INTO [t29721] VALUES (13,'إذا استجد ثوبا سماه باسمه قميصا , أو إزارا , أو عمامة , يقول: اللهم لك الحمد أنت كسوتنيه','14','0','0');
INSERT INTO [t29721] VALUES (14,'من أي بلد أنت؟ قال: قلت: من أهل الكوفة , قال: أبأرضك أرض يقال لها: كوتي ذات نخل وسباخ؟','15','0','0');
INSERT INTO [t29721] VALUES (15,'من سمع بالدجال فلينأ عنه','16','0','0');
INSERT INTO [t29721] VALUES (16,'إذا سمعتم بالدجال ففروا منه؛ فإن الرجل يأتيه ليرد عليه فيتبعه لما يرى معه من الشبهات ,','17','0','0');
INSERT INTO [t29721] VALUES (17,'من سمع بالدجال فلينأ عنه , قال أبو النعمان: تباعد منه والله إن الرجل ليأتيه وهو يحسب أنه','18','0','0');
INSERT INTO [t29721] VALUES (18,'السمت الصالح , والهدى الصالح , والاقتصاد جزء من بضع وعشرين جزءا من النبوة , حدثنا أحمد ,','19','0','0');
INSERT INTO [t29721] VALUES (19,'اللهم إني أعوذ بك من الكسل والغرم , ومن عذاب القبر , ومن فتنة الصدر','20','0','0');
INSERT INTO [t29721] VALUES (20,'السلام عليكم يا أهل القبور , يغفر الله لنا ولكم , أنتم لنا سلفنا وقال مرة: فرط ونحن','21','0','0');
INSERT INTO [t29721] VALUES (21,'ما منكم من أحد إلا له قرين من الشياطين , قالوا: ولا أنت يا رسول الله , قال: ولا أنا , إلا','22','0','0');
INSERT INTO [t29721] VALUES (22,'الميت يعذب ببكاء أهله عليه , قال: فقال ابن عباس: إنما مر رسول الله صلى الله عليه وسلم','23','0','0');
INSERT INTO [t29721] VALUES (23,'إن الله عز وجل ليعذبه ببكاء بعض أهله عليه','24','0','0');
INSERT INTO [t29721] VALUES (24,'بكاء أهل الكافر عذاب عليه بعد موته','25','0','0');
INSERT INTO [t29721] VALUES (25,'كان نبي من الأنبياء يخط فمن صادف مثل خطه','26','0','0');
INSERT INTO [t29721] VALUES (26,'ليس عن أولاد المشركين قال: الله أعلم بما كانوا عاملين','27','0','0');
INSERT INTO [t29721] VALUES (27,'رجلا من الأنصار أعتق عبدا له عن دبر منه , لم يكن له مال غيره , قال: فقال رسول الله صلى','28','0','0');
INSERT INTO [t29721] VALUES (28,'أبو مذكور أعتق غلاما له عن دبر منه , فقال رسول الله صلى الله عليه وسلم: من يشتريه مني.','29','0','0');
INSERT INTO [t29721] VALUES (29,'ينزل الله تبارك وتعالى كل ليلة إلى سماء الدنيا حين يبقى ثلث الليل الآخر , فيقول عز وجل:','30','0','0');
INSERT INTO [t29721] VALUES (30,'يأمر بنبيذ من الليل , فينبذ , فيشرب منه الغد وليلته , ثم يمسك عنه يوم','31','0','0');
INSERT INTO [t29721] VALUES (31,'رجلا أعتق ستة أعبد له ولم يترك مالا غيره , فقال رسول الله صلى الله عليه وسلم في ذلك قولا','32','0','0');
INSERT INTO [t29721] VALUES (32,'إذا بايع بايع على شهادة أن لا إله إلا الله وأن محمدا رسول الله , وإقام الصلاة , وإيتاء','33','0','0');
INSERT INTO [t29721] VALUES (33,'بال جرير , فتوضأ ومسح على خفيه , فقيل له , فقال: رأيت رسول الله صلى الله عليه وسلم','34','0','0');
INSERT INTO [t29721] VALUES (34,'إذا جاء المصدق فلا يصدر إلا وهو عنكم راض','35','0','0');
INSERT INTO [t29721] VALUES (35,'أتيت رسول الله صلى الله عليه وسلم أبايعه , فاشترط علي النصح لكل مسلم , فإني لكم','36','0','0');
INSERT INTO [t29721] VALUES (36,'الدينار بالدينار , والدرهم بالدرهم , وزن بوزن , لا فضل بينهما , ولا يباع عاجل','37','0','0');
INSERT INTO [t29721] VALUES (37,'الوتر خمس صلوات كتبهن الله على العباد من جاء بهن ولم ينقص منهن شيئا كان له عند الله عهد ,','38','0','0');
INSERT INTO [t29721] VALUES (38,'البيعان بالخيار ما لم يتفرقا , فإن صدقا وبينا بورك لهما في بيعهما , وإن كتما وكذبا محق','39','0','0');
