INSERT INTO [t29721] VALUES (43,'قام في اثنتين من الصلاة ولم يجلس , فلما قضى صلاته سجد سجدتين وهو جالس ثم سلم بعد','44','0','0');
INSERT INTO [t29721] VALUES (44,'صلى بنا رسول الله صلى الله عليه وسلم , فقام ولم يجلس في الركعتين من صلاة الظهر أو العصر ,','45','0','0');
INSERT INTO [t29721] VALUES (45,'الملائكة عشرة أجزاء , فتسعة أجزاء الكروبيون الذين يسبحون الليل والنهار لا يفترون , وجزء','46','0','0');
INSERT INTO [t29721] VALUES (46,'أتاني جبريل عليه السلام وفي يده مرآة بيضاء فيها نكتة سوداء , فقلت: ما هذا يا جبريل , قال:','47','0','0');
INSERT INTO [t29721] VALUES (47,'أنا فرطكم على الحوض','48','0','0');
INSERT INTO [t29721] VALUES (48,'أنا فرطكم على الحوض','49','0','0');
INSERT INTO [t29721] VALUES (49,'أمر بقتله , وكان عينا لأبي سفيان , وكان حليفا فمر على حلقة الأنصار , فقال: إني مسلم ,','50','0','0');
INSERT INTO [t29721] VALUES (50,'فيه عبد أو أمة أو خمس مائة أو عشرون ومائة شاة أو غرة , فقال: يا رسول الله , ما لي ما أودي','51','0','0');
INSERT INTO [t29721] VALUES (51,'نهى عن نبيذ الجراجسية , قال: الأخضر','52','0','0');
INSERT INTO [t29721] VALUES (52,'من صلى علي صلاة صلى الله عليه عشر صلوات وحط عنه عشر خطيات ورفع له عشر','53','0','0');
