INSERT INTO [t29725] VALUES (1,'ما لي وللدنيا , وما للدنيا ومالي , والذي نفسي بيده ما مثلي ومثل الدنيا إلا كراكب سار في','2','0','0');
INSERT INTO [t29725] VALUES (2,'وما شبع من خبز بر مرتين في يوم حتى لقي الله','3','0','0');
INSERT INTO [t29725] VALUES (3,'ما بال أقوام بلغهم أني أترخص في الأمر فيدعونه، والله إني لأعلمهم بالله وأشدهم له','4','0','0');
INSERT INTO [t29725] VALUES (4,'من اشتاق إلى الجنة سابق إلى الخيرات , ومن أشفق من النار لهى عن الشهوات , ومن ترقب الموت','5','0','0');
INSERT INTO [t29725] VALUES (5,'ما من عبد يحب الله ورسوله إلا للفقر أسرع إليه من جرية السيل من رأس الجبل إلى وجهه ,','6','0','0');
INSERT INTO [t29725] VALUES (6,'حق على الله أن لا يرفع من الدنيا شيئا إلا وضعه','7','0','0');
INSERT INTO [t29725] VALUES (7,'يصوم الشهر حتى نقول: لا يريد أن يفطر منه شيئا , أو يفطر الشهر حتى نقول: لا يريد أن يصوم','8','0','0');
INSERT INTO [t29725] VALUES (8,'يصلي، ولجوفه أزيز كأزيز المرجل.','9','0','0');
INSERT INTO [t29725] VALUES (9,'إني أحب أن أسمعه من غيري. قال: فافتتحت سورة النساء , فلما بلغت: {فكيف إذا جئنا من كل أمة','10','0','0');
INSERT INTO [t29725] VALUES (10,'ما أمسى عند آل محمد صاع من بر , ولا صاع من حب.','11','0','0');
INSERT INTO [t29725] VALUES (11,'توفي رسول الله صلى الله عليه وسلم ودرعه رهن بثلاثين صاعا من شعير كان أخذه لأهله من','12','0','0');
INSERT INTO [t29725] VALUES (12,'ما أعرف منكم اليوم شيئا مما أدركت عليه أصحابي إلا هذه الصلاة , ولقد صنعتم فيها ما لا','13','0','0');
INSERT INTO [t29725] VALUES (13,'مثل أصحابي في أمتي كالملح في الطعام , لا يصلح الطعام إلا بالملح.','14','0','0');
INSERT INTO [t29725] VALUES (14,'لا يؤمن أحدكم حتى يحب لأخيه ما يحب لنفسه','15','0','0');
INSERT INTO [t29725] VALUES (15,'يخرج في آخر الزمان رجال يختلون الدنيا بالدين , يلبسون جلود الضأن من اللين , ألسنتهم أحلى','16','0','0');
INSERT INTO [t29725] VALUES (16,'اتقوا الله, وعليكم بالقرآن؛ فإنه نور الظلمة وهدى النهار , فخذوا به على ما كان من جهد','17','0','0');
INSERT INTO [t29725] VALUES (17,'لا تزول قدما العبد يوم القيامة حتى يسأل عن أربع: عن عمره فيما أفناه , وعن شبابه فيما','18','0','0');
INSERT INTO [t29725] VALUES (18,'استحيوا من الله حق الحياء. قالوا: يا رسول الله إنا نستحي.','19','0','0');
INSERT INTO [t29725] VALUES (19,'احفظ الله يحفظك , احفظ الله تجده أمامك , إذا سألت فاسأل الله , وإذا استعنت فاستعن بالله ,','20','0','0');
INSERT INTO [t29725] VALUES (20,'ما تواد اثنان في الإسلام ففرق بينهما إلا من ذنب يحدثه أحدهما','21','0','0');
INSERT INTO [t29725] VALUES (21,'كيف أنعم وصاحب القرن شاخص ببصره , منحن ظهره , ينتظر متى يؤمر.','22','0','0');
INSERT INTO [t29725] VALUES (22,'كيف أنعم وصاحب القرن قد التقم القرن , واستمع الأذان متى يؤمر فينفخ , فاشتد ذلك على أصحاب','23','0','0');
INSERT INTO [t29725] VALUES (23,'لو أن رجلا ارتبط فرسا فنتجت عنده مهرا حين يرى أول الآيات ما يركب حتى','24','0','0');
INSERT INTO [t29725] VALUES (24,'الآيات كلها في ثلاثة أشهر','25','0','0');
INSERT INTO [t29725] VALUES (25,'إنكم محشورون إلى الله حفاة عراة غرلا: {كما بدأنا أول خلق نعيده وعدا علينا إنا كنا فاعلين}','26','0','0');
INSERT INTO [t29725] VALUES (26,'إني أرى ما لا ترون، وأسمع ما لا تسمعون , أطت السماء وحق لها أن تئط؛ ما فيها موضع أربع','27','0','0');
INSERT INTO [t29725] VALUES (27,'أمامكم عقبة كئودا، لا يجوزها إلا المتخففون، وإني أحب أن أتخفف لها','28','0','0');
INSERT INTO [t29725] VALUES (28,'من مشى لأخيه المسلم في حاجة كتب الله له بكل خطوة سبعين حسنة، ومحا عنه بكل خطوة سبعين','29','0','0');
INSERT INTO [t29725] VALUES (29,'من أمسى وأصبح وليس المؤمنون والمؤمنات والمسلمون والمسلمات من همه فليس مني ولست','30','0','0');
INSERT INTO [t29725] VALUES (30,'أوحى الله تبارك وتعالى إلى نبي من الأنبياء: قل لفلان العابد أما زهدك في الدنيا فتعجلت','31','0','0');
INSERT INTO [t29725] VALUES (31,'من قرأ: قل هو الله أحد.','32','0','0');
INSERT INTO [t29725] VALUES (32,'يرحم الله لبيدا أيدرون له ألف بيت؟ أليس هو الذي يقول: ذهب الذين يعاش في أكنافهم ... وبقيت','33','0','0');
INSERT INTO [t29725] VALUES (33,'تختم باليمين تكن من المقربين. قال: يا رسول الله وما المقربون؟ قال: جبريل','34','0','0');
INSERT INTO [t29725] VALUES (34,'من أحب أن ينظر إلى آدم في خلقه، وأنا في خلقي، وإلى إبراهيم في خلقه، وإلى موسى في مناجاته','35','0','0');
INSERT INTO [t29725] VALUES (35,'الأعمال بالنية، وإنما لامرئ ما نوى , فمن كانت هجرته إلى الله وإلى رسوله , فهجرته إلى الله','36','0','0');
INSERT INTO [t29725] VALUES (36,'الغزو غزوان: غزو يبتغى به وجه الله تنفق فيه الكريمة ويحتسب فيه العمل , ويياسر فيه الشريك،','37','0','0');
INSERT INTO [t29725] VALUES (37,'النظر إلى وجه علي بن أبي طالب عبادة','38','0','0');
INSERT INTO [t29725] VALUES (38,'النظر إلى وجه علي عبادة','39','0','0');
INSERT INTO [t29725] VALUES (39,'يحشر الجبارون والمتكبرون على صور الذر , يجاء بالجبارين والمتكبرين رجالا في صور الذر يطؤهم','40','0','0');
INSERT INTO [t29725] VALUES (40,'إني قمت على باب الجنة فإذا عامة من دخلها المساكين , ونظرت إلى النار فإذا عامة من دخلها','41','0','0');
INSERT INTO [t29725] VALUES (41,'يأخذ الجبار سمواته وأرضه بيده، وقبض يده فجعل يقبضها ويبسطها , ويقول: أنا الجبار أنا','42','0','0');
INSERT INTO [t29725] VALUES (42,'قطع في مجن قيمته ثلاثة دراهم','43','0','0');
INSERT INTO [t29725] VALUES (43,'لا حسد إلا في اثنتين: رجل أتاه الله مالا وسلطه على هلكته في الحق , ورجل أتاه الله علما','44','0','0');
INSERT INTO [t29725] VALUES (44,'تسبح عند منامها ثلاثا وثلاثين، وتكبر أربعا وثلاثين، وتحمد ثلاثا','45','0','0');
INSERT INTO [t29725] VALUES (45,'يقول في ركوعه: سبوح قدوس رب الملائكة والروح','46','0','0');
INSERT INTO [t29725] VALUES (46,'أخف الناس الصلاة في تمام','47','0','0');
INSERT INTO [t29725] VALUES (47,'البزاق في المسجد خطيئة، وكفارتها دفنها','48','0','0');
INSERT INTO [t29725] VALUES (48,'لا يؤمن أحدكم حتى أكون أحب إليه من ولده ووالده والناس أجمعين','49','0','0');
INSERT INTO [t29725] VALUES (49,'تعلموا ما شئتم أن تعلموا من العلم , فإن الله لا يأجركم على العلم حتى تعملوا به، إن','50','0','0');
INSERT INTO [t29725] VALUES (50,'الإيمان بضع وستون أو بضع وسبعون بابا , أعلاها لا إله إلا الله , وأدناها إماطة الأذى عن','51','0','0');
INSERT INTO [t29725] VALUES (51,'من شهد جنازة وصلى عليها وصبر حتى تدفن كان له من الأجر قيراطان، أصغرهما مثل جبل','52','0','0');
INSERT INTO [t29725] VALUES (52,'ابن عمك عليا قد خطبك , فماذا تقولين؟ فبكت , وقالت: كأنك يا أبت ادخرتني لفقير قريش , فقال:','53','0','0');
INSERT INTO [t29725] VALUES (53,'من كان يؤمن بالله واليوم الآخر فليقل خيرا أو ليسكت , ومن كان يؤمن بالله واليوم الآخر','54','0','0');
INSERT INTO [t29725] VALUES (54,'ما شيء يصيب المؤمن حتى الشوكة إلا كتب الله له حسنة وحط عنه بها خطيئة','55','0','0');
INSERT INTO [t29725] VALUES (55,'يقري الضيف ويفك العاني , ويصل الرحم ويحسن الجوار , فهل ينفعه ذلك؟ قال: لا , إنه لم يقل','56','0','0');
INSERT INTO [t29725] VALUES (56,'المؤمن يأكل في معى واحد، والكافر يأكل في سبعة أمعاء','57','0','0');
INSERT INTO [t29725] VALUES (57,'لكل ابن آدم حظ من الزنا , فالعينان تزنيان وزناهما النظر , واليدان تزنيان وزناهما البطش ,','58','0','0');
INSERT INTO [t29725] VALUES (58,'{لا يضركم من ضل إذا اهتديتم} [المائدة: ١٠٥] . كيف يصنع فيها؟ فقال أبو ثعلبة: والله لقد','59','0','0');
INSERT INTO [t29725] VALUES (59,'من أراد أن يمسك بالقضيب الياقوت الأحمر الذي غرسه الله تبارك وتعالى في جنة الخلد فليتمسك','60','0','0');
