INSERT INTO [t29738] VALUES (1,'رحمك الله أبا بكر، كنت إلف رسول الله صلى الله عليه وسلم وأنيسه، ومستراحه وثقته، وموضع سره','2','0','0');
INSERT INTO [t29738] VALUES (2,'كتاب الله القصاص. فعفا القوم، فقال رسول الله صلى الله عليه وسلم: إن من عباد الله من لو','3','0','0');
INSERT INTO [t29738] VALUES (3,'رجلا سرق بردة، فرفعه إلى النبي صلى الله عليه وسلم فأمر بقطعه، فقال: يا رسول الله , قد','4','0','0');
INSERT INTO [t29738] VALUES (4,'لا يصوم عبد يوما في سبيل الله إلا باعد الله بذلك اليوم النار عن وجهه سبعين','5','0','0');
INSERT INTO [t29738] VALUES (5,'الغسل يوم الجمعة. قال: هو واجب على كل محتلم.','6','0','0');
INSERT INTO [t29738] VALUES (6,'ليس على المسلم في فرسه ولا مملوكه صدقة. أخرجه النسائي في جمعه حديث مالك بن أنس رحمه الله،','7','0','0');
INSERT INTO [t29738] VALUES (7,'أسلم أناس من عرينة، فاجتووا المدينة، فقال لهم رسول الله صلى الله عليه وسلم: لو خرجتم إلى','8','0','0');
INSERT INTO [t29738] VALUES (8,'حيا من العرب اجتووا المدينة، فقال لهم النبي صلى الله عليه وسلم: لو خرجتم إلى إبلنا فأصبتم','9','0','0');
INSERT INTO [t29738] VALUES (9,'غزا مع رسول الله صلى الله عليه وسلم ست عشرة غزوة. أخرجه البخاري في صحيحه، عن أحمد بن','10','0','0');
INSERT INTO [t29738] VALUES (10,'كان يعرض راحلته ويصلي إليها.','11','0','0');
INSERT INTO [t29738] VALUES (11,'اليدين يسجدان كما يسجد الوجه، فإذا وضع أحدكم وجهه فليضع يديه، وإذا رفعه','12','0','0');
INSERT INTO [t29738] VALUES (12,'إذا أكل أحدكم فليأكل بيمينه، وإذا شرب فليشرب بيمينه , فإن الشيطان يأكل بشماله ويشرب','13','0','0');
INSERT INTO [t29738] VALUES (13,'خمس لا جناح في قتلهن على من قتلهن في الحل والحرم: العقرب، والفأرة، والغراب، والحدأة،','14','0','0');
INSERT INTO [t29738] VALUES (14,'لا تتركوا النار في بيوتكم حين تنامون','15','0','0');
INSERT INTO [t29738] VALUES (15,'لا تسافر المرأة ثلاثا إلا ومعها ذو محرم','16','0','0');
INSERT INTO [t29738] VALUES (16,'فمر برجل وهو ينحر بدنته، وهي باركة، فقال: ابعثها قياما مقيدة سنة محمد صلى الله عليه','17','0','0');
INSERT INTO [t29738] VALUES (17,'إذا أقيمت الصلاة فلا صلاة إلا المكتوبة.','18','0','0');
INSERT INTO [t29738] VALUES (18,'إني أحبه فأحبه وأحب من يحبه.','19','0','0');
INSERT INTO [t29738] VALUES (19,'إن أنسا غلام كيس فليخدمك. قال: فخدمته في السفر والحضر، فوالله ما قال لشيء صنعته: لم صنعت','20','0','0');
INSERT INTO [t29738] VALUES (20,'يلبي بالحج والعمرة جميعا.','21','0','0');
INSERT INTO [t29738] VALUES (21,'فتوضأ وصبه علي فأفقت، فقلت: يا رسول الله كيف أصنع في مالي ولي أخوات؟ فنزلت آية الميراث:','22','0','0');
INSERT INTO [t29738] VALUES (22,'سليكا جاء ورسول الله صلى الله عليه وسلم يخطب فجلس، فأمره النبي صلى الله عليه وسلم أن يصلي','23','0','0');
INSERT INTO [t29738] VALUES (23,'الحلال بين، وإن الحرام بين، وإن بين ذلك أمورا مشتبهات. وربما قال: مشتبهة , وسأضرب لكم في','24','0','0');
INSERT INTO [t29738] VALUES (24,'لا تسافر امرأة مسيرة يومين ولا ليلتين إلا ومعها ذو محرم أو زوجها، ولا صوم يومين: يوم','25','0','0');
INSERT INTO [t29738] VALUES (25,'من أعتق رقبة أعتق الله بكل عضو منها عضوا منه من النار، حتى فرجه بفرجه','26','0','0');
INSERT INTO [t29738] VALUES (26,'من أعتق رقبة أعتق الله بكل إرب إربا منه من النار، حتى باليد اليد، وبالرجل الرجل، وبالفرج','27','0','0');
INSERT INTO [t29738] VALUES (27,'لا تكتبوا عني، ومن كتب عني غير القرآن فليمحه','28','0','0');
INSERT INTO [t29738] VALUES (28,'ومن كذب علي متعمدا فليتبوأ مقعده من النار','29','0','0');
INSERT INTO [t29738] VALUES (29,'يخرج رجلان من النار فيعرضان على الله عز وجل، يؤمر بهما إلى النار، قال: فيلتفت أحدهما،','30','0','0');
INSERT INTO [t29738] VALUES (30,'زجر عن الشرب قائما.','31','0','0');
INSERT INTO [t29738] VALUES (31,'لا عدوى، ولا طيرة، ويعجبني الفأل الكلمة الطيبة الحسنة.','32','0','0');
INSERT INTO [t29738] VALUES (32,'عطس عندك رجلان فشمت أحدهما ولم تشمت الآخر، أو فسمته ولم تشمت الآخر، فقال: إن هذا حمد الله','33','0','0');
INSERT INTO [t29738] VALUES (33,'اللهم إنك حي لا تموت، وخالق لا تعاب، وبصير لا ترتاب، وصادق لا تكذب، وقاهر لا تغلب، وبدي','34','0','0');
INSERT INTO [t29738] VALUES (34,'الجساسة بطوله، هكذا في كتابه','35','0','0');
INSERT INTO [t29738] VALUES (35,'حدثني تميم الداري، أن ناسا من قومه كانوا في البحر في سفينة لهم، فانكسرت، فركب بعضهم على','36','0','0');
INSERT INTO [t29738] VALUES (36,'إني لم أدعكم لرغبة ولا لرهبة نزلت، ولكن تميما الداري أخبرني أن قوما من أهل فلسطين ركبوا','37','0','0');
INSERT INTO [t29738] VALUES (37,'إن تميما الداري دخل علي اليوم في الهاجرة، أو في الظهيرة، فأخبرني أن بني عم له ألقتهم','38','0','0');
INSERT INTO [t29738] VALUES (38,'كانت ليلتي انقلب، فوضع نعليه عند رجليه، ووضع رداءه، وبسط طرف إزاره على فراشه، ولم يلبث','39','0','0');
INSERT INTO [t29738] VALUES (39,'من فطر صائما كتب له مثل أجره، إلا أنه لا ينقص من أجر الصائم شيئا، ومن جهز غازيا في سبيل','40','0','0');
INSERT INTO [t29738] VALUES (40,'عن كسب الإماء. رواه البخاري في صحيحه، عن علي بن الجعد، فوقع موافقة عالية، ولله الحمد ولد','41','0','0');
INSERT INTO [t29738] VALUES (41,'الله ورسوله ينهيانكم عن لحوم الحمر فإنها رجس. أخرجه البخاري في الجهاد، عن عبد الله بن','42','0','0');
INSERT INTO [t29738] VALUES (42,'نذر اعتكاف ليلة في الجاهلية، فأمره النبي صلى الله عليه وسلم أن يعتكف، فلم يعتكف حتى كان','43','0','0');
INSERT INTO [t29738] VALUES (43,'عمه غاب عن قتال بدر، فقال: أغيب عن أول قتال قاتل رسول الله صلى الله عليه وسلم المشركين،','44','0','0');
INSERT INTO [t29738] VALUES (44,'عمه غاب عن قتال بدر، فقال: غيبت عن أول قتال قاتله رسول الله صلى الله عليه وسلم المشركين،','45','0','0');
INSERT INTO [t29738] VALUES (45,'غاب أنس بن النضر عن قتال بدر، فقال: غيبت عن أول قتال قاتله رسول الله صلى الله عليه وسلم،','46','0','0');
