INSERT INTO [t29747] VALUES (1,'من لا يشكر القليل لا يشكر الكثير، والتحدث بنعمة الله شكر، والسكوت عنها كفر، والجماعة','2','0','0');
INSERT INTO [t29747] VALUES (2,'يعتكف في العشر الأواخر من رمضان.','3','0','0');
INSERT INTO [t29747] VALUES (3,'من يحسن أن يتكلم بالعربية فلا يتكلمن بالعجمية؛ فإنه يورث النفاق.','4','0','0');
INSERT INTO [t29747] VALUES (4,'من أكرم ذا شيبة كأنما قد أكرم نوحا في قومه، ومن أكرم نوحا في قومه فقد أكرم الله عز','5','0','0');
INSERT INTO [t29747] VALUES (5,'أحسني جوار نعم الله عز وجل؛ فإنها قل ما نفرت من قوم فكادت أن ترجع','6','0','0');
INSERT INTO [t29747] VALUES (6,'أعطاني ربي عز وجل في علي خصالا في الدنيا وخصالا في الآخرة، أعطاني به أنه صاحب لوائي عند','7','0','0');
INSERT INTO [t29747] VALUES (7,'إنه قد أصابت فلانا فاقة فحلت له الصدقة، حتى يصيب سدادا من العيش، فما سوى ذلك من المسألة','8','0','0');
INSERT INTO [t29747] VALUES (8,'التسبيح للرجال، والتصفيق للنساء في الصلاة.','9','0','0');
INSERT INTO [t29747] VALUES (9,'” قطع رسول الله صلى الله عليه وسلم يد سارق في مجن.','10','0','0');
INSERT INTO [t29747] VALUES (10,'وأنا متكئ على قبر، فقال: لا تؤذ صاحب هذا القبر.','11','0','0');
INSERT INTO [t29747] VALUES (11,'لا طاعة لمخلوق في معصية الخالق.','12','0','0');
INSERT INTO [t29747] VALUES (12,'توضأت أنا ورسول الله صلى الله عليه وسلم من إناء واحد قد أصابت منه الهرة قبل ذلك. لا أعلم','13','0','0');
INSERT INTO [t29747] VALUES (13,'أتدري أين تغرب؟ قلت: الله ورسوله أعلم. قال: فإنها تذهب حتى تسجد تحت العرش عند ربها،','14','0','0');
INSERT INTO [t29747] VALUES (14,'لا تجلسوا إلى القبور، ولا تصلوا إليها. هذا حديث صحيح عن رسول الله صلى الله عليه وسلم،','15','0','0');
INSERT INTO [t29747] VALUES (15,'وما شيء أحب إلى الله من الحمد.','16','0','0');
INSERT INTO [t29747] VALUES (16,'ما رأيناك تركت الصلاة على أحد إلا على هذا. فقال: إنه كان يبغض عثمان أبغضه','17','0','0');
INSERT INTO [t29747] VALUES (17,'” لم يكن رسول الله صلى الله عليه وسلم يحجبه شيء من القرآن إلا الجنابة.','18','0','0');
INSERT INTO [t29747] VALUES (18,'لو أن أحدكم إذا نزل منزلا قال: أعوذ بكلمات الله التامة من شر ما خلق لم يضره شيء في ذلك','19','0','0');
INSERT INTO [t29747] VALUES (19,'بين الركن اليماني، يقول: {ربنا آتنا في الدنيا حسنة وفي الآخرة حسنة وقنا عذاب النار}','20','0','0');
INSERT INTO [t29747] VALUES (20,'نعمتان مغبون فيهما كثير من الناس، الصحة والفراغ.','21','0','0');
INSERT INTO [t29747] VALUES (21,'من أحب أن يسبق الدائب المجتهد فليكف عن الذنوب.','22','0','0');
INSERT INTO [t29747] VALUES (22,'سألت ربي مسألة وددت أني لم أكن سألته، قلت: أي رب قد كان قبلي أنبياء منهم من سخرت له الريح','23','0','0');
INSERT INTO [t29747] VALUES (23,'هذا صراط الله المستقيم ثم خط سبلا، ثم قال: وليس من هذه السبل سبيل إلا عليه الشيطان يدعو','24','0','0');
INSERT INTO [t29747] VALUES (24,'إذا صليت المكتوبة، فقولي: سبحان الله عشرا، والحمد لله عشرا، والله أكبر عشرا، ثم سلي ما','25','0','0');
INSERT INTO [t29747] VALUES (25,'” إن بني إسرائيل استخلفوا خليفة عليهم بعد موسى عليه السلام، فقام يصلي في القمراء فوق بيت','26','0','0');
INSERT INTO [t29747] VALUES (26,'من جعل قاضيا فقد ذبح بغير سكين.','27','0','0');
INSERT INTO [t29747] VALUES (27,'الدنيا سجن المؤمن، وجنة الكافر.','28','0','0');
INSERT INTO [t29747] VALUES (28,'جنات الفردوس أربعة، ثنتان من ذهب، وثنتان من فضة , وحليتهما وآنيتهما وما فيهما، وليس بين','29','0','0');
INSERT INTO [t29747] VALUES (29,'مثل أمتي مثل المطر، لا يدرى أوله خير أو آخره.','30','0','0');
INSERT INTO [t29747] VALUES (30,'بشر المشائين في ظلم الليل إلى المساجد من نور تام يوم القيامة.','31','0','0');
INSERT INTO [t29747] VALUES (31,'التأني من الله، والعجلة من الشيطان، وما أحد أكثر معاذير من الله عز وجل','32','0','0');
INSERT INTO [t29747] VALUES (32,'تسمونهم محمدا ثم تسبونهم','33','0','0');
INSERT INTO [t29747] VALUES (33,'إن من حق إجلال الله عز وجل على العباد إكرام حامل القرآن غير الجافي عنه ولا الغالي','34','0','0');
INSERT INTO [t29747] VALUES (34,'احفظوني في أصحابي وأزواجي وأصهاري','35','0','0');
INSERT INTO [t29747] VALUES (35,'كلمتان خفيفتان على اللسان ثقيلتان في الميزان حبيبتان إلى الرحمن: سبحان الله وبحمده سبحان','36','0','0');
INSERT INTO [t29747] VALUES (36,'” قدم أصحاب النبي صلى الله عليه وسلم الأهواز، فكانوا يصلون في الطرق','37','0','0');
INSERT INTO [t29747] VALUES (37,'سيد آي القرآن آية الكرسي: {الله لا إله إلا هو الحي القيوم} [البقرة: ٢٥٥] أخبرنا القاسم بن','38','0','0');
INSERT INTO [t29747] VALUES (38,'إذا اختلف الناس أن أتخذ سيفا من خشب، وقد اتخذته، فإن شئت خرجت معك.','39','0','0');
INSERT INTO [t29747] VALUES (39,'إن الله لعن الخمر بعينها، وعاصرها، ومعتصرها، وبائعها، ومشتريها، وحاملها، والمحمولة إليه،','40','0','0');
INSERT INTO [t29747] VALUES (40,'” كانت أموال بني النضير مما أفاء الله عز وجل على رسوله صلى الله عليه وسلم، مما لم يوجف','41','0','0');
INSERT INTO [t29747] VALUES (41,'من كذب علي متعمدا فليتبوأ مقعده من النار.','42','0','0');
INSERT INTO [t29747] VALUES (42,'العلم ثلاثة وما سواهن فهو فضل: آية محكمة، أو فريضة عادلة، أو سنة','43','0','0');
INSERT INTO [t29747] VALUES (43,'الحمى قطعة من النار، فأبردوها عنكم بالماء البارد.','44','0','0');
INSERT INTO [t29747] VALUES (44,'عن كسر السكة الجائزة إلا من بأس','45','0','0');
INSERT INTO [t29747] VALUES (45,'أرحم ما يكون الله على عبده إذا وضع في حفرته','46','0','0');
INSERT INTO [t29747] VALUES (46,'أحب الناس إلى الله عز وجل أولهم دخولا المساجد، وآخرهم خروجا','47','0','0');
INSERT INTO [t29747] VALUES (47,'من كان عليه دين وهو يريد أداءه فهو في جوار الله عز وجل فأحببت أن أكون في ذمة الله عز','48','0','0');
INSERT INTO [t29747] VALUES (48,'إذا اغتسل من جنابة بدأ فيغسل يديه، ثم يتوضأ كما يتوضأ للصلاة، ثم يغمس يديه في الماء فيخلل','49','0','0');
INSERT INTO [t29747] VALUES (49,'ليس من البر الصيام في السفر','50','0','0');
INSERT INTO [t29747] VALUES (50,'من أصبح معافى في بدنه آمنا في سربه عنده قوت يومه فكأنما حيزت له الدنيا، يا ابن جعشم يكفيك','51','0','0');
INSERT INTO [t29747] VALUES (51,'لكل أمة مجوس، ومجوس هذه الأمة القدرية، إن مرضوا فلا تعودوهم، وإن ماتوا فلا تشهدوهم، وإن','52','0','0');
INSERT INTO [t29747] VALUES (52,'من أحب أن يجتهد في الدعاء فليقل: اللهم أعني على ذكرك وشكرك وحسن عبادتك','53','0','0');
INSERT INTO [t29747] VALUES (53,'{وإذ أخذ ربك من بني آدم من ظهورهم ذريتهم} [الأعراف: ١٧٢] .','54','0','0');
INSERT INTO [t29747] VALUES (54,'ما كان لنا إلا جلد كبش ننام عليه بالليل، أنا على ناحية وفاطمة على','55','0','0');
INSERT INTO [t29747] VALUES (55,'كيف أنت؟ وكيف حالك؟ قال: كيف حال من أمسى وأصبح ينتظر الموت، لا يدري ما يصنع الله','56','0','0');
