INSERT INTO [t29752] VALUES (1,'لا عدوى ولا طيرة، ويعجبني الفأل','2','0','0');
INSERT INTO [t29752] VALUES (2,'من كان صائما من الشهر فليصم أيام البيض ثلاث عشرة وأربع عشرة وخمس عشرة','3','0','0');
INSERT INTO [t29752] VALUES (3,'إن الإسلام بدأ غريبا وسيعود كما بدأ، أو ليأرزن الإسلام بين المسجدين كما تأرز الحية في','4','0','0');
INSERT INTO [t29752] VALUES (4,'ما نفعني مال ما نفعني مال أبي بكر.','5','0','0');
INSERT INTO [t29752] VALUES (5,'فتحت القرى بالسيف وفتحت المدينة بالقرآن','6','0','0');
INSERT INTO [t29752] VALUES (6,'ما زلت أسائل عن مالي من أين أصبته وفي أي شيء أنفقته حتى ظننت أني لا أراك. قال عبد الرحمن:','7','0','0');
INSERT INTO [t29752] VALUES (7,'المؤمن في الدنيا كالغريب، لا يجزع من ذلها، ولا ينافس في عزها , للناس حال وله','8','0','0');
INSERT INTO [t29752] VALUES (8,'الحج عرفات، فمن أدرك عرفة قبل طلوع الفجر فقد أدرك الحج , وأيام منى ثلاث، {فمن تعجل في','9','0','0');
INSERT INTO [t29752] VALUES (9,'لما بعثني رسول الله صلى الله عليه وسلم إلى اليمن أرسل في إثري، فرددت. فقال: تدري لم','10','0','0');
INSERT INTO [t29752] VALUES (10,'تفل في رجل عمرو بن معاذ حين قطعت رجله , فبرأ','11','0','0');
INSERT INTO [t29752] VALUES (11,'أفرد الحج','12','0','0');
INSERT INTO [t29752] VALUES (12,'نهى عن الشغار.','13','0','0');
INSERT INTO [t29752] VALUES (13,'لأن يجلس أحدكم على جمرة، أو على نار فتحرق ثيابه حتى تخلص إليه , خير من أن يجلس على','14','0','0');
INSERT INTO [t29752] VALUES (14,'شهدت مع عمومتي حلف المطيبين، فما أحب أن لي حمر النعم وإني أنكثه','15','0','0');
INSERT INTO [t29752] VALUES (15,'صلى صلاة الصبح، فقال: أشاهد فلان؟ قالوا: لا. قال: أما إن هاتين الصلاتين أثقل الصلوات على','16','0','0');
INSERT INTO [t29752] VALUES (16,'مالي وللدنيا، ما أنا والدنيا إلا كراكب استظل تحت شجرة، ثم راح فتركها','17','0','0');
INSERT INTO [t29752] VALUES (17,'التحيات لله والصلوات والطيبات، السلام عليك أيها النبي ورحمة الله وبركاته، السلام علينا','18','0','0');
INSERT INTO [t29752] VALUES (18,'لا تقوم الساعة حتى يبعث دجالون كذابون قريبا من ثلاثين، كلهم يزعم أنه رسول','19','0','0');
INSERT INTO [t29752] VALUES (19,'أسمع الله ولا تسمعني.','20','0','0');
INSERT INTO [t29752] VALUES (20,'يكون في أمتي المهدي، إن قصر عمره سبع سنين، وإلا فثمان سنين، وإلا فتسع، تنعم أمتي في زمانه','21','0','0');
INSERT INTO [t29752] VALUES (21,'تحت كل شعرة جنابة فاغسلوا الشعر وانقوا البشرة.','22','0','0');
INSERT INTO [t29752] VALUES (22,'نعم الإدام الخل. أخبرنا أبو عبد الله، قال: ثنا يوسف، قال: ثنا محمد بن موسى الحلواني، قال:','23','0','0');
INSERT INTO [t29752] VALUES (23,'ذكر قيام الليل، ففاضت عيناه حتى تحادرت دموعه، ثم قرأ {تتجافى جنوبهم عن المضاجع} [السجدة:','24','0','0');
INSERT INTO [t29752] VALUES (24,'قضاء قضاه الله علي عز وجل على لسان نبيكم صلى الله عليه وسلم , النبي الأمي أنه لا يحبني','25','0','0');
INSERT INTO [t29752] VALUES (25,'لا ينظر الله عز وجل إلى مسبل أخبرنا أبو عبد الله، قال: ثنا يوسف، قال: ثنا محمد بن إسحاق','26','0','0');
INSERT INTO [t29752] VALUES (26,'لقد مشيت إلى رسول الله صلى الله عليه مرارا بخبز شعير وإهالة سنخة , ولقد سمعته يقول: ما','27','0','0');
INSERT INTO [t29752] VALUES (27,'يكون في آخر الزمان أمراء ظلمة , ووزراء فسقة , وقضاة خونة , وفقهاء كذبة، فمن أدركهم منكم','28','0','0');
INSERT INTO [t29752] VALUES (28,'من يرد الله به خيرا يصب منه','29','0','0');
INSERT INTO [t29752] VALUES (29,'إن المتخلعات هن المنافقات','30','0','0');
INSERT INTO [t29752] VALUES (30,'حججنا مع رسول الله صلى الله عليه وسلم , ومعنا النساء والصبيان، فلبينا عن الصبيان ورمينا','31','0','0');
INSERT INTO [t29752] VALUES (31,'” كان رسول الله صلى الله عليه وسلم وأبو بكر وعمر يقرؤون {مالك يوم الدين} [الفاتحة:','32','0','0');
INSERT INTO [t29752] VALUES (32,'أتدرون أي سورة أنزلت علي آنفا؟ الكوثر نهر في الجنة وعدنيه ربي، ترده أمتي فيختلج الرجل','33','0','0');
INSERT INTO [t29752] VALUES (33,'إذا قاتل أحدكم فليجتنب الوجه','34','0','0');
INSERT INTO [t29752] VALUES (34,'ليس بحيض، إنما هو عرق. فأمرها أن تترك الصلاة قدر إقرائها وحيضتها، وتغتسل','35','0','0');
INSERT INTO [t29752] VALUES (35,'أقبل أبو بكر وعمر رضي الله عنهما , قال: يا علي هذان سيدا كهول أهل الجنة من الأولين','36','0','0');
INSERT INTO [t29752] VALUES (36,'ما من مسلمين يموت بينهما ثلاثة لم يبلغوا الحنث إلا غفر الله لهما بفضل رحمته','37','0','0');
INSERT INTO [t29752] VALUES (37,'امسحوا ثلاثة أيام ولياليهن، إلا من جنابة، وللمقيم يوم وليلة','38','0','0');
INSERT INTO [t29752] VALUES (38,'يقول الله عز وجل يوم القيامة: أين المتحابون في جلالي، اليوم أظلهم في ظلي يوم لا ظل إلا','39','0','0');
INSERT INTO [t29752] VALUES (39,'صلى على النجاشي. فقال: هذا منكر أن النبي صلى عليه. فقال له ابن أبي سمينة: من رواه عن','40','0','0');
INSERT INTO [t29752] VALUES (40,'تغسل رأس رسول الله صلى الله عليه وسلم وهو معتكف، يضع إليها رأسه في حجرها وهي','41','0','0');
INSERT INTO [t29752] VALUES (41,'لا يدخل الجنة من لا يأمن جاره بوائقه','42','0','0');
INSERT INTO [t29752] VALUES (42,'تقتل عمارا الفئة الباغية','43','0','0');
INSERT INTO [t29752] VALUES (43,'هذا كتاب كتبه رب العالمين، تسمية أهل الجنة، وتسمية أهل النار وآبائهم، ثم أجمل على آخرهم،','44','0','0');
INSERT INTO [t29752] VALUES (44,'أنه لا يحبني إلا مؤمن ولا يبغضني إلا منافق. قال يوسف: قال لنا أحمد بن محمد بن محمد:','45','0','0');
INSERT INTO [t29752] VALUES (45,'اقتلوا الأسودين في الصلاة الحية والعقرب أخبرنا أبو عبد الله، قال: ثنا يوسف، قال: ثنا محمد','46','0','0');
INSERT INTO [t29752] VALUES (46,'كان المهاجرون يوم بدر نيفا على ستين، وكان الأنصار نيفا على أربعين','47','0','0');
INSERT INTO [t29752] VALUES (47,'لما كان يوم حنين أقبلت هوازن وغيرهم بذراريهم ونعمهم، ومع النبي صلى الله عليه يومئذ عشرة','48','0','0');
INSERT INTO [t29752] VALUES (48,'” كنا نعبد الحجارة والأوثان، إذا رأينا حجرا أحسن من حجر ألقيناه وأخذنا غيره، ولا نعرف','49','0','0');
INSERT INTO [t29752] VALUES (49,'إذا حدث عن رسول الله صلى الله عليه وسلم قال: أو كما قال.','50','0','0');
INSERT INTO [t29752] VALUES (50,'أيما رجل باع متاعا فأفلس المبتاع ولم يقبض الذي باعه من الثمن شيئا، فإن وجد البائع سلعته','51','0','0');
INSERT INTO [t29752] VALUES (51,'لا حسد إلا في اثنتين: رجل آتاه الله عز وجل مالا فسلطه على هلكته في الحق، ورجل آتاه الله','52','0','0');
