INSERT INTO [t29767] VALUES (1,'ليس فيما دون خمس ذود صدقة، وليس فيما دون خمس أو أقل صدقة','2','0','0');
INSERT INTO [t29767] VALUES (2,'ليس فيما دون خمسة أوسق صدقة ولا فيما دون خمس ذود صدقة، ولا فيما دون خمس أواق','3','0','0');
INSERT INTO [t29767] VALUES (3,'لا تحل في البر والتمر زكاة حتى يبلغ خمسة أوسق، ولا تحل في الإبل زكاة حتى تبلغ خمس','4','0','0');
INSERT INTO [t29767] VALUES (4,'ليس فيما دون خمسة أوسق من التمر صدقة، وليس فيما دون خمس أواق من الورق صدقة، وليس فيما دون','5','0','0');
INSERT INTO [t29767] VALUES (5,'ليس على الرجل المسلم زكاة في كرمه ولا في زرعه إذا كان أقل من خمسة أوسق','6','0','0');
INSERT INTO [t29767] VALUES (6,'يضع الرجل إحدى رجليه على الأخرى وهو متكئ','7','0','0');
INSERT INTO [t29767] VALUES (7,'إذا أقيمت الصلاة فلا تقوموا حتى تروني','8','0','0');
INSERT INTO [t29767] VALUES (8,'يمس الرجل ذكره بيمينه','9','0','0');
INSERT INTO [t29767] VALUES (9,'ما أتى الله عالما علما إلا أخذ عليه الميثاق ألا يكتمه','10','0','0');
INSERT INTO [t29767] VALUES (10,'أيما امرأة نكحت دون إذن وليها فنكاحها باطل، فإن أصابها فلها مهرها، وإن اشتجروا فالسلطان','11','0','0');
INSERT INTO [t29767] VALUES (11,'من حسن إسلام المرء تركه ما لا يعنيه','12','0','0');
INSERT INTO [t29767] VALUES (12,'إذا قلت لأخيك: أنصت , والإمام يخطب يوم الجمعة فقد لغوت','13','0','0');
INSERT INTO [t29767] VALUES (13,'إذا قلت لصاحبك أنصت والإمام يخطب يوم الجمعة فقد لغوت','14','0','0');
INSERT INTO [t29767] VALUES (14,'نهى أن يتنفس أحد في الإناء','15','0','0');
INSERT INTO [t29767] VALUES (15,'ما من مؤمن إلا يأتي يوم القيامة يعني بيهودي أو نصراني، يقول: هذا فدائي من','16','0','0');
INSERT INTO [t29767] VALUES (16,'لا يموت رجل مسلم إلا أدخل الله مكانه النار يهوديا أو نصرانيا.','17','0','0');
INSERT INTO [t29767] VALUES (17,'هل من قوم أعظم منا أجرا؟ آمنا بك واتبعناك، قال: ما منعكم من ذلك ورسول الله بين أظهركم ,','18','0','0');
INSERT INTO [t29767] VALUES (18,'من أنظر معسرا أو وضع عنه أظله الله في ظله يوم لا ظل إلا ظله.','19','0','0');
INSERT INTO [t29767] VALUES (19,'ما من رجل يسلك طريقا يطلب فيه علما إلا سهل الله له به طريقا إلى الجنة، ومن أبطأ به عمله','20','0','0');
INSERT INTO [t29767] VALUES (20,'الخاصرة عرق الكلوة، فإذا تحرك آذت صاحبها , فداووها بالماء المحرق','21','0','0');
INSERT INTO [t29767] VALUES (21,'الإيمان بضع وسبعون بابا، أفضلها شهادة أن لا إله إلا الله، وأهونها إماطة الأذى عن الطريق،','22','0','0');
INSERT INTO [t29767] VALUES (22,'السمت الصالح والهدي الصالح والاقتصاد جزء من خمسة وعشرين جزءا من النبوة','23','0','0');
INSERT INTO [t29767] VALUES (23,'تلبية النبي صلى الله عليه وسلم: لبيك اللهم لبيك، لبيك لا شريك لك لبيك، إن الحمد والنعمة','24','0','0');
INSERT INTO [t29767] VALUES (24,'كان منزلا نزله النبي صلى الله عليه وسلم يكون أسمح لخروجه , تعني الأبطح','25','0','0');
INSERT INTO [t29767] VALUES (25,'ما أظلت الخضراء ولا أقلت الغبراء أصدق لهجة من أبي ذر، ومن سره أن ينظر إلى مثل عيسى في','26','0','0');
INSERT INTO [t29767] VALUES (26,'من مات وهو يعلم أنه لا إله إلا الله دخل الجنة. لفظ الرافقي.','27','0','0');
INSERT INTO [t29767] VALUES (27,'لما صور الله آدم عليه السلام تركه فجعل إبليس ينظر إليه , فلما رآه أجوف، قال:','28','0','0');
INSERT INTO [t29767] VALUES (28,'إذا رأيتم الجنازة فقوموا , فمن تبعها فلا يقعد حتى توضع','29','0','0');
INSERT INTO [t29767] VALUES (29,'خلق أحدكم يجمع في بطن أمه أربعين يوما، ثم يكون مضغة، ثم يكون علقة، ثم يجئ الملك، فيقول:','30','0','0');
INSERT INTO [t29767] VALUES (30,'لا عليكم ألا تعجبوا بأحد حتى تنظروا بم يختم له؛ فإن الرجل قد يعمل بعمل أهل الجنة برهة من','31','0','0');
INSERT INTO [t29767] VALUES (31,'هذا كتاب كتبه رب العالمين فيه تسمية أهل الجنة وتسمية آبائهم، ثم أجمل على آخرهم فلا يزاد','32','0','0');
INSERT INTO [t29767] VALUES (32,'الرجل ليعمل بعمل أهل الجنة، وإنه لمكتوب في الكتاب أنه من أهل النار، فإذا كان قبل موته','33','0','0');
INSERT INTO [t29767] VALUES (33,'إذا دعي إلى جنازة سأل عنها، فإن أثني عليها خيرا قام فصلى عليها، وإن أثني عليها غير ذلك،','34','0','0');
INSERT INTO [t29767] VALUES (34,'لبيك بحجة وعمرة معا. . . . . . . . . قال: أن يسلم الناس من لسانك.','35','0','0');
INSERT INTO [t29767] VALUES (35,'أي الأعمال أفضل؟ قال: إيمان بالله ورسوله. قيل: ثم ماذا؟ قال: ثم الجهاد في سبيل','36','0','0');
INSERT INTO [t29767] VALUES (36,'جمع بين الظهر والعصر. . . . . . . . وقال: يا بني لقد جزعت عليك جزعا لم أجزعه على شيء قط','37','0','0');
INSERT INTO [t29767] VALUES (37,'طلع له أحد، فقال: هذا جبل يحبنا ونحبه، اللهم إن إبراهيم حرم مكة، وإني أحرم ما بين','38','0','0');
INSERT INTO [t29767] VALUES (38,'أي الكلام أحب إلى الله؟ قال: ما اصطفاه الله لملائكته: سبحان ربي وبحمده، سبحان ربي','39','0','0');
INSERT INTO [t29767] VALUES (39,'أراك ضعيفا، وإني أحب لك ما أحب لنفسي , لا تأمرن على اثنين، ولا تولين مال','40','0','0');
INSERT INTO [t29767] VALUES (40,'كل راع مسئول عن رعيته، الأمير عن رعيته، والرجل عن أهله، والمرأة عن زوجها، والعبد عن','41','0','0');
INSERT INTO [t29767] VALUES (41,'الماهر في القرآن مع السفرة الكرام البررة، والذي يقرأ القرآن يتتعتع فيه وهو عليه شديد فله','42','0','0');
INSERT INTO [t29767] VALUES (42,'لا تزال أمتي على الفطرة ما لم تؤخر العشاء إلى اشتباك النجوم','43','0','0');
INSERT INTO [t29767] VALUES (43,'من قال في سوق من هذه الأسواق: لا إله إلا الله وحده لا شريك له، له الملك، وله الحمد، يحيي','44','0','0');
INSERT INTO [t29767] VALUES (44,'من رأى عبدا به بلاء، فقال: الحمد لله الذي عافاني مما ابتلاك به، وفضلني على كثير ممن خلق','45','0','0');
INSERT INTO [t29767] VALUES (45,'في حوضي من الأباريق بعدد نجوم السماء','46','0','0');
INSERT INTO [t29767] VALUES (46,'لو أن جميع الناس اشتركوا في دم رجل مؤمن لكان حقا على الله واجبا أن يدخلهم','47','0','0');
