INSERT INTO [t29775] VALUES (1,'إني أصبت مالا بخيبر لم أصب مالا قط أحب إلي منه , فقال له: إن شئت تصدقت وإن شئت أمسكت','2','0','0');
INSERT INTO [t29775] VALUES (2,'تعرض أعمال بني آدم في كل يوم اثنين وفي كل يوم خميس، فيرحم المترحمين ويغفر للمستغفرين، ثم','3','0','0');
INSERT INTO [t29775] VALUES (3,'اللهم إنهم حفاة فاحملهم، اللهم إنهم عراة فاكسهم، اللهم إنهم جياع','4','0','0');
INSERT INTO [t29775] VALUES (4,'ما عمل أهل الجنة؟ قال: الصدق، وإذا صدق العبد بر وإذا بر آمن وإذا آمن دخل','5','0','0');
INSERT INTO [t29775] VALUES (5,'لما احتلمت دخلت على رسول الله صلى الله عليه وسلم فأخبرته، فقال: لا تدخل على','6','0','0');
INSERT INTO [t29775] VALUES (6,'بعثت بجوامع الكلم، ونصرت بالرعب، وبينا أنا نائم أتيت بمفاتيح خزائن الأرض، فوضعت في يدي،','7','0','0');
INSERT INTO [t29775] VALUES (7,'إن الله لا يستحي من الحق، أرأيت المرأة ترى في النوم ما يرى الرجل، أتغتسل؟ قال:','8','0','0');
INSERT INTO [t29775] VALUES (8,'إياكم والظن فإن الظن أكذب الحديث، ولا تحسسوا، ولا تجسسوا، ولا تنافسوا، ولا تحاسدوا، ولا','9','0','0');
INSERT INTO [t29775] VALUES (9,'إياكم والظن فإن الظن أكذب الحديث، ولا تجسسوا، ولا تباغضوا، ولا تقاطعوا، ولا تحاسدوا، ولا','10','0','0');
INSERT INTO [t29775] VALUES (10,'لو يعلم الناس ما في النداء والصف الأول، ثم لم يجدوا إلا أن يستهموا عليه لاستهموا، أو لو','11','0','0');
INSERT INTO [t29775] VALUES (11,'من توضأ فليستنثر، ومن استجمر فليوتر','12','0','0');
INSERT INTO [t29775] VALUES (12,'إن قدر حوضي لما بين أيلة وصنعاء في اليمن، وإن فيه من الأباريق بعدد نجوم','13','0','0');
INSERT INTO [t29775] VALUES (13,'أي الناس أحب إليك؟ قال: لم؟ قال: لأحب من تحب. قال: عائشة. قال: لست أسأل عن النساء، إنما','14','0','0');
INSERT INTO [t29775] VALUES (14,'اللهم صل على أبي بكر فإنه يحبك ويحب رسولك، اللهم صل على عمر فإنه يحبك ويحب رسولك، اللهم','15','0','0');
INSERT INTO [t29775] VALUES (15,'إن ابني كان بطني له وعاء وثديي له سقاء وحجري له حواء، وإن أباه يزعم أنه سينزعه مني , فقال','16','0','0');
INSERT INTO [t29775] VALUES (16,'ليس على الرجل المسلم زكاة في كرمة، ولا في زرعة إذا كان أقل من خمسة','17','0','0');
INSERT INTO [t29775] VALUES (17,'إن من الحنطة خمرا، ومن الشعير خمرا، ومن الزبيب خمرا، ومن التمر خمرا، ومن العسل خمرا، وأنا','18','0','0');
INSERT INTO [t29775] VALUES (18,'كل مسكر حرام , ومن شرب الخمر في الدنيا فمات وهو يدمنها لم يتب لم يشربها في','19','0','0');
INSERT INTO [t29775] VALUES (19,'من مات مدمن خمر لقي الله عز وجل كعابد وثن','20','0','0');
INSERT INTO [t29775] VALUES (20,'في الخمر قليلها إثم وكثيرها فسوق، ولا يقبل الله له صلاة أربعين ليلة فإن مات فيها مات ميتة','21','0','0');
INSERT INTO [t29775] VALUES (21,'ما أسكر كثيره فقليله حرام','22','0','0');
INSERT INTO [t29775] VALUES (22,'قليل ما كثيره مسكر، وكثير ما قليله مسكر حرام','23','0','0');
INSERT INTO [t29775] VALUES (23,'نهى عن ثمن الخمر ومهر البغي وثمن الكلب، وقال: إذا أتاك يطلب ثمن الكلب فاملأ كفيه','24','0','0');
INSERT INTO [t29775] VALUES (24,'من شرب الخمر فاجلدوه، ثم إذا شرب فاجلدوه، ثم إذا شرب فاجلدوه، ثم إذا شرب في الرابعة','25','0','0');
INSERT INTO [t29775] VALUES (25,'جلد رجلا في الخمر أربعين جلدة بسوط له طرفان','26','0','0');
INSERT INTO [t29775] VALUES (26,'لعن الله اليهود، حرمت عليهم الشحوم فجملوها فباعوها؟','27','0','0');
INSERT INTO [t29775] VALUES (27,'إياكم والخمر فإنها مفتاح كل شر، أتي رجل فقيل له: إما أن تحرق هذا الكتاب وإما أن تقتل هذا','28','0','0');
INSERT INTO [t29775] VALUES (28,'بينا نحن قعود على شراب لنا على رملة، ونحن ثلاثة أو أربعة، عندنا باطية لنا، ونحن نشرب','29','0','0');
INSERT INTO [t29775] VALUES (29,'ثلاثة لا ترى أعينهم النار زاد ابن الحارث: يوم القيامة ثم اتفقوا: عين بكت من خشية الله،','30','0','0');
INSERT INTO [t29775] VALUES (30,'أربع خلال من كن فيه كان منافقا خالصا: من إذا حدث كذب، وإذا وعد أخلف، وإذا عاهد غدر، وإذا','31','0','0');
INSERT INTO [t29775] VALUES (31,'{الله يتوفى الأنفس حين موتها} [الزمر: ٤٢] . قال: تلتقي أرواح الأحياء والأموات في السماء','32','0','0');
INSERT INTO [t29775] VALUES (32,'لولا ثلاث خلال ما أحببت أن أبقى في الدنيا. قلت: وما هن؟ قال: وضعي وجهي ساجدا لخالقي جل','33','0','0');
INSERT INTO [t29775] VALUES (33,'لا يحل لامرأة تؤمن بالله واليوم الآخر تحد على ميت فوق ثلاث إلا على زوج','34','0','0');
INSERT INTO [t29775] VALUES (34,'يصلي صلاته من الليل وأنا معترضة بينه وبين القبلة كاعتراض الجنازة','35','0','0');
INSERT INTO [t29775] VALUES (35,'من اقتنى كلبا إلا كلب ماشية نقص من أجره كل يوم قيراطان','36','0','0');
INSERT INTO [t29775] VALUES (36,'ألا أخبركم بخياركم من شراركم , خياركم أطولكم أعمارا وأحسنكم أعمالا','37','0','0');
INSERT INTO [t29775] VALUES (37,'الولد للفراش وللعاهر الحجر','38','0','0');
INSERT INTO [t29775] VALUES (38,'أشد الناس يوم القيامة عذابا الذين يشبهون خلق الله','39','0','0');
INSERT INTO [t29775] VALUES (39,'إن الله عز وجل ليقبل توبة العبد ما لم يغرغر','40','0','0');
INSERT INTO [t29775] VALUES (40,'الدنيا سجن المؤمن وجنة الكافر','41','0','0');
INSERT INTO [t29775] VALUES (41,'ما آتى الله عز وجل عالما علما إلا أخذ عليه الميثاق ألا يكتم','42','0','0');
INSERT INTO [t29775] VALUES (42,'ما من نفس إلا قد كتب مدخلها ومخرجها وما هو لاقيه. قال: فقال رجل من الأنصار: ففيم العمل يا','43','0','0');
INSERT INTO [t29775] VALUES (43,'لا يقوم الرجل للرجل من مجلسه، ولكن أفسحوا يفسح الله لكم','44','0','0');
INSERT INTO [t29775] VALUES (44,'لا يبغض أبا بكر وعمر مؤمن، ولا يحبهما منافق','45','0','0');
INSERT INTO [t29775] VALUES (45,'لو أنكم تتفقان لي على أمر واحد ما عصيتكما في مشورة أبدا، ولقد ضرب لي ربي عز وجل لكما','46','0','0');
INSERT INTO [t29775] VALUES (46,'تعوذوا بالله من جب الحزن. قالوا: يا رسول الله، وما جب الحزن؟ قال: واد في جهنم يتعوذ منه','47','0','0');
INSERT INTO [t29775] VALUES (47,'من حج فلم يرفث ولم يفسق يرجع كيوم ولدته أمه','48','0','0');
INSERT INTO [t29775] VALUES (48,'النفقة في الحج كالنفقة في سبيل الله سبعين ضعفا','49','0','0');
INSERT INTO [t29775] VALUES (49,'من أضحى يوما ملبيا حتى تغرب الشمس غربت بذنوبه وعاد كما ولدته أمه','50','0','0');
INSERT INTO [t29775] VALUES (50,'الحجاج والعمار وفد الله، إن سألوا أعطوا، وإن دعوا أجيبوا، وإن أنفقوا أخلف','51','0','0');
INSERT INTO [t29775] VALUES (51,'الغازي في سبيل الله والحاج إلى بيت الله وفد الله عز وجل، دعاهم فأجابوه','52','0','0');
INSERT INTO [t29775] VALUES (52,'إن أبي أدرك الحج وهو شيخ كبير لا يستطيع أن يركب على البعير، أفأحج عنه؟ قال: حجي','53','0','0');
INSERT INTO [t29775] VALUES (53,'إذا رأيتم هلال ذي الحجة وأراد أحدكم أن يضحي، فليمسك عن شعره وأظفاره','54','0','0');
INSERT INTO [t29775] VALUES (54,'يقصر بمشقص','55','0','0');
INSERT INTO [t29775] VALUES (55,'أن الصلاة الوسطى هي العصر، وأن الحج الأكبر يوم النحر، وأن إدبار السجود الركعتان بعد','56','0','0');
INSERT INTO [t29775] VALUES (56,'يوم عرفة، ورسول الله صلى الله عليه يصلي بالناس، ونحن على أتان لنا، فمررنا ببعض الصف','57','0','0');
INSERT INTO [t29775] VALUES (57,'صليت مع رسول الله صلى الله عليه الظهر بالمدينة أربعا، وبذي الحليفة','58','0','0');
INSERT INTO [t29775] VALUES (58,'اغسلوه بماء وسدر، وادفنوه في ثوبه، ولا تخمروا رأسه، فإن الله يبعثه وهو','59','0','0');
INSERT INTO [t29775] VALUES (59,'دعا لأمته عشية عرفة بالمغفرة والرحمة، وأكثر الدعاء، فأجابه: أني قد فعلت إلا ظلم بعضهم','60','0','0');
INSERT INTO [t29775] VALUES (60,'إذا كان يوم عرفة ينزل الرب جل وعز إلى السماء الدنيا، فيباهي بهم الملائكة، فيقول: انظروا','61','0','0');
INSERT INTO [t29775] VALUES (61,'اسمعوا قولي لعلي لا ألقاكم بعدها في هذا الموقف، يأيها الناس إن دماءكم وأموالكم حرام إلى','62','0','0');
INSERT INTO [t29775] VALUES (62,'ابن خطل متعلق بأستار الكعبة. فقال رسول الله صلى الله عليه: اقتلوه.','63','0','0');
INSERT INTO [t29775] VALUES (63,'من تيمم بكسب حرام حاجا كان في شخص في غير طاعة الله، حتى إذا وضع رجله في الغرز، وبعث','64','0','0');
INSERT INTO [t29775] VALUES (64,'رأيت النبي صلى الله عليه على ناقة صهباء يرمي الجمرة، لا ضرب ولا طرد ولا جلد ولا','65','0','0');
INSERT INTO [t29775] VALUES (65,'اللهم حجة لا رياء فيها ولا سمعة','66','0','0');
INSERT INTO [t29775] VALUES (66,'أعطاه دينارا يشتري له شاة للضحية، فاشترى له شاتين، فباع إحداهما بدينار، فأتى النبي صلى','67','0','0');
INSERT INTO [t29775] VALUES (67,'من أخاف أهل المدينة فعليه لعنة الله والملائكة والناس أجمعين','68','0','0');
INSERT INTO [t29775] VALUES (68,'من جاءني زائرا لم تنزعه حاجة إلا زيارتي، كان حقا علي أن أكون له شفيعا يوم','69','0','0');
INSERT INTO [t29775] VALUES (69,'من زار قبري وجبت له شفاعتي','70','0','0');
