INSERT INTO [t29778] VALUES (1,'الأعمال بالنية، ولكل امرئ ما نوى، فمن كانت هجرته إلى الله عز وجل فهجرته إلى ما هاجر إليه،','2','0','0');
INSERT INTO [t29778] VALUES (2,'خرج بالعباس معه ليستسقي به , ويقول: اللهم إنا كنا إذا قحطنا على عهد نبينا صلى الله عليه','3','0','0');
INSERT INTO [t29778] VALUES (3,'ويل للأعقاب من النار. أو العراقيب. أخرجه البخاري، عن آدم وهو ابن أبي إياس أبو الحسين','4','0','0');
INSERT INTO [t29778] VALUES (4,'إذا رأى المطر قال: اللهم صيبا هنيا.','5','0','0');
INSERT INTO [t29778] VALUES (5,'إذا صام أحدكم يوما فنسي فأكل وشرب فليتم صومه؛ فإنما أطعمه الله عز وجل وسقاه. أخرجه','6','0','0');
INSERT INTO [t29778] VALUES (6,'{يوم يقوم الناس لرب العالمين} [المطففين: ٦] . قال: يقومون حتى يبلغ الرشح أطراف','7','0','0');
INSERT INTO [t29778] VALUES (7,'كتاب الله القصاص. فعفى القوم. فقال رسول الله صلى الله عليه وسلم: إن من عباد الله من لو','8','0','0');
INSERT INTO [t29778] VALUES (8,'{للذين أحسنوا الحسنى وزيادة} [يونس: ٢٦] . قال: إذا دخل أهل الجنة الجنة وأهل النار النار','9','0','0');
INSERT INTO [t29778] VALUES (9,'أي الأعمال أحب إلى الله عز وجل؟ قال: الصلاة لوقتها. قلت: ثم أي؟ قال: بر الوالدين. قلت: ثم','10','0','0');
INSERT INTO [t29778] VALUES (10,'يمينك على ما يصدقك به صاحبك.','11','0','0');
INSERT INTO [t29778] VALUES (11,'أشهد أن لا إله إلا الله، وأني رسول الله , لا يلقى بها عبد غير شاك فيحجب عن الجنة. قال','12','0','0');
INSERT INTO [t29778] VALUES (12,'صليت خلف النبي صلى الله عليه وسلم وأبي بكر وعمر وعثمان رضي الله عنهم، فلم أسمع أحدا منهم','13','0','0');
INSERT INTO [t29778] VALUES (13,'مثل المؤمن مثل الزرع لا يزال الريح تميله، ولا يزال المؤمن يصيبه بلاء، ومثل المنافق كمثل','14','0','0');
INSERT INTO [t29778] VALUES (14,'كانت تحت رجل من بني مخزوم فطلقها ألبتة، فأرسلت إلى أهله تبتغي النفقة فقالوا: ليست علينا','15','0','0');
INSERT INTO [t29778] VALUES (15,'الله عز وجل إذا قضى الأمر من السماء ضربت الملائكة أجنحتها خضعانا لقوله كصوت السلسلة على','16','0','0');
INSERT INTO [t29778] VALUES (16,'خير أمتي القرن الذي بعثت فيهم ثم الذين يلونهم ثم الذين يلونهم والله أعلم قال الثالثة أم','17','0','0');
INSERT INTO [t29778] VALUES (17,'بعثت أنا والساعة كهاتين. أخرجه مسلم، عن بندار محمد بن بشار، عن ابن أبي عدي وهو أبو عمرو','18','0','0');
INSERT INTO [t29778] VALUES (18,'أشهد أن لا إله إلا الله وأني رسول الله، لا يلقى بهما عبد غير شاك فيهما إلا دخل الجنة.','19','0','0');
INSERT INTO [t29778] VALUES (19,'بينما أيوب عليه السلام يغتسل عريانا خر عليه جراد من ذهب، فجعل يحثوا في ثوبه، فناداه ربه','20','0','0');
INSERT INTO [t29778] VALUES (20,'من حلف على يمين ليقتطع بها مال امرئ مسلم لقي الله عز وجل وهو عليه','21','0','0');
INSERT INTO [t29778] VALUES (21,'كان بيني وبين رجل من اليهود أرض فجحدني , فقدمته إلى النبي صلى الله عليه وسلم، فقال لي:','22','0','0');
INSERT INTO [t29778] VALUES (22,'لا يجزي ولد والده إلا أن يجده مملوكا فيشتريه فيعتقه. أخرجه مسلم، عن أبي بكر بن أبي شيبة','23','0','0');
INSERT INTO [t29778] VALUES (23,'هل تدرون ما الكوثر؟ قلنا: الله ورسوله أعلم. قال: فإنه نهر في الجنة وعدنيه ربي عز وجل، هو','24','0','0');
INSERT INTO [t29778] VALUES (24,'ما مسست ديباجا ولا حريرا ولا شيئا كان ألين من كف رسول الله صلى الله عليه وسلم، ولا شممت','25','0','0');
INSERT INTO [t29778] VALUES (25,'لا يدخل الجنة من لا يأمن جاره بوائقه.','26','0','0');
INSERT INTO [t29778] VALUES (26,'إن في الجمعة لساعة لا يوافقها مسلم قائم يصلي يسأل الله عز وجل خيرا إلا أعطاه إياه. وقال','27','0','0');
INSERT INTO [t29778] VALUES (27,'من كان يؤمن بالله واليوم الآخر فلا يؤذي جاره، ومن كان يؤمن بالله واليوم الآخر فليقل خيرا','28','0','0');
INSERT INTO [t29778] VALUES (28,'فنظر إلى القمر ليلة البدر , فقال: إنكم سترون ربكم عز وجل عيانا كما ترون هذا، لا تضامون في','29','0','0');
INSERT INTO [t29778] VALUES (29,'كلكم راع وكلكم مسئول عن رعيته، فالأمير راع وهو مسئول عن رعيته، والرجل راع على أهله وهو','30','0','0');
INSERT INTO [t29778] VALUES (30,'ألا أدلك أو أعلمك ما هو خير لك من ذلك؟ إذا أويت إلى فراشك تسبحي الله ثلاثا وثلاثين، وكبري','31','0','0');
INSERT INTO [t29778] VALUES (31,'أن لا أعطي منها على جزارتها شيئا. أخرجه البخاري، عن قبيصة بن عقبة العامري، عن سفيان وهو','32','0','0');
INSERT INTO [t29778] VALUES (32,'سباب المسلم فسوق، وقتاله كفر. قال زبيد: فقلت لوائل: سمعت هذا من عبد الله عن النبي صلى','33','0','0');
INSERT INTO [t29778] VALUES (33,'آية المنافق ثلاث: إذا حدث كذب، وإذا وعد أخلف، وإذا ائتمن خان. صحيح من حديث أبي أنس مالك','34','0','0');
INSERT INTO [t29778] VALUES (34,'لا يتقدمن أحدكم رمضان بيوم ولا يومين، إلا أن يكون صوما يصومه رجل منكم؛ ذلك الصوم. صحيح من','35','0','0');
INSERT INTO [t29778] VALUES (35,'ينصب لكل غادر لواء يوم القيامة , يقال: هذه غدرة فلان. صحيح من حديث أبي وائل شقيق بن سلمة','36','0','0');
INSERT INTO [t29778] VALUES (36,'لا تنكح البكر حتى تستأذن ولا الثيب حتى تستأمر. قيل: يا رسول الله، كيف إذنها؟ قال: إذا','37','0','0');
INSERT INTO [t29778] VALUES (37,'إن خلق أحدكم يجمع في بطن أمه أربعين يوما وأربعين ليلة، ثم يكون علقة مثل ذلك، ثم يكون مضغة','38','0','0');
INSERT INTO [t29778] VALUES (38,'اثبت حراء، فما عليك إلا نبي أو صديق أو شهيد. وكان النبي صلى الله عليه وأبو بكر وعمر','39','0','0');
INSERT INTO [t29778] VALUES (39,'كل مسكر حرام، وكل مسكر خمر. لفظ خلف بن هشام.','40','0','0');
INSERT INTO [t29778] VALUES (40,'ابعث معنا رجلا يعلمنا السنة والإسلام. قال: فأخذ بيد أبي عبيدة , فقال: هذا أمين هذه','41','0','0');
INSERT INTO [t29778] VALUES (41,'لا تبيعوا الدينار بالدينارين ولا الدرهم بالدرهمين. أخرجه مسلم عن أحمد بن عيسى المصري، كما','42','0','0');
INSERT INTO [t29778] VALUES (42,'الصعب بن جثامة أهدى لرسول الله صلى الله عليه وسلم وهو بقديد وهو محرم فخذ حمار فرده رسول','43','0','0');
INSERT INTO [t29778] VALUES (43,'أيما نخل بيعت أصولها فثمرتها للذي أبرها، إلا أن يشترط المبتاع. أخرجه مسلم، عن أبي موسى','44','0','0');
INSERT INTO [t29778] VALUES (44,'سمعت صوت رسول الله صلى الله عليه ضعيفا أعرف فيه الجوع، فهل عندك من شيء؟ قالت: نعم. فأخرجت','45','0','0');
